import { useState, useEffect, useRef } from "react";

interface Circle {
  id: number;
  x: number;
  y: number;
  radius: number;
  color: string;
  velocityX: number;
  velocityY: number;
  targetRadius: number;
  radiusVelocity: number;
}

// Hardcoded color palette
const COLOR_PALETTE = [
  "rgba(255, 99, 132, 0.6)",
  "rgba(54, 162, 235, 0.6)",
  "rgba(255, 206, 86, 0.6)",
  "rgba(75, 192, 192, 0.6)",
  "rgba(153, 102, 255, 0.6)",
  "rgba(255, 159, 64, 0.6)",
  "rgba(199, 199, 199, 0.6)",
  "rgba(83, 102, 255, 0.6)",
  "rgba(255, 99, 255, 0.6)",
  "rgba(99, 255, 132, 0.6)",
];

const getResponsiveConfig = () => {
  const width = window.innerWidth;
  const height = window.innerHeight;

  if (width < 640) {
    // Mobile
    return {
      circleCount: 15,
      minRadius: 20,
      maxRadius: 50,
      baseSpeed: 0.3,
    };
  } else if (width < 1024) {
    // Tablet
    return {
      circleCount: 25,
      minRadius: 30,
      maxRadius: 70,
      baseSpeed: 0.5,
    };
  } else {
    // Desktop
    return {
      circleCount: 35,
      minRadius: 40,
      maxRadius: 100,
      baseSpeed: 0.7,
    };
  }
};

const randomInRange = (min: number, max: number): number => {
  return Math.random() * (max - min) + min;
};

const randomColor = (): string => {
  return COLOR_PALETTE[Math.floor(Math.random() * COLOR_PALETTE.length)];
};

export function BackgroundCircles() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const circlesRef = useRef<Circle[]>([]);
  const animationFrameRef = useRef<number>();
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  // Initialize circles
  const initializeCircles = () => {
    const config = getResponsiveConfig();
    const circles: Circle[] = [];

    for (let i = 0; i < config.circleCount; i++) {
      const radius = randomInRange(config.minRadius, config.maxRadius);
      circles.push({
        id: i,
        x: randomInRange(radius, window.innerWidth - radius),
        y: randomInRange(radius, window.innerHeight - radius),
        radius: radius,
        color: randomColor(),
        velocityX: randomInRange(-config.baseSpeed, config.baseSpeed),
        velocityY: randomInRange(-config.baseSpeed, config.baseSpeed),
        targetRadius: randomInRange(config.minRadius, config.maxRadius),
        radiusVelocity: randomInRange(-0.2, 0.2),
      });
    }

    circlesRef.current = circles;
  };

  // Update circle positions and sizes
  const updateCircles = () => {
    const config = getResponsiveConfig();
    const { width, height } = dimensions;

    circlesRef.current.forEach((circle) => {
      // Update position
      circle.x += circle.velocityX;
      circle.y += circle.velocityY;

      // Bounce off walls
      if (circle.x - circle.radius <= 0 || circle.x + circle.radius >= width) {
        circle.velocityX *= -1;
        circle.x = Math.max(circle.radius, Math.min(width - circle.radius, circle.x));
      }
      if (circle.y - circle.radius <= 0 || circle.y + circle.radius >= height) {
        circle.velocityY *= -1;
        circle.y = Math.max(circle.radius, Math.min(height - circle.radius, circle.y));
      }

      // Update radius towards target
      const radiusDiff = circle.targetRadius - circle.radius;
      if (Math.abs(radiusDiff) > 0.5) {
        circle.radius += circle.radiusVelocity;
      } else {
        // Set new target radius when close enough
        circle.targetRadius = randomInRange(config.minRadius, config.maxRadius);
        circle.radiusVelocity = randomInRange(-0.2, 0.2);
      }

      // Keep radius in bounds
      circle.radius = Math.max(config.minRadius, Math.min(config.maxRadius, circle.radius));
    });
  };

  // Draw circles
  const drawCircles = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw each circle
    circlesRef.current.forEach((circle) => {
      ctx.beginPath();
      ctx.arc(circle.x, circle.y, circle.radius, 0, Math.PI * 2);
      ctx.fillStyle = circle.color;
      ctx.fill();
      ctx.closePath();
    });
  };

  // Animation loop
  const animate = () => {
    updateCircles();
    drawCircles();
    animationFrameRef.current = requestAnimationFrame(animate);
  };

  // Handle resize
  useEffect(() => {
    const updateDimensions = () => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    updateDimensions();
    window.addEventListener("resize", updateDimensions);

    return () => {
      window.removeEventListener("resize", updateDimensions);
    };
  }, []);

  // Initialize and start animation
  useEffect(() => {
    if (dimensions.width === 0 || dimensions.height === 0) return;

    initializeCircles();
    animate();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [dimensions]);

  return (
    <canvas
      ref={canvasRef}
      width={dimensions.width}
      height={dimensions.height}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        zIndex: -1,
        pointerEvents: "none",
      }}
    />
  );
}

