/**
 * Type Definitions for BackgroundCircles Component
 */

/**
 * Represents a single animated circle
 */
export interface Circle {
  /** Unique identifier for the circle */
  id: number;

  /** X coordinate position (pixels) */
  x: number;

  /** Y coordinate position (pixels) */
  y: number;

  /** Current radius of the circle (pixels) */
  radius: number;

  /** Fill color in RGBA format */
  color: string;

  /** Horizontal velocity (pixels per frame) */
  velocityX: number;

  /** Vertical velocity (pixels per frame) */
  velocityY: number;

  /** Target radius for size animation (pixels) */
  targetRadius: number;

  /** Speed of radius change (pixels per frame) */
  radiusVelocity: number;
}

/**
 * Responsive configuration based on screen size
 */
export interface ResponsiveConfig {
  /** Number of circles to render */
  circleCount: number;

  /** Minimum circle radius (pixels) */
  minRadius: number;

  /** Maximum circle radius (pixels) */
  maxRadius: number;

  /** Base movement speed multiplier */
  baseSpeed: number;
}

/**
 * Canvas dimensions
 */
export interface Dimensions {
  /** Canvas width (pixels) */
  width: number;

  /** Canvas height (pixels) */
  height: number;
}

/**
 * Screen size breakpoints
 */
export enum Breakpoint {
  /** Mobile devices (<640px) */
  Mobile = 640,

  /** Tablet devices (<1024px) */
  Tablet = 1024,
}

/**
 * Color palette type
 */
export type ColorPalette = readonly string[];

/**
 * Animation frame callback type
 */
export type AnimationFrameCallback = () => void;

