"use client";

import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import {
  Area,
  AreaChart,
  CartesianGrid,
  ReferenceLine,
  XAxis,
  YAxis,
} from "recharts";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  type ChartConfig,
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@/components/ui/chart";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Skeleton } from "@/components/ui/skeleton";
import { cn } from "@/lib/utils";
import { orpc } from "@/utils/orpc";
import { getUsageLogData, type UsageLogEntry } from "./actions";

const THRESHOLD_VALUE = 80;

const chartConfig = {
  weekly: {
    label: "Weekly Usage",
    color: "var(--chart-1)",
  },
  threshold: {
    label: "Threshold",
    color: "var(--chart-2)",
  },
} satisfies ChartConfig;

const DAYS_OF_WEEK = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

function formatDayOfWeek(timestamp: string): string {
  const date = new Date(timestamp);
  return DAYS_OF_WEEK[date.getDay()];
}

function formatTime(timestamp: string): string {
  const date = new Date(timestamp);
  return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
}

function ProgressBar({
  value,
  label,
  className,
}: {
  value: number;
  label: string;
  className?: string;
}) {
  return (
    <div className={cn("space-y-2", className)}>
      <div className="flex justify-between text-sm">
        <span className="text-muted-foreground">{label}</span>
        <span className="font-medium">{value}%</span>
      </div>
      <div className="h-2 overflow-hidden rounded-full bg-muted">
        <div
          className={cn("h-full rounded-full transition-all duration-300")}
          style={{ width: `${Math.min(value, 100)}%` }}
        />
      </div>
    </div>
  );
}

function UsageCardSkeleton() {
  return (
    <Card>
      <CardHeader>
        <CardTitle>Usage Stats</CardTitle>
        <CardDescription>Claude Code API usage limits</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="space-y-2">
          <div className="flex justify-between">
            <Skeleton className="h-4 w-24" />
            <Skeleton className="h-4 w-8" />
          </div>
          <Skeleton className="h-2 w-full rounded-full" />
        </div>
        <div className="space-y-2">
          <div className="flex justify-between">
            <Skeleton className="h-4 w-24" />
            <Skeleton className="h-4 w-8" />
          </div>
          <Skeleton className="h-2 w-full rounded-full" />
        </div>
      </CardContent>
    </Card>
  );
}

function WeeklyUsageChart({ logData }: { logData: UsageLogEntry[] }) {
  // Transform log data for the chart
  const chartData = logData.map((entry) => ({
    timestamp: entry.timestamp,
    day: formatDayOfWeek(entry.timestamp),
    time: formatTime(entry.timestamp),
    weekly: entry.weekly,
    threshold: THRESHOLD_VALUE,
  }));

  // Get date range for description
  const startDate =
    logData.length > 0
      ? new Date(logData[0].timestamp).toLocaleDateString()
      : "";
  const endDate =
    logData.length > 0
      ? new Date(logData[logData.length - 1].timestamp).toLocaleDateString()
      : "";

  return (
    <div className="w-full">
      <ChartContainer config={chartConfig}>
        <AreaChart
          accessibilityLayer
          data={chartData}
          margin={{
            left: 12,
            right: 12,
            top: 12,
            bottom: 12,
          }}
        >
          <CartesianGrid vertical={false} />
          <XAxis
            axisLine={false}
            dataKey="day"
            tickLine={false}
            tickMargin={8}
          />
          <YAxis
            axisLine={false}
            domain={[0, 100]}
            tickFormatter={(value) => `${value}%`}
            tickLine={false}
            tickMargin={8}
          />
          <ChartTooltip
            content={
              <ChartTooltipContent
                labelFormatter={(_, payload) => {
                  if (payload && payload.length > 0) {
                    const data = payload[0].payload as {
                      timestamp: string;
                      day: string;
                      time: string;
                    };
                    return `${data.day} ${data.time}`;
                  }
                  return "";
                }}
              />
            }
            cursor={false}
          />
          <defs>
            <linearGradient id="fillWeekly" x1="0" x2="0" y1="0" y2="1">
              <stop
                offset="5%"
                stopColor="var(--color-weekly)"
                stopOpacity={0.8}
              />
              <stop
                offset="95%"
                stopColor="var(--color-weekly)"
                stopOpacity={0.1}
              />
            </linearGradient>
          </defs>
          <ReferenceLine
            label={{
              value: "Threshold",
              position: "insideTopRight",
              fill: "var(--color-threshold)",
              fontSize: 12,
            }}
            stroke="var(--color-threshold)"
            strokeDasharray="5 5"
            strokeWidth={2}
            y={THRESHOLD_VALUE}
          />
          <Area
            animationDuration={0}
            dataKey="weekly"
            fill="url(#fillWeekly)"
            fillOpacity={0.4}
            isAnimationActive={false}
            stroke="var(--color-weekly)"
            strokeWidth={2}
            type="natural"
          />
        </AreaChart>
      </ChartContainer>
      <div className="mt-4 flex w-full items-start gap-2 text-sm">
        <div className="grid gap-2">
          <div className="flex items-center gap-2 font-medium leading-none">
            Weekly Claude Code usage over time
          </div>
          <div className="flex items-center gap-2 text-muted-foreground leading-none">
            {startDate && endDate ? `${startDate} - ${endDate}` : "No data yet"}
          </div>
        </div>
      </div>
    </div>
  );
}

export default function ClaudeCodeCollectPage() {
