"use client";

import { useQuery } from "@tanstack/react-query";
import {
  createColumnHelper,
  flexRender,
  getCoreRowModel,
  useReactTable,
} from "@tanstack/react-table";
import { useMemo, useState } from "react";
import {
  Area,
  AreaChart,
  CartesianGrid,
  ReferenceLine,
  XAxis,
  YAxis,
} from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  type ChartConfig,
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@/components/ui/chart";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { cn } from "@/lib/utils";
import { orpc } from "@/utils/orpc";

// Chart types and config
interface ChartDataPoint {
  month: string;
  value: number;
  threshold: number;
}

const THRESHOLD_VALUE = 500;

const chartData: ChartDataPoint[] = [
  { month: "January", value: 186, threshold: THRESHOLD_VALUE },
  { month: "February", value: 305, threshold: THRESHOLD_VALUE },
  { month: "March", value: 237, threshold: THRESHOLD_VALUE },
  { month: "April", value: 473, threshold: THRESHOLD_VALUE },
  { month: "May", value: 609, threshold: THRESHOLD_VALUE },
  { month: "June", value: 514, threshold: THRESHOLD_VALUE },
];

const chartConfig = {
  value: {
    label: "Value",
    color: "var(--chart-1)",
  },
  threshold: {
    label: "Threshold",
    color: "var(--chart-2)",
  },
} satisfies ChartConfig;

// Project idea types
interface ProjectIdea {
  id: string;
  title: string;
  description: string;
  exactDescription: string;
  tags: string[];
  completedAt: Date | null;
  startedAt: Date | null;
  createdAt: Date;
}

function truncateText(text: string, maxLength = 50): string {
  if (text.length <= maxLength) return text;
  return text.slice(0, maxLength) + "...";
}

// Chart components
function ProgressBar({
  value,
  label,
  className,
}: {
  value: number;
  label: string;
  className?: string;
}) {
  return (
    <div className={cn("space-y-2", className)}>
      <div className="flex justify-between text-sm">
        <span className="text-muted-foreground">{label}</span>
        <span className="font-medium">{value}%</span>
      </div>
      <div className="h-2 overflow-hidden rounded-full bg-muted">
        <div
          className={cn(
            "h-full rounded-full bg-primary transition-all duration-300"
          )}
          style={{ width: `${Math.min(value, 100)}%` }}
        />
      </div>
    </div>
  );
}

function UsageCardSkeleton() {
  return (
    <Card className="h-full">
      <CardHeader>
        <CardTitle>Claude Code Monitor</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="space-y-2">
          <div className="flex justify-between">
            <Skeleton className="h-4 w-24" />
            <Skeleton className="h-4 w-8" />
          </div>
          <Skeleton className="h-2 w-full rounded-full" />
        </div>
        <div className="space-y-2">
          <div className="flex justify-between">
            <Skeleton className="h-4 w-24" />
            <Skeleton className="h-4 w-8" />
          </div>
          <Skeleton className="h-2 w-full rounded-full" />
        </div>
      </CardContent>
    </Card>
  );
}

function ThresholdChart() {
  return (
    <div className="w-full">
      <ChartContainer config={chartConfig}>
        <AreaChart
          accessibilityLayer
          data={chartData}
          margin={{
            left: 12,
            right: 12,
            top: 12,
            bottom: 12,
          }}
        >
          <CartesianGrid vertical={false} />
          <XAxis
            axisLine={false}
            dataKey="month"
            tickFormatter={(value) => value.slice(0, 3)}
            tickLine={false}
            tickMargin={8}
          />
          <YAxis axisLine={false} tickLine={false} tickMargin={8} />
          <ChartTooltip content={<ChartTooltipContent />} cursor={false} />
          <defs>
            <linearGradient id="fillValue" x1="0" x2="0" y1="0" y2="1">
              <stop
                offset="5%"
                stopColor="var(--color-value)"
                stopOpacity={0.8}
              />
              <stop
                offset="95%"
                stopColor="var(--color-value)"
                stopOpacity={0.1}
              />
            </linearGradient>
          </defs>
          <ReferenceLine
            label={{
              value: "Threshold",
              position: "insideTopRight",
              fill: "var(--color-threshold)",
              fontSize: 12,
            }}
            stroke="var(--color-threshold)"
            strokeDasharray="5 5"
            strokeWidth={2}
            y={THRESHOLD_VALUE}
          />
          <Area
            animationDuration={0}
            dataKey="value"
            fill="url(#fillValue)"
            fillOpacity={0.4}
            isAnimationActive={false}
            stroke="var(--color-value)"
            strokeWidth={2}
            type="natural"
          />
        </AreaChart>
      </ChartContainer>
      <div className="mt-4 flex w-full items-start gap-2 text-sm">
        <div className="grid gap-2">
          <div className="flex items-center gap-2 font-medium leading-none">
            Values crossing threshold in recent months
          </div>
          <div className="flex items-center gap-2 text-muted-foreground leading-none">
            January - June 2024
          </div>
        </div>
      </div>
    </div>
