}

const columnHelper = createColumnHelper<ProjectIdea>();

// Claude Code Monitor Card Component
function ClaudeCodeMonitorCard() {
  const [isChartOpen, setIsChartOpen] = useState(false);

  const {
    data: stats,
    isLoading,
    error,
    refetch,
  } = useQuery({
    ...orpc.stats.getClaudeUsage.queryOptions(),
    refetchInterval: 5000,
  });

  if (isLoading) {
    return <UsageCardSkeleton />;
  }

  if (error) {
    return (
      <Card className="h-full">
        <CardHeader>
          <CardTitle>Claude Code Monitor</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-destructive text-sm">
            Failed to load usage stats
          </div>
          <p className="mt-1 text-muted-foreground text-xs">
            {error instanceof Error ? error.message : "Unknown error"}
          </p>
          <button
            className="mt-3 text-primary text-xs hover:underline"
            onClick={() => refetch()}
            type="button"
          >
            Try again
          </button>
        </CardContent>
      </Card>
    );
  }

  if (!stats) return null;

  return (
    <>
      <Card
        className="h-full cursor-pointer transition-colors hover:bg-accent/50"
        onClick={() => setIsChartOpen(true)}
      >
        <CardHeader>
          <CardTitle>Claude Code Monitor</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <ProgressBar
            label="Session (5-hour)"
            value={stats.usage_stats.session}
          />
          <ProgressBar
            label="Weekly (7-day)"
            value={stats.usage_stats.weekly}
          />
        </CardContent>
      </Card>
      <Dialog onOpenChange={setIsChartOpen} open={isChartOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Value vs Threshold</DialogTitle>
            <DialogDescription>
              Tracking values against a threshold of {THRESHOLD_VALUE}
            </DialogDescription>
          </DialogHeader>
          {isChartOpen && <ThresholdChart />}
        </DialogContent>
      </Dialog>
    </>
  );
}

// Mini In-Progress Table Component
function MiniInProgressTable() {
  const [selectedIdea, setSelectedIdea] = useState<ProjectIdea | null>(null);

  const { data: ideas = [], isLoading } = useQuery({
    ...orpc.projectIdeas.listInProgress.queryOptions(),
  });

  const tstColumnDefs = useMemo(
    () => [
      columnHelper.accessor("title", {
        header: "Title",
        cell: (info) => <span className="font-medium">{info.getValue()}</span>,
      }),
      columnHelper.accessor("exactDescription", {
        header: "Description",
        cell: (info) => (
          <span className="text-muted-foreground">
            {truncateText(info.getValue(), 50)}
          </span>
        ),
      }),
      columnHelper.accessor("tags", {
        header: "Tags",
        cell: (info) => {
          const tags = info.getValue();
          return (
            <div className="flex flex-wrap gap-1">
              {tags.map((tag) => (
                <span
                  className="inline-flex items-center rounded-full bg-muted px-2 py-0.5 text-xs"
                  key={tag}
                >
                  {tag}
                </span>
              ))}
            </div>
          );
        },
      }),
      columnHelper.accessor("startedAt", {
        header: "Started",
        cell: (info) => {
          const value = info.getValue();
          return (
            <span className="text-muted-foreground">
              {value ? new Date(value).toLocaleDateString() : "-"}
            </span>
          );
        },
      }),
    ],
    []
  );

  const table = useReactTable({
    data: ideas,
    columns: tstColumnDefs,
    getCoreRowModel: getCoreRowModel(),
  });

  if (isLoading) {
    return (
      <Card className="h-full">
        <CardHeader>
          <CardTitle>In Progress Projects</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground">Loading...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card className="flex h-full flex-col">
        <CardHeader>
          <CardTitle>In Progress Projects</CardTitle>
        </CardHeader>
        <CardContent className="flex flex-1 flex-col">
          <div className="flex-1 overflow-auto rounded-md border">
            <Table>
              <TableHeader>
                {table.getHeaderGroups().map((headerGroup) => (
                  <TableRow key={headerGroup.id}>
                    {headerGroup.headers.map((header) => (
                      <TableHead key={header.id}>
                        {header.isPlaceholder
                          ? null
                          : flexRender(
                              header.column.columnDef.header,
                              header.getContext()
                            )}
                      </TableHead>
                    ))}
                  </TableRow>
                ))}
              </TableHeader>
              <TableBody>
                {table.getRowModel().rows.length ? (
                  table.getRowModel().rows.map((row) => (
                    <TableRow
                      className="cursor-pointer hover:bg-muted/50"
                      key={row.id}
                      onClick={() => setSelectedIdea(row.original)}
                    >
                      {row.getVisibleCells().map((cell) => (
                        <TableCell key={cell.id}>
                          {flexRender(
                            cell.column.columnDef.cell,
                            cell.getContext()
                          )}
                        </TableCell>
                      ))}
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell
                      className="h-24 text-center"
                      colSpan={tstColumnDefs.length}
                    >
                      No in-progress projects found.
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <Dialog
        onOpenChange={(open) => !open && setSelectedIdea(null)}
        open={!!selectedIdea}
      >
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>{selectedIdea?.title}</DialogTitle>
            <DialogDescription>
              Started on{" "}
              {selectedIdea?.startedAt &&
                new Date(selectedIdea.startedAt).toLocaleDateString()}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <h4 className="mb-1 font-medium text-sm">Description</h4>
              <p className="text-muted-foreground text-sm">
                {selectedIdea?.description}
              </p>
            </div>
            <div>
              <h4 className="mb-1 font-medium text-sm">Exact Description</h4>
              <p className="text-muted-foreground text-sm">
                {selectedIdea?.exactDescription}
              </p>
            </div>
            {selectedIdea?.tags && selectedIdea.tags.length > 0 && (
              <div>
                <h4 className="mb-1 font-medium text-sm">Tags</h4>
                <div className="flex flex-wrap gap-1">
                  {selectedIdea.tags.map((tag) => (
                    <span
                      className="inline-flex items-center rounded-full bg-muted px-2 py-0.5 text-xs"
                      key={tag}
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

export default function DashboardPage() {
  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <h1 className="font-bold text-2xl">Dashboard</h1>
        <p className="text-muted-foreground">
          Overview of your Claude Code usage and projects
        </p>
      </div>

      <div className="grid grid-cols-1 items-stretch gap-6 lg:grid-cols-2">
        <ClaudeCodeMonitorCard />
        <MiniInProgressTable />
      </div>
    </div>
  );
}

