"use client";

import { Loader2, Pencil, Plus, Trash2 } from "lucide-react";
import { useCallback, useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  type Atom,
  createAtom,
  deleteAtom,
  getAtoms,
  updateAtom,
} from "./actions";

export default function WorkshopPage() {
  const [atoms, setAtoms] = useState<Atom[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [editingAtom, setEditingAtom] = useState<Atom | null>(null);
  const [deletingAtom, setDeletingAtom] = useState<Atom | null>(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isSaving, setIsSaving] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const loadAtoms = useCallback(async () => {
    setIsLoading(true);
    try {
      const data = await getAtoms();
      setAtoms(data);
    } catch {
      setError("Failed to load atoms");
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtoms();
  }, [loadAtoms]);

  function openCreateDialog() {
    setEditingAtom(null);
    setTitle("");
    setDescription("");
    setError(null);
    setIsDialogOpen(true);
  }

  function openEditDialog(atom: Atom) {
    setEditingAtom(atom);
    setTitle(atom.title);
    setDescription(atom.description);
    setError(null);
    setIsDialogOpen(true);
  }

  function openDeleteDialog(atom: Atom) {
    setDeletingAtom(atom);
    setIsDeleteDialogOpen(true);
  }

  async function handleSave() {
    if (!title.trim()) {
      setError("Title is required");
      return;
    }

    setIsSaving(true);
    setError(null);

    try {
      if (editingAtom) {
        const result = await updateAtom(
          editingAtom.id,
          title.trim(),
          description.trim()
        );
        if (result.success && result.atom) {
          setAtoms((prev) =>
            prev.map((a) => (a.id === editingAtom.id ? result.atom! : a))
          );
          setIsDialogOpen(false);
        } else {
          setError(result.error ?? "Failed to update atom");
        }
      } else {
        const result = await createAtom(title.trim(), description.trim());
        if (result.success && result.atom) {
          setAtoms((prev) => [...prev, result.atom!]);
          setIsDialogOpen(false);
        } else {
          setError(result.error ?? "Failed to create atom");
        }
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsSaving(false);
    }
  }

  async function handleDelete() {
    if (!deletingAtom) return;

    setIsDeleting(true);
    try {
      const result = await deleteAtom(deletingAtom.id);
      if (result.success) {
        setAtoms((prev) => prev.filter((a) => a.id !== deletingAtom.id));
        setIsDeleteDialogOpen(false);
        setDeletingAtom(null);
      } else {
        setError(result.error ?? "Failed to delete atom");
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsDeleting(false);
    }
  }

  return (
    <div className="flex h-screen flex-col">
      <div className="flex items-center justify-between border-b p-4">
        <div>
          <h1 className="font-bold text-2xl">Workshop</h1>
          <p className="text-muted-foreground text-sm">
            Build and experiment with your project atoms
          </p>
        </div>
        <Button className="gap-2" onClick={openCreateDialog}>
          <Plus className="h-4 w-4" />
          New Atom
        </Button>
      </div>

      {error && (
        <div className="mx-4 mt-4 rounded-lg border border-red-500/50 bg-red-500/10 p-4 text-red-600">
          {error}
        </div>
      )}

      <div className="flex-1 overflow-auto p-4">
        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
          </div>
        ) : atoms.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-12">
            <p className="mb-4 text-muted-foreground">No atoms yet</p>
            <Button onClick={openCreateDialog} variant="outline">
              <Plus className="mr-2 h-4 w-4" />
              Create your first atom
            </Button>
          </div>
        ) : (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {atoms.map((atom) => (
              <a
                className="block"
                href={`/workshop/${encodeURIComponent(atom.title)}`}
                key={atom.id}
              >
                <Card className="group relative h-full cursor-pointer transition-colors hover:bg-accent/50">
                  <CardHeader className="pb-2">
                    <div className="flex items-start justify-between">
                      <CardTitle className="text-base">{atom.title}</CardTitle>
                      <div className="flex gap-1 opacity-0 transition-opacity group-hover:opacity-100">
                        <Button
                          onClick={(e) => {
                            e.preventDefault();
                            openEditDialog(atom);
                          }}
                          size="sm"
                          variant="ghost"
                        >
                          <Pencil className="h-4 w-4" />
                        </Button>
                        <Button
                          onClick={(e) => {
                            e.preventDefault();
                            openDeleteDialog(atom);
                          }}
                          size="sm"
                          variant="ghost"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <p className="line-clamp-3 text-muted-foreground text-sm">
                      {atom.description || "No description"}
                    </p>
                    <p className="mt-2 text-muted-foreground text-xs">
                      Created: {new Date(atom.createdAt).toLocaleDateString()}
                    </p>
                  </CardContent>
                </Card>
              </a>
            ))}
            <Card
              className="flex min-h-[150px] cursor-pointer items-center justify-center border-dashed transition-colors hover:bg-accent/50"
              onClick={openCreateDialog}
            >
              <div className="flex flex-col items-center gap-2 text-muted-foreground">
                <Plus className="h-8 w-8" />
                <span>Add another atom</span>
              </div>
            </Card>
          </div>
        )}
      </div>

      <Dialog onOpenChange={setIsDialogOpen} open={isDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>
              {editingAtom ? "Edit Atom" : "Create New Atom"}
            </DialogTitle>
            <DialogDescription>
              {editingAtom
                ? "Update the title and description of this atom."
                : "Add a new atom with a title and description."}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="title">
                Title
              </label>
              <Input
                id="title"
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Enter atom title..."
                value={title}
              />
            </div>
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="description">
                Description
              </label>
              <Textarea
                className="min-h-[100px]"
                id="description"
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Enter atom description..."
                value={description}
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              disabled={isSaving}
              onClick={() => setIsDialogOpen(false)}
              variant="outline"
            >
              Cancel
            </Button>
            <Button disabled={isSaving || !title.trim()} onClick={handleSave}>
              {isSaving ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Saving...
                </>
              ) : editingAtom ? (
                "Update"
              ) : (
                "Create"
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog onOpenChange={setIsDeleteDialogOpen} open={isDeleteDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Delete Atom</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete &quot;{deletingAtom?.title}&quot;?
              This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              disabled={isDeleting}
              onClick={() => setIsDeleteDialogOpen(false)}
              variant="outline"
            >
              Cancel
            </Button>
            <Button
              disabled={isDeleting}
              onClick={handleDelete}
              variant="destructive"
            >
              {isDeleting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Deleting...
                </>
              ) : (
                "Delete"
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

