   234→    }
   235→  }
   236→
   237→  async function handleMarkComplete() {
   238→    if (!completingProject) return;
   239→
   240→    setIsCompleting(true);
   241→    try {
   242→      const result = await markProjectComplete(completingProject.id);
   243→      if (result.success) {
   244→        setProjects((prev) =>
   245→          prev.filter((p) => p.id !== completingProject.id)
   246→        );
   247→        setIsCompleteDialogOpen(false);
   248→        setCompletingProject(null);
   249→      } else {
   250→        setError(result.error ?? "Failed to mark project as complete");
   251→      }
   252→    } catch {
   253→      setError("An unexpected error occurred");
   254→    } finally {
   255→      setIsCompleting(false);
   256→    }
   257→  }
   258→
   259→  return (
   260→    <div className="flex h-screen flex-col">
   261→      <div className="flex items-center justify-between border-b p-4">
   262→        <div>
   263→          <h1 className="font-bold text-2xl">Pavilion</h1>
   264→          <p className="text-muted-foreground text-sm">
   265→            Enumerate, and own your risky subproblems.
   266→          </p>
   267→        </div>
   268→      </div>
   269→
   270→      {error && (
   271→        <div className="mx-4 mt-4 rounded-lg border border-red-500/50 bg-red-500/10 p-4 text-red-600">
   272→          {error}
   273→          <button
   274→            className="ml-2 text-red-600 hover:text-red-800"
   275→            onClick={() => setError(null)}
   276→            type="button"
   277→          >
   278→            <X className="inline h-4 w-4" />
   279→          </button>
   280→        </div>
   281→      )}
   282→
   283→      <div className="flex-1 overflow-auto p-4">
   284→        {isLoading ? (
   285→          <div className="flex items-center justify-center py-12">
   286→            <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
   287→          </div>
   288→        ) : projects.length === 0 ? (
   289→          <div className="flex flex-col items-center justify-center py-12">
   290→            <p className="mb-4 text-muted-foreground">No active projects yet</p>
   291→            <Button onClick={openCreateDialog} variant="outline">
   292→              <Plus className="mr-2 h-4 w-4" />
   293→              Start your first project
   294→            </Button>
   295→          </div>
   296→        ) : (
   297→          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
   298→            {projects.map((project) => (
   299→              <Card
   300→                className="group relative h-full cursor-pointer transition-shadow hover:shadow-md"
   301→                key={project.id}
   302→                onClick={() => router.push(`/pavilion/${project.id}`)}
   303→              >
   304→                <CardHeader className="pb-2">
   305→                  <div className="flex items-start justify-between gap-2">
   306→                    <CardTitle className="line-clamp-2 text-base">
   307→                      {project.title}
   308→                    </CardTitle>
   309→                    <div className="flex shrink-0 gap-1 opacity-0 transition-opacity group-hover:opacity-100">
   310→                      <Button
   311→                        className="hover:bg-green-100 hover:text-green-700"
   312→                        onClick={(e) => {
   313→                          e.stopPropagation();
   314→                          openCompleteDialog(project);
   315→                        }}
   316→                        size="icon"
   317→                        title="Mark as complete"
   318→                        variant="ghost"
   319→                      >
   320→                        <CheckCircle2 className="h-4 w-4 text-green-600" />
   321→                      </Button>
   322→                      <Button
   323→                        className="hover:bg-blue-100 hover:text-blue-700"
   324→                        onClick={(e) => {
   325→                          e.stopPropagation();
   326→                          openEditDialog(project);
   327→                        }}
   328→                        size="icon"
   329→                        title="Edit project"
   330→                        variant="ghost"
   331→                      >
   332→                        <Pencil className="h-4 w-4" />
   333→                      </Button>
   334→                      <Button
   335→                        className="hover:bg-red-100 hover:text-red-700"
   336→                        onClick={(e) => {
   337→                          e.stopPropagation();
   338→                          openDeleteDialog(project);
   339→                        }}
   340→                        size="icon"
   341→                        title="Delete project"
   342→                        variant="ghost"
   343→                      >
   344→                        <Trash2 className="h-4 w-4" />
   345→                      </Button>
   346→                    </div>
   347→                  </div>
   348→                  <StatusBadge status={getProjectStatus(project)} />
   349→                </CardHeader>
   350→                <CardContent className="space-y-3">
   351→                  <p className="line-clamp-3 text-muted-foreground text-sm">
   352→                    {project.description}
   353→                  </p>
   354→                  {project.tags.length > 0 && (
   355→                    <div className="flex flex-wrap gap-1">
   356→                      {project.tags.slice(0, 4).map((tag) => (
   357→                        <Badge
   358→                          className="text-xs"
   359→                          key={tag}
   360→                          variant="secondary"
   361→                        >
   362→                          <Tag className="mr-1 h-3 w-3" />
   363→                          {tag}
   364→                        </Badge>
   365→                      ))}
   366→                      {project.tags.length > 4 && (
   367→                        <Badge className="text-xs" variant="secondary">
   368→                          +{project.tags.length - 4}
   369→                        </Badge>
   370→                      )}
   371→                    </div>
   372→                  )}
   373→                  <p className="text-muted-foreground text-xs">
   374→                    Started:{" "}
   375→                    {project.startedAt
   376→                      ? new Date(project.startedAt).toLocaleDateString()
   377→                      : "Not started"}
   378→                  </p>
   379→                </CardContent>
   380→              </Card>
   381→            ))}
   382→            <Card
