   384→              onClick={openCreateDialog}
   385→            >
   386→              <div className="flex flex-col items-center gap-2 text-muted-foreground">
   387→                <Plus className="h-8 w-8" />
   388→                <span>Add another project</span>
   389→              </div>
   390→            </Card>
   391→          </div>
   392→        )}
   393→      </div>
   394→
   395→      <Dialog onOpenChange={setIsDialogOpen} open={isDialogOpen}>
   396→        <DialogContent className="max-h-[90vh] overflow-y-auto sm:max-w-lg">
   397→          <DialogHeader>
   398→            <DialogTitle>
   399→              {editingProject ? "Edit Project" : "Create New Project"}
   400→            </DialogTitle>
   401→            <DialogDescription>
   402→              {editingProject
   403→                ? "Update the details of this project."
   404→                : "Add a new project to track your progress."}
   405→            </DialogDescription>
   406→          </DialogHeader>
   407→          <div className="space-y-4 py-4">
   408→            <div className="space-y-2">
   409→              <label className="font-medium text-sm" htmlFor="title">
   410→                Title *
   411→              </label>
   412→              <Input
   413→                id="title"
   414→                onChange={(e) => setTitle(e.target.value)}
   415→                placeholder="Enter project title..."
   416→                value={title}
   417→              />
   418→            </div>
   419→            <div className="space-y-2">
   420→              <label className="font-medium text-sm" htmlFor="description">
   421→                Description *
   422→              </label>
   423→              <Textarea
   424→                className="min-h-[80px]"
   425→                id="description"
   426→                onChange={(e) => setDescription(e.target.value)}
   427→                placeholder="Brief summary of the project..."
   428→                value={description}
   429→              />
   430→            </div>
   431→            <div className="space-y-2">
   432→              <label className="font-medium text-sm" htmlFor="exactDescription">
   433→                Detailed Description
   434→              </label>
   435→              <Textarea
   436→                className="min-h-[100px]"
   437→                id="exactDescription"
   438→                onChange={(e) => setExactDescription(e.target.value)}
   439→                placeholder="Full project details (optional, uses description if empty)..."
   440→                value={exactDescription}
   441→              />
   442→            </div>
   443→            <div className="space-y-2">
   444→              <label className="font-medium text-sm" htmlFor="codebaseName">
   445→                Codebase Name *
   446→              </label>
   447→              <Input
   448→                id="codebaseName"
   449→                onChange={(e) => setCodebaseName(e.target.value)}
   450→                placeholder="e.g., my-project (lowercase, dashes allowed)"
   451→                value={codebaseName}
   452→              />
   453→              <p className="text-muted-foreground text-xs">
   454→                Used for folder naming. Will be converted to lowercase slug.
   455→              </p>
   456→            </div>
   457→            <div className="space-y-2">
   458→              <label className="font-medium text-sm" htmlFor="tags">
   459→                Tags
   460→              </label>
   461→              <div className="flex gap-2">
   462→                <Input
   463→                  id="tags"
   464→                  onChange={(e) => setTagsInput(e.target.value)}
   465→                  onKeyDown={handleTagKeyDown}
   466→                  placeholder="Add a tag..."
   467→                  value={tagsInput}
   468→                />
   469→                <Button
   470→                  disabled={!tagsInput.trim()}
   471→                  onClick={handleAddTag}
   472→                  type="button"
   473→                  variant="outline"
   474→                >
   475→                  Add
   476→                </Button>
   477→              </div>
   478→              {tags.length > 0 && (
   479→                <div className="flex flex-wrap gap-1 pt-2">
   480→                  {tags.map((tag) => (
   481→                    <Badge
   482→                      className="cursor-pointer gap-1"
   483→                      key={tag}
   484→                      onClick={() => handleRemoveTag(tag)}
   485→                      variant="secondary"
   486→                    >
   487→                      {tag}
   488→                      <X className="h-3 w-3" />
   489→                    </Badge>
   490→                  ))}
   491→                </div>
   492→              )}
   493→            </div>
   494→          </div>
   495→          <DialogFooter>
   496→            <Button
   497→              disabled={isSaving}
   498→              onClick={() => setIsDialogOpen(false)}
   499→              variant="outline"
   500→            >
   501→              Cancel
   502→            </Button>
   503→            <Button
   504→              disabled={
   505→                isSaving ||
   506→                !title.trim() ||
   507→                !description.trim() ||
   508→                !codebaseName.trim()
   509→              }
   510→              onClick={handleSave}
   511→            >
   512→              {isSaving ? (
   513→                <>
   514→                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   515→                  Saving...
   516→                </>
   517→              ) : editingProject ? (
   518→                "Update"
   519→              ) : (
   520→                "Create"
   521→              )}
   522→            </Button>
   523→          </DialogFooter>
   524→        </DialogContent>
   525→      </Dialog>
   526→
   527→      <Dialog onOpenChange={setIsDeleteDialogOpen} open={isDeleteDialogOpen}>
   528→        <DialogContent className="sm:max-w-md">
   529→          <DialogHeader>
   530→            <DialogTitle>Delete Project</DialogTitle>
   531→            <DialogDescription>
   532→              Are you sure you want to delete &quot;{deletingProject?.title}
   533→              &quot;? This action cannot be undone.
   534→            </DialogDescription>
   535→          </DialogHeader>
   536→          <DialogFooter>
   537→            <Button
   538→              disabled={isDeleting}
   539→              onClick={() => setIsDeleteDialogOpen(false)}
   540→              variant="outline"
   541→            >
   542→              Cancel
   543→            </Button>
   544→            <Button
   545→              disabled={isDeleting}
   546→              onClick={handleDelete}
   547→              variant="destructive"
   548→            >
   549→              {isDeleting ? (
   550→                <>
   551→                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   552→                  Deleting...
