"use client";

import {
  CheckCircle2,
  Loader2,
  Pencil,
  Plus,
  Tag,
  Trash2,
  X,
} from "lucide-react";
import { useRouter } from "next/navigation";
import { useCallback, useEffect, useState } from "react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  createProject,
  deleteProject,
  getActiveProjects,
  markProjectComplete,
  type Project,
  updateProject,
} from "./actions";

type ProjectStatus = "in-progress" | "not-started" | "completed";

function getProjectStatus(project: Project): ProjectStatus {
  if (project.completedAt) return "completed";
  if (project.startedAt) return "in-progress";
  return "not-started";
}

function StatusBadge({ status }: { status: ProjectStatus }) {
  const config: Record<
    ProjectStatus,
    {
      variant: "default" | "secondary" | "destructive" | "outline";
      label: string;
    }
  > = {
    "in-progress": { variant: "default", label: "In Progress" },
    "not-started": { variant: "secondary", label: "Not Started" },
    completed: { variant: "outline", label: "Completed" },
  };

  return <Badge variant={config[status].variant}>{config[status].label}</Badge>;
}

export default function PavilionPage() {
  const router = useRouter();
  const [projects, setProjects] = useState<Project[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [editingProject, setEditingProject] = useState<Project | null>(null);
  const [deletingProject, setDeletingProject] = useState<Project | null>(null);
  const [completingProject, setCompletingProject] = useState<Project | null>(
    null
  );
  const [isCompleteDialogOpen, setIsCompleteDialogOpen] = useState(false);
  const [isCompleting, setIsCompleting] = useState(false);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [exactDescription, setExactDescription] = useState("");
  const [codebaseName, setCodebaseName] = useState("");
  const [tagsInput, setTagsInput] = useState("");
  const [tags, setTags] = useState<string[]>([]);
  const [isSaving, setIsSaving] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const loadProjects = useCallback(async () => {
    setIsLoading(true);
    try {
      const data = await getActiveProjects();
      setProjects(data);
    } catch {
      setError("Failed to load projects");
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadProjects();
  }, [loadProjects]);

  function openCreateDialog() {
    setEditingProject(null);
    setTitle("");
    setDescription("");
    setExactDescription("");
    setCodebaseName("");
    setTags([]);
    setTagsInput("");
    setError(null);
    setIsDialogOpen(true);
  }

  function openEditDialog(project: Project) {
    setEditingProject(project);
    setTitle(project.title);
    setDescription(project.description);
    setExactDescription(project.exactDescription);
    setCodebaseName(project.codebaseName);
    setTags(project.tags);
    setTagsInput("");
    setError(null);
    setIsDialogOpen(true);
  }

  function openDeleteDialog(project: Project) {
    setDeletingProject(project);
    setIsDeleteDialogOpen(true);
  }

  function openCompleteDialog(project: Project) {
    setCompletingProject(project);
    setIsCompleteDialogOpen(true);
  }

  function handleAddTag() {
    const trimmedTag = tagsInput.trim();
    if (trimmedTag && !tags.includes(trimmedTag)) {
      setTags((prev) => [...prev, trimmedTag]);
      setTagsInput("");
    }
  }

  function handleRemoveTag(tagToRemove: string) {
    setTags((prev) => prev.filter((tag) => tag !== tagToRemove));
  }

  function handleTagKeyDown(e: React.KeyboardEvent) {
    if (e.key === "Enter") {
      e.preventDefault();
      handleAddTag();
    }
  }

  async function handleSave() {
    if (!title.trim()) {
      setError("Title is required");
      return;
    }
    if (!description.trim()) {
      setError("Description is required");
      return;
    }
    if (!codebaseName.trim()) {
      setError("Codebase name is required");
      return;
    }
    const validCodebaseName = codebaseName
      .toLowerCase()
      .replace(/\s+/g, "-")
      .replace(/[^a-z0-9-]/g, "");
    if (!validCodebaseName) {
      setError("Invalid codebase name (must contain letters or numbers)");
      return;
    }

    setIsSaving(true);
    setError(null);

    try {
      if (editingProject) {
        const result = await updateProject(editingProject.id, {
          title: title.trim(),
          description: description.trim(),
          exactDescription: exactDescription.trim() || description.trim(),
          codebaseName: validCodebaseName,
          tags,
        });
        if (result.success && result.project) {
          setProjects((prev) =>
            prev.map((p) => (p.id === editingProject.id ? result.project! : p))
          );
          setIsDialogOpen(false);
        } else {
          setError(result.error ?? "Failed to update project");
        }
      } else {
        const result = await createProject({
          title: title.trim(),
          description: description.trim(),
          exactDescription: exactDescription.trim() || description.trim(),
          codebaseName: validCodebaseName,
          tags,
          startedAt: new Date(),
        });
        if (result.success && result.project) {
          setProjects((prev) => [...prev, result.project!]);
          setIsDialogOpen(false);
        } else {
          setError(result.error ?? "Failed to create project");
        }
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsSaving(false);
    }
  }

  async function handleDelete() {
    if (!deletingProject) return;

    setIsDeleting(true);
    try {
      const result = await deleteProject(deletingProject.id);
      if (result.success) {
        setProjects((prev) => prev.filter((p) => p.id !== deletingProject.id));
        setIsDeleteDialogOpen(false);
        setDeletingProject(null);
      } else {
        setError(result.error ?? "Failed to delete project");
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsDeleting(false);
    }
  }

  async function handleMarkComplete() {
    if (!completingProject) return;

    setIsCompleting(true);
    try {
      const result = await markProjectComplete(completingProject.id);
      if (result.success) {
        setProjects((prev) =>
          prev.filter((p) => p.id !== completingProject.id)
        );
        setIsCompleteDialogOpen(false);
        setCompletingProject(null);
      } else {
        setError(result.error ?? "Failed to mark project as complete");
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsCompleting(false);
    }
  }

  return (
    <div className="flex h-screen flex-col">
      <div className="flex items-center justify-between border-b p-4">
        <div>
          <h1 className="font-bold text-2xl">Pavilion</h1>
          <p className="text-muted-foreground text-sm">
            Enumerate, and own your risky subproblems.
          </p>
        </div>
      </div>

      {error && (
        <div className="mx-4 mt-4 rounded-lg border border-red-500/50 bg-red-500/10 p-4 text-red-600">
          {error}
          <button
            className="ml-2 text-red-600 hover:text-red-800"
            onClick={() => setError(null)}
            type="button"
          >
            <X className="inline h-4 w-4" />
          </button>
        </div>
      )}

      <div className="flex-1 overflow-auto p-4">
        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
          </div>
        ) : projects.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-12">
            <p className="mb-4 text-muted-foreground">No active projects yet</p>
            <Button onClick={openCreateDialog} variant="outline">
              <Plus className="mr-2 h-4 w-4" />
              Start your first project
            </Button>
          </div>
        ) : (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {projects.map((project) => (
              <Card
                className="group relative h-full cursor-pointer transition-shadow hover:shadow-md"
                key={project.id}
                onClick={() => router.push(`/pavilion/${project.id}`)}
              >
                <CardHeader className="pb-2">
                  <div className="flex items-start justify-between gap-2">
                    <CardTitle className="line-clamp-2 text-base">
                      {project.title}
                    </CardTitle>
                    <div className="flex shrink-0 gap-1 opacity-0 transition-opacity group-hover:opacity-100">
                      <Button
                        className="hover:bg-green-100 hover:text-green-700"
                        onClick={(e) => {
                          e.stopPropagation();
                          openCompleteDialog(project);
                        }}
                        size="icon"
                        title="Mark as complete"
                        variant="ghost"
                      >
                        <CheckCircle2 className="h-4 w-4 text-green-600" />
                      </Button>
                      <Button
                        className="hover:bg-blue-100 hover:text-blue-700"
                        onClick={(e) => {
                          e.stopPropagation();
                          openEditDialog(project);
                        }}
                        size="icon"
                        title="Edit project"
                        variant="ghost"
                      >
                        <Pencil className="h-4 w-4" />
                      </Button>
                      <Button
                        className="hover:bg-red-100 hover:text-red-700"
                        onClick={(e) => {
                          e.stopPropagation();
                          openDeleteDialog(project);
                        }}
                        size="icon"
                        title="Delete project"
                        variant="ghost"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                  <StatusBadge status={getProjectStatus(project)} />
                </CardHeader>
                <CardContent className="space-y-3">
                  <p className="line-clamp-3 text-muted-foreground text-sm">
                    {project.description}
                  </p>
                  {project.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1">
                      {project.tags.slice(0, 4).map((tag) => (
                        <Badge
                          className="text-xs"
                          key={tag}
                          variant="secondary"
                        >
                          <Tag className="mr-1 h-3 w-3" />
                          {tag}
                        </Badge>
                      ))}
                      {project.tags.length > 4 && (
                        <Badge className="text-xs" variant="secondary">
                          +{project.tags.length - 4}
                        </Badge>
                      )}
                    </div>
                  )}
                  <p className="text-muted-foreground text-xs">
                    Started:{" "}
                    {project.startedAt
                      ? new Date(project.startedAt).toLocaleDateString()
                      : "Not started"}
                  </p>
                </CardContent>
              </Card>
            ))}
            <Card
              className="flex min-h-[200px] cursor-pointer items-center justify-center border-dashed transition-colors hover:bg-accent/50"
              onClick={openCreateDialog}
            >
              <div className="flex flex-col items-center gap-2 text-muted-foreground">
                <Plus className="h-8 w-8" />
                <span>Add another project</span>
              </div>
            </Card>
          </div>
        )}
      </div>

      <Dialog onOpenChange={setIsDialogOpen} open={isDialogOpen}>
        <DialogContent className="max-h-[90vh] overflow-y-auto sm:max-w-lg">
          <DialogHeader>
            <DialogTitle>
              {editingProject ? "Edit Project" : "Create New Project"}
            </DialogTitle>
            <DialogDescription>
              {editingProject
                ? "Update the details of this project."
                : "Add a new project to track your progress."}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="title">
                Title *
              </label>
              <Input
                id="title"
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Enter project title..."
                value={title}
              />
            </div>
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="description">
                Description *
              </label>
              <Textarea
                className="min-h-[80px]"
                id="description"
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Brief summary of the project..."
                value={description}
              />
            </div>
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="exactDescription">
                Detailed Description
              </label>
              <Textarea
                className="min-h-[100px]"
                id="exactDescription"
                onChange={(e) => setExactDescription(e.target.value)}
                placeholder="Full project details (optional, uses description if empty)..."
                value={exactDescription}
              />
            </div>
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="codebaseName">
                Codebase Name *
              </label>
              <Input
                id="codebaseName"
                onChange={(e) => setCodebaseName(e.target.value)}
                placeholder="e.g., my-project (lowercase, dashes allowed)"
                value={codebaseName}
              />
              <p className="text-muted-foreground text-xs">
                Used for folder naming. Will be converted to lowercase slug.
              </p>
            </div>
            <div className="space-y-2">
              <label className="font-medium text-sm" htmlFor="tags">
                Tags
              </label>
              <div className="flex gap-2">
                <Input
                  id="tags"
                  onChange={(e) => setTagsInput(e.target.value)}
                  onKeyDown={handleTagKeyDown}
                  placeholder="Add a tag..."
                  value={tagsInput}
                />
                <Button
                  disabled={!tagsInput.trim()}
                  onClick={handleAddTag}
                  type="button"
                  variant="outline"
                >
                  Add
                </Button>
              </div>
              {tags.length > 0 && (
                <div className="flex flex-wrap gap-1 pt-2">
                  {tags.map((tag) => (
                    <Badge
                      className="cursor-pointer gap-1"
                      key={tag}
                      onClick={() => handleRemoveTag(tag)}
                      variant="secondary"
                    >
                      {tag}
                      <X className="h-3 w-3" />
                    </Badge>
                  ))}
                </div>
              )}
            </div>
          </div>
          <DialogFooter>
            <Button
              disabled={isSaving}
              onClick={() => setIsDialogOpen(false)}
              variant="outline"
            >
              Cancel
            </Button>
            <Button
              disabled={
                isSaving ||
                !title.trim() ||
                !description.trim() ||
                !codebaseName.trim()
              }
              onClick={handleSave}
            >
              {isSaving ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Saving...
                </>
              ) : editingProject ? (
                "Update"
              ) : (
                "Create"
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog onOpenChange={setIsDeleteDialogOpen} open={isDeleteDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Delete Project</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete &quot;{deletingProject?.title}
              &quot;? This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              disabled={isDeleting}
              onClick={() => setIsDeleteDialogOpen(false)}
              variant="outline"
            >
              Cancel
            </Button>
            <Button
              disabled={isDeleting}
              onClick={handleDelete}
              variant="destructive"
            >
              {isDeleting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Deleting...
                </>
              ) : (
                "Delete"
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog
        onOpenChange={setIsCompleteDialogOpen}
        open={isCompleteDialogOpen}
      >
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Complete Project</DialogTitle>
            <DialogDescription>
              Are you sure you want to mark &quot;{completingProject?.title}
              &quot; as complete? It will be removed from your active projects.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              disabled={isCompleting}
              onClick={() => setIsCompleteDialogOpen(false)}
              variant="outline"
            >
              Cancel
            </Button>
            <Button
              className="bg-green-600 hover:bg-green-700"
              disabled={isCompleting}
              onClick={handleMarkComplete}
            >
              {isCompleting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Completing...
                </>
              ) : (
                <>
                  <CheckCircle2 className="mr-2 h-4 w-4" />
                  Complete
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

