"use client";

import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { useHelloWorld } from "./hooks";

export default function Page() {
  const hello = useHelloWorld();

  return (
    <div className="flex min-h-screen flex-col items-center justify-center gap-6 p-4">
      <Card className="h-64 w-full max-w-md overflow-auto p-4 font-mono text-sm">
        {hello.data ? (
          <div>{hello.data}</div>
        ) : (
          <span className="text-muted-foreground">No response yet</span>
        )}
      </Card>
      <Button disabled={hello.isPending} onClick={() => hello.mutate()}>
        {hello.isPending ? "Loading..." : "Hello World"}
      </Button>
    </div>
  );
}

input.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { MOCK_PROJECTS, MOCK_COMPONENTS, type MockProject, type MockComponent } from "./utils";
import { cn } from "./lib/utils";

type GridColumns = 1 | 2 | 3 | 4;
type GridGap = "sm" | "md" | "lg";
type LayoutType = "cards" | "dashboard" | "compact";

export default function Page() {
  const [columns, setColumns] = useState<GridColumns>(3);
  const [gap, setGap] = useState<GridGap>("md");
  const [layoutType, setLayoutType] = useState<LayoutType>("cards");
  const [itemsStretch, setItemsStretch] = useState(false);

  const gapClasses: Record<GridGap, string> = {
    sm: "gap-3",
    md: "gap-4",
    lg: "gap-6",
  };

  const columnClasses: Record<GridColumns, string> = {
    1: "grid-cols-1",
    2: "grid-cols-1 md:grid-cols-2",
    3: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3",
    4: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4",
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Controls Section */}
      <div className="mb-8 space-y-6">
        <div>
          <h1 className="mb-2 font-bold text-3xl">Grid UI Implementation</h1>
          <p className="text-muted-foreground">
            Interactive demonstration of responsive grid layouts from the source codebase
          </p>
        </div>

        {/* Layout Type Controls */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Layout Type</CardTitle>
          </CardHeader>
          <CardContent className="flex flex-wrap gap-2">
            <Button
              variant={layoutType === "cards" ? "default" : "outline"}
              onClick={() => setLayoutType("cards")}
            >
              Cards Layout
            </Button>
            <Button
              variant={layoutType === "dashboard" ? "default" : "outline"}
              onClick={() => setLayoutType("dashboard")}
            >
              Dashboard Layout
            </Button>
            <Button
              variant={layoutType === "compact" ? "default" : "outline"}
              onClick={() => setLayoutType("compact")}
            >
              Compact Layout
            </Button>
          </CardContent>
        </Card>

        {/* Grid Controls */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Grid Settings</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Columns Control */}
            <div className="space-y-2">
              <label className="font-medium text-sm">Columns (max)</label>
              <div className="flex flex-wrap gap-2">
                {([1, 2, 3, 4] as GridColumns[]).map((col) => (
                  <Button
                    key={col}
                    variant={columns === col ? "default" : "outline"}
                    onClick={() => setColumns(col)}
                    size="sm"
                  >
                    {col}
                  </Button>
                ))}
              </div>
              <p className="text-muted-foreground text-xs">
                Current: <code className="rounded bg-muted px-1">{columnClasses[columns]}</code>
              </p>
            </div>

            {/* Gap Control */}
            <div className="space-y-2">
              <label className="font-medium text-sm">Gap Size</label>
              <div className="flex flex-wrap gap-2">
                {(["sm", "md", "lg"] as GridGap[]).map((g) => (
                  <Button
                    key={g}
                    variant={gap === g ? "default" : "outline"}
                    onClick={() => setGap(g)}
                    size="sm"
                  >
                    {g.toUpperCase()}
                  </Button>
                ))}
              </div>
              <p className="text-muted-foreground text-xs">
                Current: <code className="rounded bg-muted px-1">{gapClasses[gap]}</code>
              </p>
            </div>

            {/* Stretch Control */}
            <div className="space-y-2">
              <label className="font-medium text-sm">Alignment</label>
              <div className="flex flex-wrap gap-2">
                <Button
                  variant={itemsStretch ? "default" : "outline"}
                  onClick={() => setItemsStretch(!itemsStretch)}
                  size="sm"
                >
                  {itemsStretch ? "items-stretch ✓" : "items-stretch"}
                </Button>
              </div>
              <p className="text-muted-foreground text-xs">
                Makes all grid items equal height
              </p>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Grid Preview Section */}
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <h2 className="font-semibold text-xl">
            {layoutType === "cards" && "Cards Layout Preview"}
            {layoutType === "dashboard" && "Dashboard Layout Preview"}
            {layoutType === "compact" && "Compact Layout Preview"}
          </h2>
          <Badge variant="outline">
            {layoutType === "dashboard" ? MOCK_PROJECTS.length : MOCK_COMPONENTS.length} items
          </Badge>
        </div>

        {/* Cards Layout - based on atoms/pavilion pages */}
        {layoutType === "cards" && (
          <div
            className={cn(
              "grid",
              columnClasses[columns],
              gapClasses[gap],
              itemsStretch && "items-stretch"
            )}
          >
            {MOCK_COMPONENTS.map((component) => (
              <Card
                key={component.id}
                className="group cursor-pointer transition-colors hover:bg-accent/50"
              >
                <CardHeader className="pb-2">
                  <CardTitle className="text-base">{component.title}</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  <p className="line-clamp-3 text-muted-foreground text-sm">
                    {component.description}
                  </p>
                  {component.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1">
                      {component.tags.map((tag) => (
                        <Badge key={tag} variant="secondary" className="text-xs">
                          {tag}
                        </Badge>
                      ))}
                    </div>
                  )}
                  <p className="text-muted-foreground text-xs">
                    Created: {new Date(component.createdAt).toLocaleDateString()}
                  </p>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Dashboard Layout - based on dashboard page */}
        {layoutType === "dashboard" && (
          <div
            className={cn(
              "grid",
              "grid-cols-1 lg:grid-cols-2",
              gapClasses[gap],
              itemsStretch && "items-stretch"
            )}
          >
            {MOCK_PROJECTS.map((project) => (
              <Card key={project.id} className="flex flex-col">
                <CardHeader>
                  <CardTitle>{project.title}</CardTitle>
                </CardHeader>
                <CardContent className="flex flex-1 flex-col space-y-4">
                  <p className="text-muted-foreground text-sm">{project.description}</p>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Progress</span>
                      <span className="font-medium">{project.progress}%</span>
                    </div>
                    <div className="h-2 overflow-hidden rounded-full bg-muted">
                      <div
                        className="h-full rounded-full bg-primary transition-all duration-300"
                        style={{ width: `${project.progress}%` }}
                      />
                    </div>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {project.tags.map((tag) => (
                      <Badge key={tag} variant="outline" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Compact Layout - based on atoms list */}
        {layoutType === "compact" && (
          <div
            className={cn(
              "grid",
              columnClasses[columns],
              gapClasses[gap]
            )}
          >
            {MOCK_COMPONENTS.map((component) => (
              <div
                key={component.id}
                className="block rounded-md border bg-background p-3 transition-colors hover:bg-accent"
              >
                <span className="font-medium text-sm">{component.title}</span>
                <p className="mt-1 line-clamp-2 text-muted-foreground text-xs">
                  {component.description}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Code Reference Section */}
      <Card className="mt-8">
        <CardHeader>
          <CardTitle className="text-lg">Generated Grid Classes</CardTitle>
        </CardHeader>
        <CardContent className="space-y-2">
          <div className="rounded-lg bg-muted p-3 font-mono text-sm">
            <div>
              className=&quot;grid {columnClasses[columns]} {gapClasses[gap]}
              {itemsStretch && " items-stretch"}&quot;
            </div>
          </div>
          <p className="text-muted-foreground text-xs">
            This demonstrates the responsive grid pattern used throughout the source codebase
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

