                  {component.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1">
                      {component.tags.map((tag) => (
                        <Badge key={tag} variant="secondary" className="text-xs">
                          {tag}
                        </Badge>
                      ))}
                    </div>
                  )}
                  <p className="text-muted-foreground text-xs">
                    Created: {new Date(component.createdAt).toLocaleDateString()}
                  </p>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Dashboard Layout - based on dashboard page */}
        {layoutType === "dashboard" && (
          <div
            className={cn(
              "grid",
              "grid-cols-1 lg:grid-cols-2",
              gapClasses[gap],
              itemsStretch && "items-stretch"
            )}
          >
            {MOCK_PROJECTS.map((project) => (
              <Card key={project.id} className="flex flex-col">
                <CardHeader>
                  <CardTitle>{project.title}</CardTitle>
                </CardHeader>
                <CardContent className="flex flex-1 flex-col space-y-4">
                  <p className="text-muted-foreground text-sm">{project.description}</p>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Progress</span>
                      <span className="font-medium">{project.progress}%</span>
                    </div>
                    <div className="h-2 overflow-hidden rounded-full bg-muted">
                      <div
                        className="h-full rounded-full bg-primary transition-all duration-300"
                        style={{ width: `${project.progress}%` }}
                      />
                    </div>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {project.tags.map((tag) => (
                      <Badge key={tag} variant="outline" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Compact Layout - based on atoms list */}
        {layoutType === "compact" && (
          <div
            className={cn(
              "grid",
              columnClasses[columns],
              gapClasses[gap]
            )}
          >
            {MOCK_COMPONENTS.map((component) => (
              <div
                key={component.id}
                className="block rounded-md border bg-background p-3 transition-colors hover:bg-accent"
              >
                <span className="font-medium text-sm">{component.title}</span>
                <p className="mt-1 line-clamp-2 text-muted-foreground text-xs">
                  {component.description}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Code Reference Section */}
      <Card className="mt-8">
        <CardHeader>
          <CardTitle className="text-lg">Generated Grid Classes</CardTitle>
        </CardHeader>
        <CardContent className="space-y-2">
          <div className="rounded-lg bg-muted p-3 font-mono text-sm">
            <div>
              className=&quot;grid {columnClasses[columns]} {gapClasses[gap]}
              {itemsStretch && " items-stretch"}&quot;
            </div>
          </div>
          <p className="text-muted-foreground text-xs">
            This demonstrates the responsive grid pattern used throughout the source codebase
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

