import { DemoEmail } from '@/data/demoInbox';
import { cn } from '@/lib/utils';
import { Mail, MailOpen, Clock } from 'lucide-react';

interface EmailListProps {
  emails: DemoEmail[];
  selectedId: string | null;
  onSelect: (emailId: string) => void;
  privacyMode: boolean;
}

export function EmailList({ emails, selectedId, onSelect, privacyMode }: EmailListProps) {
  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    const now = new Date();
    const diffDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      return date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return date.toLocaleDateString('en-US', { weekday: 'short' });
    } else {
      return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    }
  };

  const unreadCount = emails.filter(e => e.unread).length;

  return (
    <div className="flex flex-col h-full" role="region" aria-label="Email inbox">
      {/* Header */}
      <div className="p-4 border-b border-border">
        <h2 className="text-2xl font-bold">Inbox</h2>
        <p className="text-muted-foreground">
          {unreadCount > 0 ? (
            <span className="text-primary font-semibold">{unreadCount} unread</span>
          ) : (
            'All caught up'
          )}{' '}
          · {emails.length} total
        </p>
      </div>

      {/* Email list */}
      <div
        className="flex-1 overflow-y-auto"
        role="listbox"
        aria-label="Email messages"
      >
        {emails.map((email, index) => (
          <button
            key={email.id}
            onClick={() => onSelect(email.id)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                onSelect(email.id);
              }
            }}
            className={cn(
              'email-card w-full text-left cursor-pointer block',
              email.unread && 'email-card-unread',
              selectedId === email.id && 'email-card-selected'
            )}
            role="option"
            aria-selected={selectedId === email.id}
            aria-label={`${email.unread ? 'Unread email' : 'Email'} from ${privacyMode ? 'hidden sender' : email.fromName}, subject: ${email.subject}, ${formatDate(email.date)}`}
            tabIndex={0}
          >
            <div className="flex items-start gap-3">
              {/* Read/unread icon */}
              <div className="flex-shrink-0 mt-1">
                {email.unread ? (
                  <Mail className="w-5 h-5 text-primary" aria-hidden="true" />
                ) : (
                  <MailOpen className="w-5 h-5 text-muted-foreground" aria-hidden="true" />
                )}
              </div>

              {/* Email content */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between gap-2 mb-1">
                  <span
                    className={cn(
                      'font-semibold truncate',
                      email.unread && 'text-foreground',
                      !email.unread && 'text-muted-foreground'
                    )}
                  >
                    {privacyMode ? '••••••' : email.fromName}
                  </span>
                  <span className="flex items-center gap-1 text-sm text-muted-foreground flex-shrink-0">
                    <Clock className="w-3 h-3" aria-hidden="true" />
                    {formatDate(email.date)}
                  </span>
                </div>
                <p
                  className={cn(
                    'truncate mb-1',
                    email.unread && 'font-medium text-foreground',
                    !email.unread && 'text-muted-foreground'
                  )}
                >
                  {email.subject}
                </p>
                <p className="text-sm text-muted-foreground truncate">
                  {email.snippet}
                </p>
              </div>
            </div>
          </button>
        ))}
      </div>
    </div>
  );
}

