     1→import { useState, useCallback, useRef } from 'react';
     2→import { demoInbox, DemoEmail } from '@/data/demoInbox';
     3→
     4→export interface ConversationMessage {
     5→  role: 'user' | 'assistant';
     6→  content: string;
     7→  timestamp: Date;
     8→}
     9→
    10→export interface NarrationStep {
    11→  text: string;
    12→  timestamp: Date;
    13→  type: 'action' | 'info' | 'confirmation';
    14→}
    15→
    16→export interface AgentResponse {
    17→  assistant_reply: string;
    18→  speak: string;
    19→  ask_user_confirmation: boolean;
    20→  confirmation_question: string | null;
    21→  tool_calls: Array<{
    22→    tool_name: string;
    23→    arguments: Record<string, unknown>;
    24→  }>;
    25→  narration_steps: string[];
    26→}
    27→
    28→export interface AppState {
    29→  currentView: 'inbox' | 'email' | 'drafts' | 'compose' | 'settings';
    30→  selectedEmailId: string | null;
    31→  draftEmail: {
    32→    to: string;
    33→    subject: string;
    34→    body: string;
    35→    threadId?: string;
    36→  } | null;
    37→  privacyMode: boolean;
    38→}
    39→
    40→interface UseEchoPilotReturn {
    41→  status: 'idle' | 'listening' | 'processing' | 'speaking';
    42→  conversation: ConversationMessage[];
    43→  narrationLog: NarrationStep[];
    44→  appState: AppState;
    45→  emails: DemoEmail[];
    46→  selectedEmail: DemoEmail | null;
    47→  isDemoMode: boolean;
    48→  pendingConfirmation: string | null;
    49→  
    50→  processUserInput: (text: string) => Promise<void>;
    51→  confirmAction: () => Promise<void>;
    52→  cancelAction: () => void;
    53→  setDemoMode: (demo: boolean) => void;
    54→  togglePrivacyMode: () => void;
    55→  navigateTo: (view: AppState['currentView']) => void;
    56→  selectEmail: (emailId: string) => void;
    57→  speakText: (text: string) => Promise<void>;
    58→  setStatus: (status: 'idle' | 'listening' | 'processing' | 'speaking') => void;
    59→}
    60→
    61→export function useEchoPilot(): UseEchoPilotReturn {
    62→  const [status, setStatus] = useState<'idle' | 'listening' | 'processing' | 'speaking'>('idle');
    63→  const [conversation, setConversation] = useState<ConversationMessage[]>([]);
    64→  const [narrationLog, setNarrationLog] = useState<NarrationStep[]>([]);
    65→  const [appState, setAppState] = useState<AppState>({
    66→    currentView: 'inbox',
    67→    selectedEmailId: null,
    68→    draftEmail: null,
    69→    privacyMode: false
    70→  });
    71→  const [emails, setEmails] = useState<DemoEmail[]>(demoInbox);
    72→  const [isDemoMode, setIsDemoMode] = useState(true);
    73→  const [pendingConfirmation, setPendingConfirmation] = useState<string | null>(null);
    74→  const pendingActionRef = useRef<AgentResponse | null>(null);
    75→  const audioRef = useRef<HTMLAudioElement | null>(null);
    76→
    77→  const addNarration = useCallback((text: string, type: NarrationStep['type'] = 'info') => {
    78→    setNarrationLog(prev => [...prev, { text, timestamp: new Date(), type }]);
    79→  }, []);
    80→
    81→  const speakText = useCallback(async (text: string) => {
    82→    if (!text) return;
    83→    
    84→    setStatus('speaking');
    85→    addNarration(`Speaking: "${text}"`, 'info');
    86→    
    87→    // Use browser's built-in speech synthesis (reliable and instant)
    88→    if ('speechSynthesis' in window) {
    89→      // Cancel any ongoing speech
    90→      window.speechSynthesis.cancel();
    91→      
    92→      return new Promise<void>((resolve) => {
    93→        const utterance = new SpeechSynthesisUtterance(text);
    94→        utterance.rate = 0.95;
    95→        utterance.pitch = 1;
    96→        utterance.volume = 1;
    97→        
    98→        // Try to use a good voice
    99→        const voices = window.speechSynthesis.getVoices();
   100→        const preferredVoice = voices.find(v => 
   101→          v.name.includes('Google') || 
   102→          v.name.includes('Samantha') ||
   103→          v.name.includes('Daniel') ||
   104→          v.lang.startsWith('en')
   105→        );
   106→        if (preferredVoice) {
   107→          utterance.voice = preferredVoice;
   108→        }
   109→        
   110→        utterance.onend = () => {
   111→          setStatus('idle');
   112→          resolve();
   113→        };
   114→        utterance.onerror = () => {
   115→          console.error('Speech synthesis error');
   116→          setStatus('idle');
   117→          resolve();
   118→        };
   119→        
   120→        window.speechSynthesis.speak(utterance);
   121→      });
   122→    } else {
   123→      console.warn('Speech synthesis not supported');
   124→      setStatus('idle');
   125→    }
   126→  }, [addNarration]);
   127→
   128→  const executeToolCalls = useCallback((toolCalls: AgentResponse['tool_calls']) => {
   129→    for (const call of toolCalls) {
   130→      addNarration(`Executing: ${call.tool_name}`, 'action');
   131→      
   132→      switch (call.tool_name) {
   133→        case 'gmail_list_messages':
   134→          setAppState(prev => ({ ...prev, currentView: 'inbox' }));
   135→          break;
   136→          
   137→        case 'gmail_get_message':
   138→          const emailId = call.arguments.messageId as string;
   139→          setAppState(prev => ({ 
   140→            ...prev, 
   141→            currentView: 'email',
   142→            selectedEmailId: emailId 
   143→          }));
   144→          // Mark as read
   145→          setEmails(prev => prev.map(e => 
   146→            e.id === emailId ? { ...e, unread: false } : e
   147→          ));
   148→          break;
   149→          
   150→        case 'gmail_create_draft':
   151→          setAppState(prev => ({
   152→            ...prev,
   153→            currentView: 'compose',
   154→            draftEmail: {
   155→              to: call.arguments.to as string,
   156→              subject: call.arguments.subject as string,
   157→              body: call.arguments.body as string,
   158→              threadId: call.arguments.threadId as string | undefined
   159→            }
   160→          }));
   161→          break;
   162→          
   163→        case 'app_navigate':
   164→          const dest = call.arguments.destination as string;
   165→          if (['inbox', 'drafts', 'settings', 'compose'].includes(dest)) {
   166→            setAppState(prev => ({ 
   167→              ...prev, 
   168→              currentView: dest as AppState['currentView'] 
   169→            }));
   170→          }
   171→          break;
   172→      }
   173→    }
   174→  }, [addNarration]);
   175→
   176→  const processUserInput = useCallback(async (text: string) => {
   177→    if (!text.trim()) return;
   178→    
   179→    setStatus('processing');
   180→    addNarration('Processing your request...', 'info');
   181→    
   182→    // Add user message to conversation
   183→    const userMessage: ConversationMessage = {
   184→      role: 'user',
   185→      content: text,
   186→      timestamp: new Date()
   187→    };
   188→    setConversation(prev => [...prev, userMessage]);
   189→    
   190→    try {
   191→      const response = await fetch(
   192→        `${import.meta.env.VITE_SUPABASE_URL}/functions/v1/echopilot-agent`,
   193→        {
   194→          method: 'POST',
   195→          headers: {
   196→            'Content-Type': 'application/json',
   197→            'apikey': import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY,
   198→            'Authorization': `Bearer ${import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY}`,
   199→          },
   200→          body: JSON.stringify({
   201→            message: text,
   202→            conversationHistory: conversation.slice(-10).map(m => ({
   203→              role: m.role,
   204→              content: m.content
   205→            })),
   206→            currentState: appState,
   207→            demoInbox: isDemoMode ? emails : null
   208→          }),
   209→        }
   210→      );
   211→
   212→      if (!response.ok) {
   213→        throw new Error('Agent request failed');
   214→      }
   215→
   216→      const agentResponse: AgentResponse = await response.json();
   217→      
   218→      // Add narration steps
   219→      agentResponse.narration_steps?.forEach(step => {
   220→        addNarration(step, 'action');
   221→      });
   222→
   223→      const hasListTool = agentResponse.tool_calls?.some(c => c.tool_name === 'gmail_list_messages') ?? false;
   224→
   225→      const formatSpokenDate = (iso: string) => {
   226→        const date = new Date(iso);
   227→        const now = new Date();
   228→        const diffDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24));
   229→        if (diffDays === 0) return `today at ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`;
   230→        if (diffDays === 1) return `yesterday at ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`;
   231→        return `on ${date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`;
   232→      };
   233→
   234→      const buildUnreadInboxSpeech = () => {
   235→        const unread = emails.filter(e => e.unread);
   236→        if (unread.length === 0) {
   237→          return "You have no unread emails.";
   238→        }
   239→
   240→        const items = unread.slice(0, 5).map((email, idx) => {
   241→          const who = appState.privacyMode ? '' : `From ${email.fromName}. `;
   242→          return `${idx + 1}) ${who}Subject: ${email.subject}. Received ${formatSpokenDate(email.date)}.`;
   243→        });
   244→
   245→        const more = unread.length > 5 ? ` And ${unread.length - 5} more.` : '';
   246→        return `You have ${unread.length} unread emails.${more} ${items.join(' ')} Want me to open one?`;
   247→      };
   248→
   249→      // Prefer speaking actual inbox content when the user asks to read unread emails
   250→      let assistantText = agentResponse.assistant_reply;
   251→      let speakOut = agentResponse.speak;
   252→
   253→      if (isDemoMode && hasListTool) {
   254→        const spoken = buildUnreadInboxSpeech();
   255→        assistantText = spoken;
   256→        speakOut = spoken;
   257→        addNarration('Reading your unread emails out loud.', 'action');
   258→      }
   259→      
   260→      // Add assistant response to conversation
   261→      const assistantMessage: ConversationMessage = {
   262→        role: 'assistant',
   263→        content: assistantText,
   264→        timestamp: new Date()
   265→      };
   266→      setConversation(prev => [...prev, assistantMessage]);
   267→      
   268→      // Handle confirmation requests
   269→      if (agentResponse.ask_user_confirmation) {
   270→        setPendingConfirmation(agentResponse.confirmation_question || 'Do you want to proceed?');
   271→        pendingActionRef.current = agentResponse;
   272→        await speakText(`${speakOut || assistantText} ${agentResponse.confirmation_question || ''}`.trim());
   273→      } else {
   274→        // Execute tool calls
   275→        if (agentResponse.tool_calls?.length > 0) {
   276→          executeToolCalls(agentResponse.tool_calls);
   277→        }
   278→        
   279→        // Speak response
   280→        await speakText(speakOut || assistantText);
   281→      }
   282→      
   283→    } catch (error) {
   284→      console.error('Agent error:', error);
   285→      addNarration('Error processing request', 'info');
   286→      await speakText("I'm sorry, I encountered an error. Please try again.");
   287→    }
   288→  }, [conversation, appState, isDemoMode, emails, addNarration, speakText, executeToolCalls]);
   289→
   290→  const confirmAction = useCallback(async () => {
   291→    if (!pendingActionRef.current) return;
   292→    
   293→    addNarration('Action confirmed', 'confirmation');
   294→    
   295→    // Execute the pending tool calls
   296→    if (pendingActionRef.current.tool_calls?.length > 0) {
   297→      executeToolCalls(pendingActionRef.current.tool_calls);
   298→    }
   299→    
   300→    await speakText("Done. The action has been completed.");
   301→    
   302→    setPendingConfirmation(null);
   303→    pendingActionRef.current = null;
   304→  }, [addNarration, speakText, executeToolCalls]);
   305→
   306→  const cancelAction = useCallback(() => {
   307→    addNarration('Action cancelled', 'info');
   308→    setPendingConfirmation(null);
   309→    pendingActionRef.current = null;
   310→    speakText("Okay, I've cancelled that action.");
   311→  }, [addNarration, speakText]);
   312→
   313→  const togglePrivacyMode = useCallback(() => {
   314→    setAppState(prev => {
   315→      const newMode = !prev.privacyMode;
   316→      speakText(newMode ? "Privacy mode is now on." : "Privacy mode is now off.");
   317→      return { ...prev, privacyMode: newMode };
   318→    });
   319→  }, [speakText]);
   320→
   321→  const navigateTo = useCallback((view: AppState['currentView']) => {
   322→    setAppState(prev => ({ ...prev, currentView: view, selectedEmailId: null }));
   323→  }, []);
   324→
   325→  const selectEmail = useCallback((emailId: string) => {
   326→    setAppState(prev => ({ 
   327→      ...prev, 
   328→      currentView: 'email',
   329→      selectedEmailId: emailId 
   330→    }));
   331→    setEmails(prev => prev.map(e => 
   332→      e.id === emailId ? { ...e, unread: false } : e
   333→    ));
   334→  }, []);
   335→
   336→  const selectedEmail = emails.find(e => e.id === appState.selectedEmailId) || null;
   337→
   338→  return {
   339→    status,
   340→    conversation,
   341→    narrationLog,
   342→    appState,
   343→    emails,
   344→    selectedEmail,
   345→    isDemoMode,
   346→    pendingConfirmation,
   347→    processUserInput,
   348→    confirmAction,
   349→    cancelAction,
   350→    setDemoMode: setIsDemoMode,
   351→    togglePrivacyMode,
   352→    navigateTo,
   353→    selectEmail,
   354→    speakText,
   355→    setStatus
   356→  };
   357→}
   358→

