import { useState, useCallback, useRef } from 'react';
import { demoInbox, DemoEmail } from '@/data/demoInbox';

export interface ConversationMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

export interface NarrationStep {
  text: string;
  timestamp: Date;
  type: 'action' | 'info' | 'confirmation';
}

export interface AgentResponse {
  assistant_reply: string;
  speak: string;
  ask_user_confirmation: boolean;
  confirmation_question: string | null;
  tool_calls: Array<{
    tool_name: string;
    arguments: Record<string, unknown>;
  }>;
  narration_steps: string[];
}

export interface AppState {
  currentView: 'inbox' | 'email' | 'drafts' | 'compose' | 'settings';
  selectedEmailId: string | null;
  draftEmail: {
    to: string;
    subject: string;
    body: string;
    threadId?: string;
  } | null;
  privacyMode: boolean;
}

interface UseEchoPilotReturn {
  status: 'idle' | 'listening' | 'processing' | 'speaking';
  conversation: ConversationMessage[];
  narrationLog: NarrationStep[];
  appState: AppState;
  emails: DemoEmail[];
  selectedEmail: DemoEmail | null;
  isDemoMode: boolean;
  pendingConfirmation: string | null;

  processUserInput: (text: string) => Promise<void>;
  confirmAction: () => Promise<void>;
  cancelAction: () => void;
  setDemoMode: (demo: boolean) => void;
  togglePrivacyMode: () => void;
  navigateTo: (view: AppState['currentView']) => void;
  selectEmail: (emailId: string) => void;
  speakText: (text: string) => Promise<void>;
  setStatus: (status: 'idle' | 'listening' | 'processing' | 'speaking') => void;
}

export function useEchoPilot(): UseEchoPilotReturn {
  const [status, setStatus] = useState<'idle' | 'listening' | 'processing' | 'speaking'>('idle');
  const [conversation, setConversation] = useState<ConversationMessage[]>([]);
  const [narrationLog, setNarrationLog] = useState<NarrationStep[]>([]);
  const [appState, setAppState] = useState<AppState>({
    currentView: 'inbox',
    selectedEmailId: null,
    draftEmail: null,
    privacyMode: false
  });
  const [emails, setEmails] = useState<DemoEmail[]>(demoInbox);
  const [isDemoMode, setIsDemoMode] = useState(true);
  const [pendingConfirmation, setPendingConfirmation] = useState<string | null>(null);
  const pendingActionRef = useRef<AgentResponse | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  const addNarration = useCallback((text: string, type: NarrationStep['type'] = 'info') => {
    setNarrationLog(prev => [...prev, { text, timestamp: new Date(), type }]);
  }, []);

  const speakText = useCallback(async (text: string) => {
    if (!text) return;

    setStatus('speaking');
    addNarration(`Speaking: "${text}"`, 'info');

    // Use browser's built-in speech synthesis (reliable and instant)
    if ('speechSynthesis' in window) {
      // Cancel any ongoing speech
      window.speechSynthesis.cancel();

      return new Promise<void>((resolve) => {
        const utterance = new SpeechSynthesisUtterance(text);
        utterance.rate = 0.95;
        utterance.pitch = 1;
        utterance.volume = 1;

        // Try to use a good voice
        const voices = window.speechSynthesis.getVoices();
        const preferredVoice = voices.find(v => 
          v.name.includes('Google') || 
          v.name.includes('Samantha') ||
          v.name.includes('Daniel') ||
          v.lang.startsWith('en')
        );
        if (preferredVoice) {
          utterance.voice = preferredVoice;
        }

        utterance.onend = () => {
          setStatus('idle');
          resolve();
        };
        utterance.onerror = () => {
          console.error('Speech synthesis error');
          setStatus('idle');
          resolve();
        };

        window.speechSynthesis.speak(utterance);
      });
    } else {
      console.warn('Speech synthesis not supported');
      setStatus('idle');
    }
  }, [addNarration]);

  const executeToolCalls = useCallback((toolCalls: AgentResponse['tool_calls']) => {
    for (const call of toolCalls) {
      addNarration(`Executing: ${call.tool_name}`, 'action');

      switch (call.tool_name) {
        case 'gmail_list_messages':
          setAppState(prev => ({ ...prev, currentView: 'inbox' }));
          break;

        case 'gmail_get_message':
          const emailId = call.arguments.messageId as string;
          setAppState(prev => ({ 
            ...prev, 
            currentView: 'email',
            selectedEmailId: emailId 
          }));
          // Mark as read
          setEmails(prev => prev.map(e => 
            e.id === emailId ? { ...e, unread: false } : e
          ));
          break;

        case 'gmail_create_draft':
          setAppState(prev => ({
            ...prev,
            currentView: 'compose',
            draftEmail: {
              to: call.arguments.to as string,
              subject: call.arguments.subject as string,
              body: call.arguments.body as string,
              threadId: call.arguments.threadId as string | undefined
            }
          }));
          break;

        case 'app_navigate':
          const dest = call.arguments.destination as string;
          if (['inbox', 'drafts', 'settings', 'compose'].includes(dest)) {
            setAppState(prev => ({ 
              ...prev, 
              currentView: dest as AppState['currentView'] 
            }));
          }
          break;
      }
    }
  }, [addNarration]);

  const processUserInput = useCallback(async (text: string) => {
    if (!text.trim()) return;

    setStatus('processing');
    addNarration('Processing your request...', 'info');

    // Add user message to conversation
    const userMessage: ConversationMessage = {
      role: 'user',
      content: text,
      timestamp: new Date()
    };
    setConversation(prev => [...prev, userMessage]);

    try {
      const response = await fetch(
        `${import.meta.env.VITE_SUPABASE_URL}/functions/v1/echopilot-agent`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'apikey': import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY,
            'Authorization': `Bearer ${import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY}`,
          },
          body: JSON.stringify({
            message: text,
            conversationHistory: conversation.slice(-10).map(m => ({
              role: m.role,
              content: m.content
            })),
            currentState: appState,
            demoInbox: isDemoMode ? emails : null
          }),
        }
      );

      if (!response.ok) {
        throw new Error('Agent request failed');
      }

      const agentResponse: AgentResponse = await response.json();

      // Add narration steps
      agentResponse.narration_steps?.forEach(step => {
        addNarration(step, 'action');
      });

      const hasListTool = agentResponse.tool_calls?.some(c => c.tool_name === 'gmail_list_messages') ?? false;

      const formatSpokenDate = (iso: string) => {
        const date = new Date(iso);
        const now = new Date();
        const diffDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24));
        if (diffDays === 0) return `today at ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`;
        if (diffDays === 1) return `yesterday at ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`;
        return `on ${date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`;
      };

      const buildUnreadInboxSpeech = () => {
        const unread = emails.filter(e => e.unread);
        if (unread.length === 0) {
          return "You have no unread emails.";
        }

        const items = unread.slice(0, 5).map((email, idx) => {
          const who = appState.privacyMode ? '' : `From ${email.fromName}. `;
          return `${idx + 1}) ${who}Subject: ${email.subject}. Received ${formatSpokenDate(email.date)}.`;
        });

        const more = unread.length > 5 ? ` And ${unread.length - 5} more.` : '';
        return `You have ${unread.length} unread emails.${more} ${items.join(' ')} Want me to open one?`;
      };

      // Prefer speaking actual inbox content when the user asks to read unread emails
      let assistantText = agentResponse.assistant_reply;
      let speakOut = agentResponse.speak;

      if (isDemoMode && hasListTool) {
        const spoken = buildUnreadInboxSpeech();
        assistantText = spoken;
        speakOut = spoken;
        addNarration('Reading your unread emails out loud.', 'action');
      }

      // Add assistant response to conversation
      const assistantMessage: ConversationMessage = {
        role: 'assistant',
        content: assistantText,
        timestamp: new Date()
      };
      setConversation(prev => [...prev, assistantMessage]);

      // Handle confirmation requests
      if (agentResponse.ask_user_confirmation) {
        setPendingConfirmation(agentResponse.confirmation_question || 'Do you want to proceed?');
        pendingActionRef.current = agentResponse;
        await speakText(`${speakOut || assistantText} ${agentResponse.confirmation_question || ''}`.trim());
      } else {
        // Execute tool calls
        if (agentResponse.tool_calls?.length > 0) {
          executeToolCalls(agentResponse.tool_calls);
        }

        // Speak response
        await speakText(speakOut || assistantText);
      }

    } catch (error) {
      console.error('Agent error:', error);
      addNarration('Error processing request', 'info');
      await speakText("I'm sorry, I encountered an error. Please try again.");
    }
  }, [conversation, appState, isDemoMode, emails, addNarration, speakText, executeToolCalls]);

  const confirmAction = useCallback(async () => {
    if (!pendingActionRef.current) return;

    addNarration('Action confirmed', 'confirmation');

    // Execute the pending tool calls
    if (pendingActionRef.current.tool_calls?.length > 0) {
      executeToolCalls(pendingActionRef.current.tool_calls);
    }

    await speakText("Done. The action has been completed.");

    setPendingConfirmation(null);
    pendingActionRef.current = null;
  }, [addNarration, speakText, executeToolCalls]);

  const cancelAction = useCallback(() => {
    addNarration('Action cancelled', 'info');
    setPendingConfirmation(null);
    pendingActionRef.current = null;
    speakText("Okay, I've cancelled that action.");
  }, [addNarration, speakText]);

  const togglePrivacyMode = useCallback(() => {
    setAppState(prev => {
      const newMode = !prev.privacyMode;
      speakText(newMode ? "Privacy mode is now on." : "Privacy mode is now off.");
      return { ...prev, privacyMode: newMode };
    });
  }, [speakText]);

  const navigateTo = useCallback((view: AppState['currentView']) => {
    setAppState(prev => ({ ...prev, currentView: view, selectedEmailId: null }));
  }, []);

  const selectEmail = useCallback((emailId: string) => {
    setAppState(prev => ({ 
      ...prev, 
      currentView: 'email',
      selectedEmailId: emailId 
    }));
    setEmails(prev => prev.map(e => 
      e.id === emailId ? { ...e, unread: false } : e
    ));
  }, []);

  const selectedEmail = emails.find(e => e.id === appState.selectedEmailId) || null;

  return {
    status,
    conversation,
    narrationLog,
    appState,
    emails,
    selectedEmail,
    isDemoMode,
    pendingConfirmation,
    processUserInput,
    confirmAction,
    cancelAction,
    setDemoMode: setIsDemoMode,
    togglePrivacyMode,
    navigateTo,
    selectEmail,
    speakText,
    setStatus
  };
}

