import { useEffect, useCallback } from 'react';
import { useEchoPilot } from '@/hooks/useEchoPilot';
import { useWebSpeechRecognition } from '@/hooks/useWebSpeechRecognition';
import { VoiceButton } from '@/components/VoiceButton';
import { EmailList } from '@/components/EmailList';
import { EmailView } from '@/components/EmailView';
import { NarrationLog } from '@/components/NarrationLog';
import { TranscriptPanel } from '@/components/TranscriptPanel';
import { SettingsDialog } from '@/components/SettingsDialog';
import { Headphones } from 'lucide-react';

const Index = () => {
  const {
    status,
    conversation,
    narrationLog,
    appState,
    emails,
    selectedEmail,
    isDemoMode,
    pendingConfirmation,
    processUserInput,
    confirmAction,
    cancelAction,
    setDemoMode,
    togglePrivacyMode,
    navigateTo,
    selectEmail,
    speakText,
    setStatus
  } = useEchoPilot();

  const {
    isListening,
    transcript,
    startListening,
    stopListening,
    isSupported
  } = useWebSpeechRecognition();

  // Handle voice input start
  const handleStartListening = useCallback(() => {
    if (status !== 'idle') return;
    setStatus('listening');
    startListening();
  }, [status, setStatus, startListening]);

  // Handle voice input end
  const handleStopListening = useCallback(async () => {
    if (status !== 'listening') return;
    stopListening();

    if (transcript.trim()) {
      await processUserInput(transcript);
    } else {
      setStatus('idle');
    }
  }, [status, stopListening, transcript, processUserInput, setStatus]);

  // Keyboard shortcuts (Space to talk)
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.code === 'Space' && !e.repeat && e.target === document.body) {
        e.preventDefault();
        handleStartListening();
      }
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.code === 'Space' && e.target === document.body) {
        e.preventDefault();
        handleStopListening();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [handleStartListening, handleStopListening]);

  // Welcome message on mount
  useEffect(() => {
    const timer = setTimeout(() => {
      speakText("Welcome to EchoPilot. Hold the spacebar or click the microphone to speak. Try saying: read my unread emails.");
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="min-h-screen bg-background text-foreground dark">
      {/* Header */}
      <header className="border-b border-border p-4">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Headphones className="w-8 h-8 text-primary" aria-hidden="true" />
            <h1 className="text-2xl font-bold">EchoPilot</h1>
            <span className="px-2 py-1 text-xs bg-primary/20 text-primary rounded-full">
              {isDemoMode ? 'Demo' : 'Live'}
            </span>
          </div>
          <SettingsDialog
            isDemoMode={isDemoMode}
            onDemoModeChange={setDemoMode}
            privacyMode={appState.privacyMode}
            onPrivacyModeChange={togglePrivacyMode}
          />
        </div>
      </header>

      {/* Main content - 3 panel layout */}
      <main className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-12 gap-4 p-4 h-[calc(100vh-80px)]">
        {/* Left Panel - Voice Controls & Transcript */}
        <div className="lg:col-span-3 flex flex-col gap-4">
          {/* Voice button area */}
          <div className="bg-card rounded-xl p-6 flex flex-col items-center justify-center min-h-[300px]">
            <VoiceButton
              status={status}
              onMouseDown={handleStartListening}
              onMouseUp={handleStopListening}
              onKeyDown={() => {}}
              onKeyUp={() => {}}
              disabled={!isSupported}
            />
          </div>

          {/* Transcript */}
          <div className="bg-card rounded-xl flex-1 overflow-hidden min-h-[200px]">
            <TranscriptPanel
              conversation={conversation}
              currentTranscript={transcript}
              isListening={isListening || status === 'listening'}
            />
          </div>
        </div>

        {/* Center Panel - Email View */}
        <div className="lg:col-span-6 bg-card rounded-xl overflow-hidden">
          {appState.currentView === 'email' && selectedEmail ? (
            <EmailView
              email={selectedEmail}
              onBack={() => navigateTo('inbox')}
              privacyMode={appState.privacyMode}
            />
          ) : (
            <EmailList
              emails={emails}
              selectedId={appState.selectedEmailId}
              onSelect={selectEmail}
              privacyMode={appState.privacyMode}
            />
          )}
        </div>

        {/* Right Panel - Activity Log */}
        <div className="lg:col-span-3 bg-card rounded-xl overflow-hidden">
          <NarrationLog
            steps={narrationLog}
            pendingConfirmation={pendingConfirmation}
            onConfirm={confirmAction}
            onCancel={cancelAction}
          />
        </div>
      </main>
    </div>
  );
};

export default Index;

