'use client';

import { useState, useEffect, useRef } from 'react';

// Inline clsx functionality - joins classnames and filters out falsy values
function clsx(...args: any[]): string {
  return args.filter(Boolean).join(' ');
}

// Circle data structure
interface Circle {
  id: string;
  x: number;
  y: number;
  size: number;
  color: string;
  opacity: number;
  xVelocity: number;
  yVelocity: number;
}

// Hardcoded color palette
const COLOR_PALETTE = [
  '#FF6B6B', // Coral Red
  '#4ECDC4', // Turquoise
  '#45B7D1', // Sky Blue
  '#FFA07A', // Light Salmon
  '#98D8C8', // Mint
  '#F7DC6F', // Pale Yellow
  '#BB8FCE', // Lavender
  '#85C1E2', // Light Blue
  '#F8B88B', // Peach
  '#A8E6CF', // Mint Green
];

export function BackgroundCircles() {
  const [circles, setCircles] = useState<Circle[]>([]);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const animationFrameId = useRef<number | null>(null);
  const circlesRef = useRef<Circle[]>([]);

  // Determine number of circles based on screen size
  const getCircleCount = (width: number, height: number): number => {
    const area = width * height;

    // Mobile (< 640px)
    if (width < 640) return Math.floor(area / 50000);
    // Tablet (640px - 1024px)
    if (width < 1024) return Math.floor(area / 40000);
    // Desktop (>= 1024px)
    return Math.floor(area / 30000);
  };

  // Initialize circles with random properties
  const initializeCircles = (width: number, height: number): Circle[] => {
    const count = getCircleCount(width, height);
    const newCircles: Circle[] = [];

    for (let i = 0; i < count; i++) {
      try {
        const color = COLOR_PALETTE[Math.floor(Math.random() * COLOR_PALETTE.length)];

        // Size varies by screen size
        const minSize = width < 640 ? 20 : width < 1024 ? 30 : 40;
        const maxSize = width < 640 ? 80 : width < 1024 ? 120 : 160;

        newCircles.push({
          id: `circle-${i}-${Date.now()}`,
          x: Math.random() * width,
          y: Math.random() * height,
          size: minSize + Math.random() * (maxSize - minSize),
          color: color,
          opacity: 0.1 + Math.random() * 0.4, // 0.1 to 0.5
          xVelocity: (Math.random() - 0.5) * 0.5, // -0.25 to 0.25
          yVelocity: (Math.random() - 0.5) * 0.5,
        });
      } catch (error) {
        console.error('Error initializing circle:', error);
        // Use default color on error
        newCircles.push({
          id: `circle-${i}-${Date.now()}`,
          x: Math.random() * width,
          y: Math.random() * height,
          size: 60,
          color: '#4ECDC4',
          opacity: 0.3,
          xVelocity: (Math.random() - 0.5) * 0.5,
          yVelocity: (Math.random() - 0.5) * 0.5,
        });
      }
    }

    return newCircles;
  };

  // Update circle positions and sizes
  const updateCircles = (circles: Circle[], width: number, height: number): Circle[] => {
    return circles.map((circle) => {
      let newX = circle.x + circle.xVelocity;
      let newY = circle.y + circle.yVelocity;
      let newXVelocity = circle.xVelocity;
      let newYVelocity = circle.yVelocity;

      // Bounce off edges with some randomness
      if (newX - circle.size / 2 < 0 || newX + circle.size / 2 > width) {
        newXVelocity = -circle.xVelocity * (0.8 + Math.random() * 0.4);
        newX = Math.max(circle.size / 2, Math.min(width - circle.size / 2, newX));
      }

      if (newY - circle.size / 2 < 0 || newY + circle.size / 2 > height) {
        newYVelocity = -circle.yVelocity * (0.8 + Math.random() * 0.4);
        newY = Math.max(circle.size / 2, Math.min(height - circle.size / 2, newY));
      }

      // Subtle size pulsing using sine wave
      const time = Date.now() / 1000;
      const sizeVariation = Math.sin(time + parseFloat(circle.id.split('-')[1]) * 0.1) * 5;

      return {
        ...circle,
        x: newX,
        y: newY,
        xVelocity: newXVelocity,
        yVelocity: newYVelocity,
        size: Math.max(20, circle.size + sizeVariation),
      };
    });
  };

  // Animation loop using requestAnimationFrame
  const animate = () => {
    circlesRef.current = updateCircles(circlesRef.current, dimensions.width, dimensions.height);
    setCircles([...circlesRef.current]);
    animationFrameId.current = requestAnimationFrame(animate);
  };

  // Handle window resize with media query listening
  useEffect(() => {
    const updateDimensions = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      setDimensions({ width, height });

      // Reinitialize circles on resize
      const newCircles = initializeCircles(width, height);
      circlesRef.current = newCircles;
      setCircles(newCircles);
    };

    // Initial setup
    updateDimensions();

    // Media query listeners for responsive behavior
    const mediaQueries = [
      window.matchMedia('(max-width: 639px)'), // Mobile
      window.matchMedia('(min-width: 640px) and (max-width: 1023px)'), // Tablet
      window.matchMedia('(min-width: 1024px)'), // Desktop
    ];

    const handleMediaChange = () => {
      updateDimensions();
    };

    // Add listeners
    mediaQueries.forEach((mq) => {
      if (mq.addEventListener) {
        mq.addEventListener('change', handleMediaChange);
      } else {
        // Fallback for older browsers
        mq.addListener(handleMediaChange);
      }
    });

    // Also listen to resize for dimension changes within same breakpoint
    window.addEventListener('resize', updateDimensions);

    return () => {
      mediaQueries.forEach((mq) => {
        if (mq.removeEventListener) {
          mq.removeEventListener('change', handleMediaChange);
        } else {
          mq.removeListener(handleMediaChange);
        }
      });
      window.removeEventListener('resize', updateDimensions);
    };
  }, []);

  // Start animation loop
  useEffect(() => {
    if (circles.length === 0) return;

    // Check for requestAnimationFrame support with fallback
    const animateWithFallback = () => {
      if (typeof requestAnimationFrame !== 'undefined') {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        // Fallback to setTimeout for older browsers
        animationFrameId.current = window.setTimeout(() => {
          animate();
        }, 1000 / 60); // 60 FPS
      }
    };

    animateWithFallback();

    return () => {
      if (animationFrameId.current !== null) {
        if (typeof cancelAnimationFrame !== 'undefined') {
          cancelAnimationFrame(animationFrameId.current);
        } else {
          clearTimeout(animationFrameId.current);
        }
      }
    };
  }, [circles.length, dimensions.width, dimensions.height]);

  // Don't render until dimensions are set
  if (dimensions.width === 0 || dimensions.height === 0) {
    return null;
  }

  return (
    <div
      className={clsx(
        'fixed',
        'inset-0',
        'pointer-events-none',
        'overflow-hidden',
        'z-0'
      )}
      style={{
        width: '100vw',
        height: '100vh',
      }}
      aria-hidden="true"
    >
      {circles.map((circle) => (
        <div
          key={circle.id}
          className={clsx(
            'absolute',
            'rounded-full',
            'transition-all',
            'duration-100',
            'ease-linear'
          )}
          style={{
            left: `${circle.x}px`,
            top: `${circle.y}px`,
            width: `${circle.size}px`,
            height: `${circle.size}px`,
            backgroundColor: circle.color,
            opacity: circle.opacity,
            transform: 'translate(-50%, -50%)',
            willChange: 'transform, width, height',
          }}
        />
      ))}

      {/* CSS for additional responsive adjustments */}
      <style jsx>{`
        /* Mobile optimizations */
        @media (max-width: 639px) {
          div[class*="rounded-full"] {
            filter: blur(1px);
          }
        }

        /* Tablet optimizations */
        @media (min-width: 640px) and (max-width: 1023px) {
          div[class*="rounded-full"] {
            filter: blur(1.5px);
          }
        }

        /* Desktop optimizations */
        @media (min-width: 1024px) {
          div[class*="rounded-full"] {
            filter: blur(2px);
          }
        }

        /* Reduce motion for accessibility */
        @media (prefers-reduced-motion: reduce) {
          div[class*="rounded-full"] {
            animation: none !important;
            transition: none !important;
          }
        }
      `}</style>
    </div>
  );
}

