    updateDimensions();

    // Media query listeners for responsive behavior
    const mediaQueries = [
      window.matchMedia('(max-width: 639px)'), // Mobile
      window.matchMedia('(min-width: 640px) and (max-width: 1023px)'), // Tablet
      window.matchMedia('(min-width: 1024px)'), // Desktop
    ];

    const handleMediaChange = () => {
      updateDimensions();
    };

    // Add listeners
    mediaQueries.forEach((mq) => {
      if (mq.addEventListener) {
        mq.addEventListener('change', handleMediaChange);
      } else {
        // Fallback for older browsers
        mq.addListener(handleMediaChange);
      }
    });

    // Also listen to resize for dimension changes within same breakpoint
    window.addEventListener('resize', updateDimensions);

    return () => {
      mediaQueries.forEach((mq) => {
        if (mq.removeEventListener) {
          mq.removeEventListener('change', handleMediaChange);
        } else {
          mq.removeListener(handleMediaChange);
        }
      });
      window.removeEventListener('resize', updateDimensions);
    };
  }, []);

  // Start animation loop
  useEffect(() => {
    if (circles.length === 0) return;

    // Check for requestAnimationFrame support with fallback
    const animateWithFallback = () => {
      if (typeof requestAnimationFrame !== 'undefined') {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        // Fallback to setTimeout for older browsers
        animationFrameId.current = window.setTimeout(() => {
          animate();
        }, 1000 / 60); // 60 FPS
      }
    };

    animateWithFallback();

    return () => {
      if (animationFrameId.current !== null) {
        if (typeof cancelAnimationFrame !== 'undefined') {
          cancelAnimationFrame(animationFrameId.current);
        } else {
          clearTimeout(animationFrameId.current);
        }
      }
    };
  }, [circles.length, dimensions.width, dimensions.height]);

  // Don't render until dimensions are set
  if (dimensions.width === 0 || dimensions.height === 0) {
    return null;
  }

  return (
    <div
      className={clsx(
        'fixed',
        'inset-0',
        'pointer-events-none',
        'overflow-hidden',
        'z-0'
      )}
      style={{
        width: '100vw',
        height: '100vh',
      }}
      aria-hidden="true"
    >
      {circles.map((circle) => (
        <div
          key={circle.id}
          className={clsx(
            'absolute',
            'rounded-full',
            'transition-all',
            'duration-100',
            'ease-linear'
          )}
          style={{
            left: `${circle.x}px`,
            top: `${circle.y}px`,
            width: `${circle.size}px`,
            height: `${circle.size}px`,
            backgroundColor: circle.color,
            opacity: circle.opacity,
            transform: 'translate(-50%, -50%)',
            willChange: 'transform, width, height',
          }}
        />
      ))}

