/**
 * Type definitions for BackgroundCircles component
 */

/**
 * Represents a single animated circle in the background
 */
export interface Circle {
  /** Unique identifier for the circle (format: circle-{index}-{timestamp}) */
  id: string;

  /** X-coordinate of the circle's center in pixels */
  x: number;

  /** Y-coordinate of the circle's center in pixels */
  y: number;

  /** Diameter of the circle in pixels */
  size: number;

  /** Hex color code from the hardcoded palette */
  color: string;

  /** Opacity value between 0.1 and 0.5 */
  opacity: number;

  /** Horizontal velocity in pixels per frame (-0.25 to 0.25) */
  xVelocity: number;

  /** Vertical velocity in pixels per frame (-0.25 to 0.25) */
  yVelocity: number;
}

/**
 * Collection of circles
 */
export type Circles = Circle[];

/**
 * Viewport dimensions
 */
export interface Dimensions {
  /** Viewport width in pixels */
  width: number;

  /** Viewport height in pixels */
  height: number;
}

/**
 * Screen size breakpoint categories
 */
export type BreakpointCategory = 'mobile' | 'tablet' | 'desktop';

/**
 * Hardcoded color palette used by the component
 */
export type ColorPalette = readonly [
  '#FF6B6B', // Coral Red
  '#4ECDC4', // Turquoise
  '#45B7D1', // Sky Blue
  '#FFA07A', // Light Salmon
  '#98D8C8', // Mint
  '#F7DC6F', // Pale Yellow
  '#BB8FCE', // Lavender
  '#85C1E2', // Light Blue
  '#F8B88B', // Peach
  '#A8E6CF', // Mint Green
];

/**
 * Configuration for circle generation based on screen size
 */
export interface CircleConfig {
  /** Minimum circle diameter in pixels */
  minSize: number;

  /** Maximum circle diameter in pixels */
  maxSize: number;

  /** Divisor for calculating circle count from viewport area */
  densityDivisor: number;

  /** CSS blur amount in pixels */
  blurAmount: number;
}

/**
 * Media query breakpoint definitions
 */
export interface MediaQueryBreakpoint {
  /** CSS media query string */
  query: string;

  /** Breakpoint category */
  category: BreakpointCategory;

  /** MediaQueryList object */
  mediaQuery?: MediaQueryList;
}

