/**
 * HeadlessUI Component Extractor Types
 * Defines the data structures for extracting and recreating HeadlessUI components
 */

/**
 * Available HeadlessUI components that can be extracted
 */
export type HeadlessUIComponent =
  | "menu"
  | "listbox"
  | "combobox"
  | "switch"
  | "disclosure"
  | "dialog"
  | "popover"
  | "radio-group"
  | "tabs"
  | "transition"
  | "portal"
  | "description"
  | "field"
  | "fieldset"
  | "input"
  | "label"
  | "legend"
  | "select"
  | "textarea";

/**
 * Component metadata including URLs and display names
 */
export interface ComponentInfo {
  name: HeadlessUIComponent;
  displayName: string;
  url: string;
