/**
 * Utility functions for extracting and processing HTML/CSS from HeadlessUI documentation
 */

/**
 * Extracts inline styles from HTML elements
 */
export function extractInlineStyles(html: string): string {
  const styleRegex = /style="([^"]*)"/g;
  const styles: string[] = [];
  let match;

  while ((match = styleRegex.exec(html)) !== null) {
    styles.push(match[1]);
  }

  return styles.join("; ");
}

/**
 * Extracts class names from HTML and converts them to CSS-like format
 */
export function extractClassNames(html: string): string[] {
  const classRegex = /class="([^"]*)"/g;
  const classes: Set<string> = new Set();
  let match;

  while ((match = classRegex.exec(html)) !== null) {
    const classList = match[1].split(" ");
    classList.forEach((className) => {
      if (className.trim()) {
        classes.add(className.trim());
      }
    });
  }

  return Array.from(classes);
}

/**
 * Cleans up HTML by removing scripts and unnecessary attributes
 */
export function sanitizeHTML(html: string): string {
  // Remove script tags
  let cleaned = html.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, "");

  // Remove event handlers (onclick, onload, etc.)
  cleaned = cleaned.replace(/\son\w+="[^"]*"/gi, "");

  // Remove data attributes that might be framework-specific
  cleaned = cleaned.replace(/\sdata-headlessui-\w+="[^"]*"/gi, "");

  return cleaned.trim();
}

/**
 * Extracts Tailwind CSS classes and generates minimal CSS
 */
export function generateCSSFromTailwind(classNames: string[]): string {
  // This is a simplified version - in production, you'd use Tailwind's JIT compiler
  const cssRules: string[] = [];

  classNames.forEach((className) => {
    // Basic mapping of common Tailwind classes to CSS
    // This would be expanded significantly in a real implementation
    const tailwindToCSS: Record<string, string> = {
      "flex": "display: flex;",
      "inline-flex": "display: inline-flex;",
      "items-center": "align-items: center;",
      "justify-center": "justify-content: center;",
      "gap-2": "gap: 0.5rem;",
      "gap-4": "gap: 1rem;",
      "p-2": "padding: 0.5rem;",
      "p-4": "padding: 1rem;",
      "rounded": "border-radius: 0.25rem;",
      "rounded-md": "border-radius: 0.375rem;",
      "rounded-lg": "border-radius: 0.5rem;",
      "bg-blue-500": "background-color: #3b82f6;",
      "text-white": "color: #ffffff;",
      "text-sm": "font-size: 0.875rem; line-height: 1.25rem;",
      "font-medium": "font-weight: 500;",
      "shadow": "box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);",
      "hover:bg-blue-600": "hover { background-color: #2563eb; }",
    };

    if (tailwindToCSS[className]) {
      cssRules.push(`.${className} { ${tailwindToCSS[className]} }`);
    }
  });

  return cssRules.join("\n");
}

/**
 * Formats HTML string for better readability
 */
export function formatHTML(html: string): string {
  let formatted = html;
  let indent = 0;
  const indentSize = 2;

  const tokens = formatted.split(/(<[^>]+>)/g).filter(Boolean);
  const lines: string[] = [];

  tokens.forEach((token) => {
    if (token.startsWith("</")) {
      // Closing tag
      indent -= indentSize;
      lines.push(" ".repeat(Math.max(0, indent)) + token);
    } else if (token.startsWith("<") && !token.endsWith("/>")) {
      // Opening tag
      lines.push(" ".repeat(indent) + token);
      if (!token.startsWith("<!")) {
        indent += indentSize;
      }
    } else if (token.startsWith("<") && token.endsWith("/>")) {
      // Self-closing tag
      lines.push(" ".repeat(indent) + token);
    } else {
      // Text content
      const trimmed = token.trim();
      if (trimmed) {
        lines.push(" ".repeat(indent) + trimmed);
      }
    }
  });

  return lines.join("\n");
}

/**
 * Extracts code examples from documentation HTML
 */
export function extractCodeExample(html: string, language: "jsx" | "tsx" | "html" = "jsx"): string | null {
  // Look for code blocks with the specified language
  const codeRegex = new RegExp(`<code[^>]*class="[^"]*language-${language}[^"]*"[^>]*>([\\s\\S]*?)<\\/code>`, "i");
  const match = html.match(codeRegex);

  if (match && match[1]) {
    // Decode HTML entities
    let code = match[1];
    code = code.replace(/&lt;/g, "<");
    code = code.replace(/&gt;/g, ">");
    code = code.replace(/&amp;/g, "&");
    code = code.replace(/&quot;/g, '"');
    code = code.replace(/&#39;/g, "'");

    return code.trim();
  }

  return null;
}

/**
 * Converts HTML to React-compatible JSX format
 */
export function htmlToJSX(html: string): string {
  let jsx = html;

  // Convert class to className
  jsx = jsx.replace(/\sclass=/g, " className=");

  // Convert style strings to style objects
  jsx = jsx.replace(/style="([^"]*)"/g, (match, styleString) => {
    const styles = styleString.split(";").filter(Boolean);
    const styleObj: Record<string, string> = {};

    styles.forEach((style: string) => {
      const [property, value] = style.split(":").map((s: string) => s.trim());
      if (property && value) {
        // Convert kebab-case to camelCase
        const camelProperty = property.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
        styleObj[camelProperty] = value;
      }
    });

    return `style={${JSON.stringify(styleObj)}}`;
  });

  // Convert self-closing tags
  jsx = jsx.replace(/<(img|input|br|hr)([^>]*?)>/g, "<$1$2 />");

  return jsx;
}

/**
 * Debounce function for search/filter operations
 */
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout | null = null;

  return function executedFunction(...args: Parameters<T>) {
    const later = () => {
      timeout = null;
      func(...args);
    };

    if (timeout) {
      clearTimeout(timeout);
    }
    timeout = setTimeout(later, wait);
  };
}

/**
 * Parses component metadata from documentation page
 */
export interface ComponentMetadata {
  name: string;
  description: string;
  props?: Record<string, string>;
  examples?: string[];
}

export function parseComponentMetadata(html: string): Partial<ComponentMetadata> {
  const metadata: Partial<ComponentMetadata> = {};

  // Extract component name from h1 or title
  const nameMatch = html.match(/<h1[^>]*>([^<]+)<\/h1>/i);
  if (nameMatch) {
    metadata.name = nameMatch[1].trim();
  }

  // Extract description from first paragraph after h1
  const descMatch = html.match(/<h1[^>]*>.*?<\/h1>\\s*<p[^>]*>([^<]+)<\/p>/is);
  if (descMatch) {
    metadata.description = descMatch[1].trim();
  }

  return metadata;
}

