"use server";

import type {
  ExtractionConfig,
  ExtractionResult,
  ExtractedComponent,
  HeadlessUIComponent,
} from "./types";
import { HEADLESSUI_COMPONENTS } from "./types";
import {
  sanitizeHTML,
  extractClassNames,
  extractCodeExample,
  parseComponentMetadata,
  formatHTML,
} from "./util";

/**
 * Fetches HTML content from a URL
 * Note: This is a simplified version. In production, you would use cheerio or jsdom
 */
async function fetchHTML(url: string): Promise<string> {
  try {
    const response = await fetch(url, {
      headers: {
        "User-Agent": "Mozilla/5.0 (compatible; HeadlessUIExtractor/1.0)",
      },
      cache: "no-store",
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    return await response.text();
  } catch (error) {
    throw new Error(`Failed to fetch HTML: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

/**
 * Extracts component structure from HTML
 * This is a placeholder implementation - in production, you'd use a proper HTML parser
 */
function extractComponentStructure(html: string, componentName: string): string {
  // Look for code examples in the documentation
  const codeExample = extractCodeExample(html, "jsx") || extractCodeExample(html, "tsx");

  if (codeExample) {
    return codeExample;
  }

  // Fallback: try to extract from demo sections
  const demoRegex = /<div[^>]*class="[^"]*demo[^"]*"[^>]*>([\s\S]*?)<\/div>/i;
  const demoMatch = html.match(demoRegex);

  if (demoMatch && demoMatch[1]) {
    return sanitizeHTML(demoMatch[1]);
  }

  // If no code example found, return a template
  return `<!-- Component structure for ${componentName} could not be extracted -->
<div className="component-placeholder">
  <p>Component: ${componentName}</p>
  <p>Please refer to the HeadlessUI documentation for implementation details.</p>
</div>`;
}

/**
 * Extracts CSS/Tailwind classes from the component
 */
function extractComponentStyles(html: string): string {
  const classNames = extractClassNames(html);

  if (classNames.length === 0) {
    return "/* No styles found */";
  }

  // Return the Tailwind classes as a comment, since they're applied via className
  return `/*
 * Tailwind CSS classes used in this component:
 * ${classNames.join(", ")}
 *
 * Note: Ensure Tailwind CSS is configured in your project to use these classes.
 */`;
}

/**
 * Main action to extract a HeadlessUI component
 */
export async function extractComponent(
  config: ExtractionConfig
): Promise<ExtractionResult> {
  try {
    const componentInfo = HEADLESSUI_COMPONENTS[config.componentName];

    if (!componentInfo) {
      return {
        success: false,
        error: {
          message: `Component "${config.componentName}" not found`,
          code: "NOT_FOUND",
        },
      };
    }

    // Determine the URL based on framework preference
    const framework = config.framework || "react";
    const url = componentInfo.url.replace("/react/", `/${framework}/`);

    // Fetch the documentation page
    let html: string;
    try {
      html = await fetchHTML(url);
    } catch (error) {
      return {
        success: false,
        error: {
          message: error instanceof Error ? error.message : "Network error occurred",
          code: "NETWORK_ERROR",
        },
      };
    }

    // Extract component structure
    const componentHTML = extractComponentStructure(html, config.componentName);

    // Extract styles
    const componentCSS = config.includeStyles !== false
      ? extractComponentStyles(componentHTML)
      : "";

    // Parse metadata
    const metadata = parseComponentMetadata(html);

    // Create the extracted component object
    const extractedComponent: ExtractedComponent = {
      name: config.componentName,
      html: formatHTML(componentHTML),
      css: componentCSS,
      metadata: {
        extractedAt: new Date(),
        sourceUrl: url,
        framework,
      },
    };

    return {
      success: true,
      data: extractedComponent,
    };
  } catch (error) {
    return {
      success: false,
      error: {
        message: error instanceof Error ? error.message : "Unknown error occurred",
        code: "UNKNOWN_ERROR",
      },
    };
  }
}

/**
 * Gets the list of available components
 */
export async function getAvailableComponents() {
  return Object.values(HEADLESSUI_COMPONENTS).map((component) => ({
    name: component.name,
    displayName: component.displayName,
    description: component.description,
  }));
}

/**
 * Validates a component name
 */
export async function validateComponent(
  componentName: string
): Promise<boolean> {
  return componentName in HEADLESSUI_COMPONENTS;
}

/**
 * Gets detailed information about a specific component
 */
export async function getComponentInfo(componentName: HeadlessUIComponent) {
  const component = HEADLESSUI_COMPONENTS[componentName];

  if (!component) {
    return null;
  }

  return {
    ...component,
    exampleUrl: component.url,
  };
}

