"use client";

import { useState, useCallback, useEffect } from "react";
import type {
  HeadlessUIComponent,
  ExtractionConfig,
  ExtractionResult,
  ExtractedComponent,
  ExtractionStatus,
} from "./types";
import { extractComponent, getAvailableComponents, getComponentInfo } from "./actions";

/**
 * Hook for managing component extraction state
 */
export function useComponentExtractor() {
  const [selectedComponent, setSelectedComponent] = useState<HeadlessUIComponent | null>(null);
  const [extractedData, setExtractedData] = useState<ExtractedComponent | null>(null);
  const [status, setStatus] = useState<ExtractionStatus>("idle");
  const [error, setError] = useState<string | null>(null);

  const extract = useCallback(async (config: ExtractionConfig) => {
    setStatus("loading");
    setError(null);

    try {
      const result: ExtractionResult = await extractComponent(config);

      if (result.success && result.data) {
        setExtractedData(result.data);
        setStatus("success");
      } else {
        setError(result.error?.message || "Unknown error occurred");
        setStatus("error");
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to extract component");
      setStatus("error");
    }
  }, []);

  const reset = useCallback(() => {
    setSelectedComponent(null);
    setExtractedData(null);
    setStatus("idle");
    setError(null);
  }, []);

  return {
    selectedComponent,
    setSelectedComponent,
    extractedData,
    status,
    error,
    extract,
    reset,
    isLoading: status === "loading",
    isSuccess: status === "success",
    isError: status === "error",
  };
}

/**
 * Hook for fetching available components list
 */
export function useAvailableComponents() {
  const [components, setComponents] = useState<Array<{
    name: HeadlessUIComponent;
    displayName: string;
    description: string;
  }>>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchComponents() {
      try {
        setIsLoading(true);
        const data = await getAvailableComponents();
        setComponents(data as any);
      } catch (err) {
        setError(err instanceof Error ? err.message : "Failed to load components");
      } finally {
        setIsLoading(false);
      }
    }

    fetchComponents();
  }, []);

  return { components, isLoading, error };
}

/**
 * Hook for fetching component information
 */
export function useComponentInfo(componentName: HeadlessUIComponent | null) {
  const [info, setInfo] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!componentName) {
      setInfo(null);
      return;
    }

    async function fetchInfo() {
      try {
        setIsLoading(true);
        setError(null);
        const data = await getComponentInfo(componentName);
        setInfo(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : "Failed to load component info");
      } finally {
        setIsLoading(false);
      }
    }

    fetchInfo();
  }, [componentName]);

  return { info, isLoading, error };
}

/**
 * Legacy hook for backwards compatibility
 */
export function useHelloWorld() {
  const [data, setData] = useState<string>("");
  const [isPending, setIsPending] = useState(false);

  const mutate = useCallback(async () => {
    setIsPending(true);
    try {
      // Simulate async operation
      await new Promise((resolve) => setTimeout(resolve, 1000));
      setData("Hello World from HeadlessUI Component Extractor!");
    } finally {
      setIsPending(false);
    }
  }, []);

  return { data, isPending, mutate };
}

