'use client';

import { useEffect, useRef, useState } from 'react';

/**
 * BackgroundCircles Component
 *
 * A self-contained React component that renders an animated grid of circles.
 * Features:
 * - Animated circles that move and change size dynamically
 * - Hardcoded color palette
 * - Responsive behavior using CSS media queries
 * - No external dependencies (except React)
 */

interface Circle {
  x: number;
  y: number;
  size: number;
  targetSize: number;
  speedX: number;
  speedY: number;
  color: string;
  opacity: number;
  targetOpacity: number;
}

const COLORS = [
  'rgba(59, 130, 246, 0.5)',   // blue
  'rgba(139, 92, 246, 0.5)',   // purple
  'rgba(236, 72, 153, 0.5)',   // pink
  'rgba(251, 146, 60, 0.5)',   // orange
  'rgba(34, 197, 94, 0.5)',    // green
  'rgba(14, 165, 233, 0.5)',   // sky
];

export function BackgroundCircles() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const circlesRef = useRef<Circle[]>([]);
  const animationFrameRef = useRef<number>();
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  // Initialize circles
  useEffect(() => {
    const initCircles = () => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const width = canvas.width;
      const height = canvas.height;

      // Determine number of circles based on screen size
      const isMobile = width < 640;
      const isTablet = width >= 640 && width < 1024;
      const circleCount = isMobile ? 12 : isTablet ? 20 : 30;

      const circles: Circle[] = [];

      for (let i = 0; i < circleCount; i++) {
        const size = Math.random() * 80 + 40;
        circles.push({
          x: Math.random() * width,
          y: Math.random() * height,
          size: size,
          targetSize: size,
          speedX: (Math.random() - 0.5) * 0.5,
          speedY: (Math.random() - 0.5) * 0.5,
          color: COLORS[Math.floor(Math.random() * COLORS.length)],
          opacity: Math.random() * 0.3 + 0.2,
          targetOpacity: Math.random() * 0.3 + 0.2,
        });
      }

      circlesRef.current = circles;
    };

    initCircles();
  }, [dimensions]);

  // Handle resize
  useEffect(() => {
    const updateDimensions = () => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Animation loop
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      circlesRef.current.forEach((circle) => {
        // Update position
        circle.x += circle.speedX;
        circle.y += circle.speedY;

        // Bounce off edges
        if (circle.x < -circle.size || circle.x > canvas.width + circle.size) {
          circle.speedX *= -1;
        }
        if (circle.y < -circle.size || circle.y > canvas.height + circle.size) {
          circle.speedY *= -1;
        }

        // Wrap around edges
        if (circle.x < -circle.size) circle.x = canvas.width + circle.size;
        if (circle.x > canvas.width + circle.size) circle.x = -circle.size;
        if (circle.y < -circle.size) circle.y = canvas.height + circle.size;
        if (circle.y > canvas.height + circle.size) circle.y = -circle.size;

        // Animate size
        circle.size += (circle.targetSize - circle.size) * 0.02;
        if (Math.random() < 0.005) {
          circle.targetSize = Math.random() * 80 + 40;
        }

        // Animate opacity
        circle.opacity += (circle.targetOpacity - circle.opacity) * 0.02;
        if (Math.random() < 0.005) {
          circle.targetOpacity = Math.random() * 0.3 + 0.2;
        }

        // Draw circle
        ctx.save();
        ctx.globalAlpha = circle.opacity;
        ctx.fillStyle = circle.color;
        ctx.beginPath();
        ctx.arc(circle.x, circle.y, circle.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.restore();
      });

      animationFrameRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [dimensions]);

  return (
    <div className="background-circles-container">
      <canvas
        ref={canvasRef}
        width={dimensions.width}
        height={dimensions.height}
        className="background-circles-canvas"
      />
      <style jsx>{`
        .background-circles-container {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: -1;
          overflow: hidden;
          background: linear-gradient(to bottom, #0a0a0a, #1a1a1a);
        }

        .background-circles-canvas {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }

        /* Responsive adjustments */
        @media (max-width: 640px) {
          .background-circles-container {
            background: linear-gradient(to bottom, #0a0a0a, #1a1a2a);
          }
        }

        @media (min-width: 641px) and (max-width: 1024px) {
          .background-circles-container {
            background: linear-gradient(to bottom, #0a0a0a, #1a1a1a);
          }
        }

        @media (min-width: 1025px) {
          .background-circles-container {
            background: linear-gradient(to bottom, #0a0a0a, #1a1a2a);
          }
        }

        /* Prefers-reduced-motion support */
        @media (prefers-reduced-motion: reduce) {
          .background-circles-canvas {
            animation: none;
          }
        }
      `}</style>
    </div>
  );
}

