/**
 * BackgroundCircles - Usage Examples
 *
 * This file contains various examples of how to use the BackgroundCircles component
 * in different scenarios. Copy and adapt these examples for your use case.
 */

import { BackgroundCircles } from './BackgroundCircles';

// ============================================================================
// Example 1: Basic Usage
// ============================================================================
export function BasicExample() {
  return (
    <div className="relative min-h-screen">
      {/* Background circles component */}
      <BackgroundCircles />

      {/* Your content - must have relative positioning and higher z-index */}
      <div className="relative z-10 flex items-center justify-center min-h-screen">
        <h1 className="text-4xl font-bold text-white">
          Hello World
        </h1>
      </div>
    </div>
  );
}

// ============================================================================
// Example 2: Landing Page with Hero Section
// ============================================================================
export function LandingPageExample() {
  return (
    <div className="relative">
      <BackgroundCircles />

      <main className="relative z-10">
        {/* Hero Section */}
        <section className="min-h-screen flex items-center justify-center px-4">
          <div className="max-w-4xl mx-auto text-center">
            <h1 className="text-6xl font-bold text-white mb-6">
              Welcome to Our Platform
            </h1>
            <p className="text-xl text-gray-300 mb-8">
              Experience the future of web design with beautiful animations
            </p>
            <button className="bg-white text-black px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors">
              Get Started
            </button>
          </div>
        </section>

        {/* Additional Content */}
        <section className="min-h-screen flex items-center justify-center px-4">
          <div className="max-w-2xl mx-auto">
            <h2 className="text-4xl font-bold text-white mb-4">
              Features
            </h2>
            <ul className="text-lg text-gray-300 space-y-3">
              <li>✨ Beautiful animated backgrounds</li>
              <li>⚡ Lightning fast performance</li>
              <li>📱 Fully responsive design</li>
            </ul>
          </div>
        </section>
      </main>
    </div>
  );
}

// ============================================================================
// Example 3: Dashboard with Fixed Navigation
// ============================================================================
export function DashboardExample() {
  return (
    <div className="relative min-h-screen">
      <BackgroundCircles />

      {/* Fixed Navigation - higher z-index than background */}
      <nav className="fixed top-0 left-0 right-0 z-20 bg-black/50 backdrop-blur-md border-b border-white/10">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <h1 className="text-white font-bold text-xl">Dashboard</h1>
            <button className="text-white hover:text-gray-300">Menu</button>
          </div>
        </div>
      </nav>

      {/* Main Content - below nav, above background */}
      <main className="relative z-10 pt-20 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[1, 2, 3].map((i) => (
              <div
                key={i}
                className="bg-white/10 backdrop-blur-sm rounded-lg p-6 border border-white/20"
              >
                <h3 className="text-white text-xl font-semibold mb-2">
                  Card {i}
                </h3>
                <p className="text-gray-300">
                  Content goes here
                </p>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

// ============================================================================
// Example 4: Multi-Page Layout with Sections
// ============================================================================
export function MultiPageExample() {
  return (
    <div className="relative">
      <BackgroundCircles />

      <div className="relative z-10">
        {/* Section 1 */}
        <section className="min-h-screen flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-5xl font-bold text-white mb-4">Section 1</h2>
            <p className="text-gray-300">Scroll to see more</p>
          </div>
        </section>

        {/* Section 2 */}
        <section className="min-h-screen flex items-center justify-center bg-black/20">
          <div className="text-center">
            <h2 className="text-5xl font-bold text-white mb-4">Section 2</h2>
            <p className="text-gray-300">Another section</p>
          </div>
        </section>

        {/* Section 3 */}
        <section className="min-h-screen flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-5xl font-bold text-white mb-4">Section 3</h2>
            <p className="text-gray-300">Final section</p>
          </div>
        </section>
      </div>
    </div>
  );
}

// ============================================================================
// Example 5: Card Grid Layout
// ============================================================================
export function CardGridExample() {
  return (
    <div className="relative min-h-screen">
      <BackgroundCircles />

      <div className="relative z-10 container mx-auto px-4 py-16">
        <h1 className="text-5xl font-bold text-white text-center mb-12">
          Our Services
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {['Service 1', 'Service 2', 'Service 3', 'Service 4', 'Service 5', 'Service 6'].map((service) => (
            <div
              key={service}
              className="bg-white/10 backdrop-blur-md rounded-xl p-8 border border-white/20 hover:bg-white/20 transition-all"
            >
              <h3 className="text-2xl font-bold text-white mb-4">{service}</h3>
              <p className="text-gray-300 mb-6">
                Description of the service goes here. This is a placeholder text.
              </p>
              <button className="bg-white text-black px-6 py-2 rounded-lg font-semibold hover:bg-gray-100 transition-colors">
                Learn More
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

// ============================================================================
// Example 6: Authentication Page
// ============================================================================
export function AuthPageExample() {
  return (
    <div className="relative min-h-screen">
      <BackgroundCircles />

      <div className="relative z-10 min-h-screen flex items-center justify-center px-4">
        <div className="w-full max-w-md">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 border border-white/20">
            <h2 className="text-3xl font-bold text-white text-center mb-8">
              Sign In
            </h2>

            <form className="space-y-6">
              <div>
                <label className="block text-white mb-2">Email</label>
                <input
                  type="email"
                  className="w-full px-4 py-3 rounded-lg bg-white/20 border border-white/30 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-white/50"
                  placeholder="you@example.com"
                />
              </div>

              <div>
                <label className="block text-white mb-2">Password</label>
                <input
                  type="password"
                  className="w-full px-4 py-3 rounded-lg bg-white/20 border border-white/30 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-white/50"
                  placeholder="••••••••"
                />
              </div>

              <button
                type="submit"
                className="w-full bg-white text-black py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors"
              >
                Sign In
              </button>
            </form>

            <p className="text-center text-gray-300 mt-6">
              Don't have an account?{' '}
              <a href="#" className="text-white hover:underline">
                Sign up
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

// ============================================================================
// Example 7: Portfolio/Gallery Layout
// ============================================================================
export function PortfolioExample() {
  return (
    <div className="relative">
      <BackgroundCircles />

      <div className="relative z-10">
        {/* Hero */}
        <section className="min-h-screen flex items-center justify-center px-4">
          <div className="max-w-3xl text-center">
            <h1 className="text-7xl font-bold text-white mb-6">
              John Doe
            </h1>
            <p className="text-2xl text-gray-300">
              Designer & Developer
            </p>
          </div>
        </section>

        {/* Gallery */}
        <section className="container mx-auto px-4 py-16">
          <h2 className="text-4xl font-bold text-white text-center mb-12">
            Featured Work
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {[1, 2, 3, 4].map((item) => (
              <div
                key={item}
                className="aspect-video bg-white/10 backdrop-blur-sm rounded-xl border border-white/20 hover:bg-white/20 transition-all cursor-pointer overflow-hidden"
