  // Simulate API delay
  await new Promise((resolve) => setTimeout(resolve, 300))

  // In production: return await db.settings.findFirst({ ... })
  return {
    computeEnvironment: "kubernetes",
    gpuCount: 8,
    wallpaperTinting: true,
