   352→  background: var(--foreground);
   353→  color: var(--background);
   354→  padding: 0.125rem 0.375rem;
   355→  border-radius: 0.25rem;
   356→}
   357→
   358→.markdown-content pre {
   359→  font-family: ui-monospace, monospace;
   360→  font-size: 0.8125rem;
   361→  background: var(--foreground);
   362→  color: var(--background);
   363→  padding: 1rem;
   364→  border-radius: 0.25rem;
   365→  margin-top: 1rem;
   366→  overflow-x: auto;
   367→}
   368→
   369→.markdown-content pre code {
   370→  background: transparent;
   371→  padding: 0;
   372→}
   373→
