    const updateCanvasSize = () => {
      const { innerWidth: width, innerHeight: height } = window;
      canvas.width = width;
      canvas.height = height;

      // Calculate number of circles based on screen size
      const area = width * height;
      const circleCount = Math.floor(area / 30000); // One circle per ~30000 pixels

      // Initialize circles if not already done or if count changed
      if (circlesRef.current.length !== circleCount) {
        circlesRef.current = Array.from({ length: circleCount }, () => {
          const baseRadius = getBaseRadius(width, height);
          const radius = baseRadius + Math.random() * baseRadius * 0.5;

          return {
            x: Math.random() * width,
            y: Math.random() * height,
            radius,
            color: COLORS[Math.floor(Math.random() * COLORS.length)],
            velocityX: (Math.random() - 0.5) * 0.3,
            velocityY: (Math.random() - 0.5) * 0.3,
            targetRadius: radius,
            radiusVelocity: 0,
          };
        });
        setIsInitialized(true);
      }
    };

    updateCanvasSize();
    window.addEventListener("resize", updateCanvasSize);

    return () => {
      window.removeEventListener("resize", updateCanvasSize);
    };
  }, []);

  // Animation loop
  useEffect(() => {
    if (!isInitialized) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    let lastTime = performance.now();

    const animate = (currentTime: number) => {
      const deltaTime = currentTime - lastTime;
      lastTime = currentTime;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw each circle
      circlesRef.current.forEach((circle) => {
        // Update position
        circle.x += circle.velocityX * (deltaTime / 16);
        circle.y += circle.velocityY * (deltaTime / 16);

        // Wrap around screen edges
        if (circle.x < -circle.radius) circle.x = canvas.width + circle.radius;
        if (circle.x > canvas.width + circle.radius) circle.x = -circle.radius;
        if (circle.y < -circle.radius) circle.y = canvas.height + circle.radius;
        if (circle.y > canvas.height + circle.radius) circle.y = -circle.radius;

        // Randomly update target radius every ~3 seconds
        if (Math.random() < 0.005) {
          const baseRadius = getBaseRadius(canvas.width, canvas.height);
          circle.targetRadius = baseRadius + Math.random() * baseRadius * 0.5;
        }

        // Smoothly transition to target radius
        const radiusDiff = circle.targetRadius - circle.radius;
        circle.radiusVelocity += radiusDiff * 0.0002;
        circle.radiusVelocity *= 0.95; // Damping
        circle.radius += circle.radiusVelocity * (deltaTime / 16);

        // Draw circle
        ctx.beginPath();
        ctx.arc(circle.x, circle.y, circle.radius, 0, Math.PI * 2);
        ctx.fillStyle = circle.color;
        ctx.fill();
      });

      animationFrameRef.current = requestAnimationFrame(animate);
    };

    animationFrameRef.current = requestAnimationFrame(animate);

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [isInitialized]);

  return (
    <canvas
      ref={canvasRef}
      className="background-circles-canvas"
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        zIndex: -1,
        pointerEvents: "none",
      }}
    />
  );
}

// Helper function to calculate base radius based on screen size
function getBaseRadius(width: number, height: number): number {
  const minDimension = Math.min(width, height);

  // Responsive radius calculation using breakpoints
  if (width < 640) {
    // Mobile: smaller circles
    return minDimension * 0.08;
  } else if (width < 1024) {
    // Tablet: medium circles
    return minDimension * 0.1;
  } else {
    // Desktop: larger circles
    return minDimension * 0.12;
  }
}

