"use client";

import { useMemo } from "react";
import "./background-circles.css";

interface CircleData {
  id: number;
  color: string;
  size: number;
  x: number;
  y: number;
  duration: number;
  delay: number;
  sizeDuration: number;
  sizeDelay: number;
}

// Hardcoded color palette
const COLORS = [
  "rgba(99, 102, 241, 0.15)",   // Indigo
  "rgba(168, 85, 247, 0.15)",   // Purple
  "rgba(236, 72, 153, 0.15)",   // Pink
  "rgba(59, 130, 246, 0.15)",   // Blue
  "rgba(14, 165, 233, 0.15)",   // Sky
  "rgba(34, 197, 94, 0.15)",    // Green
  "rgba(234, 179, 8, 0.15)",    // Yellow
  "rgba(249, 115, 22, 0.15)",   // Orange
];

/**
 * BackgroundCircles component using CSS animations instead of Canvas.
 * This is an alternative implementation that uses CSS transforms and keyframes.
 *
 * Pros:
 * - GPU accelerated
 * - Easier to debug with browser dev tools
 * - Better for accessibility (can be disabled via prefers-reduced-motion)
 *
 * Cons:
 * - More DOM elements
 * - Slightly higher memory usage
 */
export function BackgroundCirclesCSS() {
  // Generate circles data once on mount
  const circles = useMemo(() => {
    const circleCount = typeof window !== "undefined"
      ? Math.floor((window.innerWidth * window.innerHeight) / 30000)
      : 20;

    return Array.from({ length: circleCount }, (_, i): CircleData => ({
      id: i,
      color: COLORS[Math.floor(Math.random() * COLORS.length)],
      size: 60 + Math.random() * 120, // 60-180px
      x: Math.random() * 100, // 0-100%
      y: Math.random() * 100, // 0-100%
      duration: 20 + Math.random() * 30, // 20-50s for movement
      delay: Math.random() * -20, // Stagger start times
      sizeDuration: 3 + Math.random() * 4, // 3-7s for size changes
      sizeDelay: Math.random() * -3,
    }));
  }, []);

  return (
    <div className="background-circles-container">
      {circles.map((circle) => (
        <div
          key={circle.id}
          className="background-circle"
          style={{
            backgroundColor: circle.color,
            width: `${circle.size}px`,
            height: `${circle.size}px`,
            left: `${circle.x}%`,
            top: `${circle.y}%`,
            animationDuration: `${circle.duration}s, ${circle.sizeDuration}s`,
            animationDelay: `${circle.delay}s, ${circle.sizeDelay}s`,
          }}
        />
      ))}
    </div>
  );
}

