input.content:
/* BackgroundCircles CSS-based implementation styles */

.background-circles-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  pointer-events: none;
}

.background-circle {
  position: absolute;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  animation-name: float, pulse;
  animation-timing-function: linear, ease-in-out;
  animation-iteration-count: infinite;
  will-change: transform, width, height;
}

/* Movement animation - creates random floating effect */
@keyframes float {
  0% {
    transform: translate(-50%, -50%) translate(0, 0);
  }
  25% {
    transform: translate(-50%, -50%) translate(30vw, 20vh);
  }
  50% {
    transform: translate(-50%, -50%) translate(-20vw, 40vh);
  }
  75% {
    transform: translate(-50%, -50%) translate(40vw, -10vh);
  }
  100% {
    transform: translate(-50%, -50%) translate(0, 0);
  }
}

/* Size change animation - creates pulsing effect */
@keyframes pulse {
  0%, 100% {
    transform: translate(-50%, -50%) scale(1);
  }
  50% {
    transform: translate(-50%, -50%) scale(1.3);
  }
}

/* Responsive adjustments */
@media (max-width: 640px) {
  .background-circle {
    /* Smaller circles on mobile */
    width: 40px !important;
    height: 40px !important;
  }

  @keyframes float {
    0% {
      transform: translate(-50%, -50%) translate(0, 0);
    }
    25% {
      transform: translate(-50%, -50%) translate(20vw, 15vh);
    }
    50% {
      transform: translate(-50%, -50%) translate(-15vw, 30vh);
    }
    75% {
      transform: translate(-50%, -50%) translate(25vw, -8vh);
    }
    100% {
      transform: translate(-50%, -50%) translate(0, 0);
    }
  }
}

@media (min-width: 641px) and (max-width: 1023px) {
  .background-circle {
    /* Medium circles on tablet */
    width: calc(var(--circle-size, 80px) * 0.8);
    height: calc(var(--circle-size, 80px) * 0.8);
  }
}

@media (min-width: 1024px) {
  .background-circle {
    /* Larger circles on desktop - use original size */
  }
}

/* Respect user's motion preferences */
@media (prefers-reduced-motion: reduce) {
  .background-circle {
    animation: none;
  }
}

