   106→
   107→function buildProblemTree(flatProblems: Problem[]): ProblemWithChildren[] {
   108→  const problemMap = new Map<string, ProblemWithChildren>();
   109→  const roots: ProblemWithChildren[] = [];
   110→
   111→  for (const problem of flatProblems) {
   112→    problemMap.set(problem.id, { ...problem, children: [] });
   113→  }
   114→
   115→  for (const problem of flatProblems) {
   116→    const node = problemMap.get(problem.id);
   117→    if (!node) continue;
   118→
   119→    if (problem.parentProblemId === null) {
   120→      roots.push(node);
   121→    } else {
   122→      const parent = problemMap.get(problem.parentProblemId);
   123→      if (parent) {
   124→        parent.children.push(node);
   125→      } else {
   126→        roots.push(node);
   127→      }
   128→    }
   129→  }
   130→
   131→  return roots;
   132→}
   133→
   134→function computeDepthFromLeaves(
   135→  roots: ProblemWithChildren[]
   136→): Map<string, number> {
   137→  const depthMap = new Map<string, number>();
   138→
   139→  function computeDepth(node: ProblemWithChildren): number {
   140→    if (node.children.length === 0) {
   141→      depthMap.set(node.id, 0);
   142→      return 0;
   143→    }
   144→    const maxChildDepth = Math.max(...node.children.map(computeDepth));
   145→    const depth = maxChildDepth + 1;
   146→    depthMap.set(node.id, depth);
   147→    return depth;
   148→  }
   149→
   150→  for (const root of roots) {
   151→    computeDepth(root);
   152→  }
   153→
   154→  return depthMap;
   155→}
   156→
   157→function convertProblemsToGraph(
   158→  roots: ProblemWithChildren[],
   159→  _flatProblems: Problem[]
   160→): { nodes: Node[]; edges: Edge[] } {
   161→  const nodes: Node[] = [];
   162→  const edges: Edge[] = [];
   163→  const depthMap = computeDepthFromLeaves(roots);
   164→
   165→  const rootBranchMap = new Map<string, number>();
   166→
   167→  function assignBranchColors(
   168→    node: ProblemWithChildren,
   169→    branchIndex: number
   170→  ): void {
   171→    rootBranchMap.set(node.id, branchIndex);
   172→    for (const child of node.children) {
   173→      assignBranchColors(child, branchIndex);
   174→    }
   175→  }
   176→
   177→  roots.forEach((root, index) => {
   178→    assignBranchColors(root, index % branchColors.length);
   179→  });
   180→
   181→  function processNode(node: ProblemWithChildren): void {
   182→    const status = deriveProblemStatus(node);
   183→    const statusStyle = statusStyles[status];
   184→    const branchIndex = rootBranchMap.get(node.id) ?? 0;
   185→    const borderColor = branchColors[branchIndex];
   186→    const isRoot = node.parentProblemId === null;
   187→    const depth = depthMap.get(node.id) ?? 0;
   188→
   189→    nodes.push({
   190→      id: node.id,
   191→      data: {
   192→        label: node.title,
   193→        status,
   194→        description: node.description,
   195→        isRoot,
   196→        depth,
   197→        problem: node,
   198→      },
   199→      position: { x: 0, y: 0 },
   200→      style: {
   201→        background: statusStyle.background,
   202→        color: statusStyle.color,
   203→        border: `2px ${statusStyle.borderStyle} ${isRoot ? "#1f2937" : borderColor}`,
   204→        borderWidth: isRoot ? "3px" : "2px",
   205→        opacity: statusStyle.opacity,
   206→        borderRadius: "8px",
   207→        padding: "8px 12px",
   208→        fontSize: "12px",
   209→        fontWeight: isRoot ? "600" : "500",
   210→        minWidth: `${nodeWidth}px`,
   211→        textAlign: "center" as const,
   212→      },
   213→    });
   214→
   215→    for (const child of node.children) {
   216→      edges.push({
   217→        id: `e${node.id}-${child.id}`,
   218→        source: node.id,
   219→        target: child.id,
   220→        style: { stroke: borderColor, strokeWidth: 2 },
   221→        animated: deriveProblemStatus(child) === "in-progress",
   222→      });
   223→      processNode(child);
   224→    }
   225→  }
   226→
   227→  for (const root of roots) {
   228→    processNode(root);
   229→  }
   230→
   231→  return { nodes, edges };
   232→}
   233→
   234→function getLayoutedElements(
   235→  nodes: Node[],
   236→  edges: Edge[]
   237→): { nodes: Node[]; edges: Edge[] } {
   238→  const dagreGraph = new dagre.graphlib.Graph();
   239→  dagreGraph.setDefaultEdgeLabel(() => ({}));
   240→
   241→  dagreGraph.setGraph({
   242→    rankdir: "RL",
   243→    nodesep: 30,
   244→    ranksep: 80,
   245→    marginx: 50,
   246→    marginy: 50,
   247→  });
   248→
   249→  for (const node of nodes) {
   250→    dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
   251→  }
   252→
   253→  for (const edge of edges) {
   254→    dagreGraph.setEdge(edge.source, edge.target);
   255→  }
   256→
   257→  dagre.layout(dagreGraph);
   258→
   259→  const layoutedNodes = nodes.map((node) => {
   260→    const nodeWithPosition = dagreGraph.node(node.id);
   261→    return {
   262→      ...node,
   263→      sourcePosition: "left" as const,
   264→      targetPosition: "right" as const,
   265→      position: {
   266→        x: nodeWithPosition.x - nodeWidth / 2,
   267→        y: nodeWithPosition.y - nodeHeight / 2,
   268→      },
   269→    };
   270→  });
   271→
   272→  return { nodes: layoutedNodes, edges };
   273→}
   274→
   275→interface ProblemNodeData {
   276→  label: string;
   277→  status: ProblemStatus;
   278→  description: string;
   279→  isRoot: boolean;
   280→  depth: number;
   281→  problem: Problem;
   282→}
   283→
   284→type SidebarPanel = "menu" | "settings" | null;
   285→
   286→function FloatingBarFlow() {
   287→  const [nodes, setNodes] = useNodesState<Node>([]);
   288→  const [edges, setEdges] = useEdgesState<Edge>([]);
   289→  const [project, setProject] = useState<StartedProject | null>(null);
   290→  const [problems, setProblems] = useState<Problem[]>([]);
   291→  const [isLoading, setIsLoading] = useState(true);
   292→  const [selectedNode, setSelectedNode] = useState<Node | null>(null);
   293→  const [sheetOpen, setSheetOpen] = useState(false);
   294→  const [isUpdating, setIsUpdating] = useState(false);
   295→  const [error, setError] = useState<string | null>(null);
   296→  const [createModalOpen, setCreateModalOpen] = useState(false);
   297→  const [componentType, setComponentType] = useState<"atom" | "molecule">(
   298→    "atom"
   299→  );
   300→  const [componentSlug, setComponentSlug] = useState("");
   301→
   302→  // Floating sidebar state
   303→  const [sidebarPanel, setSidebarPanel] = useState<SidebarPanel>(null);
   304→
   305→  const loadData = useCallback(async () => {
   306→    setIsLoading(true);
   307→    try {
   308→      const proj = await getRandomStartedProject();
   309→      setProject(proj);
   310→      if (proj) {
   311→        const data = await getProblemsByProject(proj.id);
   312→        setProblems(data);
   313→      }
   314→    } catch {
   315→      setError("Failed to load data");
   316→    } finally {
   317→      setIsLoading(false);
   318→    }
   319→  }, []);
   320→
   321→  useEffect(() => {
   322→    loadData();
   323→  }, [loadData]);
   324→
   325→  useEffect(() => {
   326→    if (problems.length === 0) {
   327→      setNodes([]);
   328→      setEdges([]);
   329→      return;
   330→    }
   331→
   332→    const tree = buildProblemTree(problems);
   333→    const { nodes: graphNodes, edges: graphEdges } = convertProblemsToGraph(
   334→      tree,
   335→      problems
   336→    );
   337→    const { nodes: layoutedNodes, edges: layoutedEdges } = getLayoutedElements(
   338→      graphNodes,
   339→      graphEdges
   340→    );
   341→    setNodes(layoutedNodes);
   342→    setEdges(layoutedEdges);
   343→  }, [problems, setNodes, setEdges]);
   344→
   345→  const onNodeClick: NodeMouseHandler = useCallback((_event, node) => {
   346→    setSelectedNode(node);
   347→    setSheetOpen(true);
   348→  }, []);
   349→
   350→  function titleToSlug(title: string): string {
   351→    return title
   352→      .toLowerCase()
   353→      .replace(/\s+/g, "-")
   354→      .replace(/[^a-z0-9-]/g, "");
   355→  }
   356→
   357→  function handleOpenCreateModal() {
   358→    if (!selectedNode) return;
   359→    const problemData = selectedNode.data as ProblemNodeData;
   360→    setComponentSlug(titleToSlug(problemData.label));
   361→    setComponentType("atom");
   362→    setCreateModalOpen(true);
   363→  }
   364→
   365→  async function handleCreateAndStart() {
   366→    if (!(selectedNode && project)) return;
   367→    const problemData = selectedNode.data as ProblemNodeData;
   368→
   369→    setIsUpdating(true);
   370→    try {
   371→      const createResult = await createComponentFolder(
   372→        componentType,
   373→        componentSlug,
   374→        project.id
   375→      );
   376→      if (!createResult.success) {
   377→        setError(createResult.error ?? "Failed to create component folder");
   378→        setIsUpdating(false);
   379→        return;
   380→      }
   381→
   382→      const result = await startProblem(problemData.problem.id);
   383→      if (result.success && result.problem) {
   384→        setProblems((prev) =>
   385→          prev.map((p) => (p.id === result.problem!.id ? result.problem! : p))
   386→        );
   387→        setSelectedNode((prev) =>
   388→          prev
   389→            ? {
   390→                ...prev,
   391→                data: {
   392→                  ...prev.data,
   393→                  status: "in-progress",
   394→                  problem: result.problem,
   395→                },
   396→              }
   397→            : null
   398→        );
   399→        setCreateModalOpen(false);
   400→      } else {
   401→        setError(result.error ?? "Failed to start problem");
   402→      }
   403→    } catch {
   404→      setError("An unexpected error occurred");
   405→    } finally {
   406→      setIsUpdating(false);
   407→    }
   408→  }
   409→
   410→  async function handleCompleteProblem() {
   411→    if (!selectedNode) return;
   412→    const problemData = selectedNode.data as ProblemNodeData;
   413→
   414→    setIsUpdating(true);
   415→    try {
   416→      const result = await completeProblem(problemData.problem.id);
   417→      if (result.success && result.problem) {
   418→        setProblems((prev) =>
   419→          prev.map((p) => (p.id === result.problem!.id ? result.problem! : p))
   420→        );
   421→        setSelectedNode((prev) =>
   422→          prev
   423→            ? {
   424→                ...prev,
   425→                data: {
   426→                  ...prev.data,
   427→                  status: "completed",
   428→                  problem: result.problem,
   429→                },
   430→              }
   431→            : null
   432→        );
   433→      } else {
   434→        setError(result.error ?? "Failed to complete problem");
   435→      }
   436→    } catch {
   437→      setError("An unexpected error occurred");
   438→    } finally {
   439→      setIsUpdating(false);
   440→    }
   441→  }
   442→
   443→  const stats = {
   444→    total: problems.length,
   445→    notStarted: problems.filter((p) => !(p.startedAt || p.completedAt)).length,
   446→    inProgress: problems.filter((p) => p.startedAt && !p.completedAt).length,
   447→    completed: problems.filter((p) => p.completedAt).length,
   448→    roots: problems.filter((p) => p.parentProblemId === null).length,
   449→  };
   450→
   451→  function toggleSidebar(panel: SidebarPanel) {
   452→    if (sidebarPanel === panel) {
   453→      setSidebarPanel(null);
   454→    } else {
   455→      setSidebarPanel(panel);
   456→    }
   457→  }
   458→
   459→  if (isLoading) {
   460→    return (
   461→      <div className="flex h-screen items-center justify-center">
   462→        <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
   463→      </div>
   464→    );
   465→  }
   466→
   467→  if (!project) {
   468→    return (
   469→      <div className="flex h-screen flex-col items-center justify-center gap-4">
   470→        <p className="text-muted-foreground">
   471→          No started projects found. Start a project first to visualize
   472→          problems.
   473→        </p>
   474→      </div>
   475→    );
   476→  }
   477→
   478→  const selectedNodeData = selectedNode?.data as ProblemNodeData | undefined;
   479→
   480→  return (
   481→    <div className="relative flex h-screen flex-col">
   482→      {error && (
   483→        <div className="absolute top-4 right-4 z-50 rounded-lg border border-red-500/50 bg-red-500/10 p-4 text-red-600">
   484→          {error}
   485→          <button
   486→            className="ml-2 text-red-600 hover:text-red-800"
   487→            onClick={() => setError(null)}
   488→            type="button"
   489→          >
   490→            x
   491→          </button>
   492→        </div>
   493→      )}
   494→
   495→      {/* Dock at bottom */}
   496→      <div className="fixed bottom-4 left-1/2 z-40 flex -translate-x-1/2 gap-1 rounded-full border bg-background/95 px-1.5 py-1 shadow-lg backdrop-blur-sm">
   497→        <Button
   498→          className="h-8 w-8 rounded-full"
   499→          onClick={() => toggleSidebar("menu")}
   500→          size="icon"
   501→          variant={sidebarPanel === "menu" ? "default" : "ghost"}
   502→        >
   503→          <Menu className="h-4 w-4" />
   504→        </Button>
   505→        <Button
   506→          className="h-8 w-8 rounded-full"
   507→          onClick={() => toggleSidebar("settings")}
   508→          size="icon"
   509→          variant={sidebarPanel === "settings" ? "default" : "ghost"}
   510→        >
   511→          <Settings className="h-4 w-4" />
   512→        </Button>
   513→      </div>
   514→
   515→      {/* Full-height sidebar panel */}
   516→      <div
   517→        className={`fixed top-3 right-3 bottom-3 z-30 transition-all duration-300 ${
   518→          sidebarPanel
   519→            ? "translate-x-0 opacity-100"
   520→            : "pointer-events-none translate-x-full opacity-0"
   521→        }`}
   522→      >
   523→        <div className="flex h-full w-80 flex-col rounded-xl border bg-background/95 shadow-xl backdrop-blur-sm">
   524→          {/* Sidebar header with close button */}
   525→          <div className="flex items-center justify-between border-b p-4">
   526→            <h3 className="font-semibold">
   527→              {sidebarPanel === "menu" ? "Menu" : "Settings"}
   528→            </h3>
   529→            <Button
   530→              className="h-8 w-8"
   531→              onClick={() => setSidebarPanel(null)}
   532→              size="icon"
   533→              variant="ghost"
   534→            >
   535→              <X className="h-4 w-4" />
   536→            </Button>
   537→          </div>
   538→
   539→          {/* Sidebar content - scrollable */}
   540→          <div className="flex-1 overflow-y-auto p-4">
   541→            {sidebarPanel === "menu" && (
   542→              <div className="space-y-4">
   543→                <div>
   544→                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
   545→                    Project
   546→                  </h4>
   547→                  <p className="font-medium">{project.title}</p>
   548→                  <p className="text-muted-foreground text-sm">
   549→                    {project.codebaseName}
   550→                  </p>
   551→                </div>
   552→                <div className="border-t pt-4">
   553→                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
   554→                    Statistics
   555→                  </h4>
   556→                  <div className="grid grid-cols-2 gap-2 text-sm">
   557→                    <div className="rounded-lg bg-muted p-2">
   558→                      <div className="font-bold text-lg">{stats.total}</div>
   559→                      <div className="text-muted-foreground text-xs">
   560→                        Total Problems
   561→                      </div>
   562→                    </div>
   563→                    <div className="rounded-lg bg-muted p-2">
   564→                      <div className="font-bold text-lg">{stats.roots}</div>
   565→                      <div className="text-muted-foreground text-xs">
   566→                        Root Problems
   567→                      </div>
   568→                    </div>
   569→                    <div className="rounded-lg bg-yellow-100 p-2">
   570→                      <div className="font-bold text-lg text-yellow-800">
   571→                        {stats.inProgress}
   572→                      </div>
   573→                      <div className="text-xs text-yellow-700">In Progress</div>
   574→                    </div>
   575→                    <div className="rounded-lg bg-green-100 p-2">
   576→                      <div className="font-bold text-green-800 text-lg">
   577→                        {stats.completed}
   578→                      </div>
   579→                      <div className="text-green-700 text-xs">Completed</div>
   580→                    </div>
   581→                  </div>
   582→                </div>
   583→                <div className="border-t pt-4">
   584→                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
   585→                    Actions
   586→                  </h4>
   587→                  <Button
   588→                    className="w-full"
   589→                    onClick={() => loadData()}
   590→                    variant="outline"
   591→                  >
   592→                    Refresh Data
   593→                  </Button>
   594→                </div>
   595→              </div>
   596→            )}
   597→
   598→            {sidebarPanel === "settings" && (
   599→              <div className="space-y-4">
   600→                <div>
