   604→                  <div className="space-y-2">
   605→                    <div className="flex items-center justify-between rounded-lg bg-muted p-3">
   606→                      <span className="text-sm">Show Grid</span>
   607→                      <div className="h-4 w-4 rounded bg-primary" />
   608→                    </div>
   609→                    <div className="flex items-center justify-between rounded-lg bg-muted p-3">
   610→                      <span className="text-sm">Animated Edges</span>
   611→                      <div className="h-4 w-4 rounded bg-primary" />
   612→                    </div>
   613→                  </div>
   614→                </div>
   615→                <div className="border-t pt-4">
   616→                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
   617→                    Legend
   618→                  </h4>
   619→                  <div className="space-y-2 text-sm">
   620→                    <div className="flex items-center gap-2">
   621→                      <div
   622→                        className="h-4 w-4 rounded"
   623→                        style={{
   624→                          background: statusStyles["not-started"].background,
   625→                        }}
   626→                      />
   627→                      <span>Not Started</span>
   628→                    </div>
   629→                    <div className="flex items-center gap-2">
   630→                      <div
   631→                        className="h-4 w-4 rounded"
   632→                        style={{
   633→                          background: statusStyles["in-progress"].background,
   634→                        }}
   635→                      />
   636→                      <span>In Progress</span>
   637→                    </div>
   638→                    <div className="flex items-center gap-2">
   639→                      <div
   640→                        className="h-4 w-4 rounded"
   641→                        style={{
   642→                          background: statusStyles.completed.background,
   643→                        }}
   644→                      />
   645→                      <span>Completed</span>
   646→                    </div>
   647→                    <div className="flex items-center gap-2">
   648→                      <div
   649→                        className="h-4 w-4 rounded"
   650→                        style={{ background: statusStyles.blocked.background }}
   651→                      />
   652→                      <span>Blocked</span>
   653→                    </div>
   654→                  </div>
   655→                </div>
   656→              </div>
   657→            )}
   658→          </div>
   659→        </div>
   660→      </div>
   661→
   662→      <ReactFlow
   663→        edges={edges}
   664→        fitView
   665→        fitViewOptions={{ padding: 0.2 }}
   666→        maxZoom={2}
   667→        minZoom={0.1}
   668→        nodes={nodes}
   669→        nodesDraggable={false}
   670→        onNodeClick={onNodeClick}
   671→      >
   672→        <Background />
   673→      </ReactFlow>
   674→
   675→      <Sheet onOpenChange={setSheetOpen} open={sheetOpen}>
   676→        <SheetContent>
   677→          <SheetHeader>
   678→            <SheetTitle>{selectedNodeData?.label}</SheetTitle>
   679→            <SheetDescription>
   680→              {selectedNodeData?.isRoot ? "Root Problem" : "Subproblem"}
   681→            </SheetDescription>
   682→          </SheetHeader>
   683→          <div className="space-y-6 p-6">
   684→            <div>
   685→              <span className="font-medium text-sm">Status</span>
   686→              <div className="mt-1">
   687→                <span
   688→                  className="inline-block rounded px-3 py-1 text-sm"
   689→                  style={{
   690→                    background:
   691→                      statusStyles[selectedNodeData?.status ?? "not-started"]
   692→                        .background,
   693→                    color:
   694→                      statusStyles[selectedNodeData?.status ?? "not-started"]
   695→                        .color,
   696→                  }}
   697→                >
   698→                  {selectedNodeData?.status ?? "not-started"}
   699→                </span>
   700→              </div>
   701→            </div>
   702→
   703→            <div>
   704→              <span className="font-medium text-sm">Description</span>
   705→              <p className="mt-1 text-muted-foreground text-sm">
   706→                {selectedNodeData?.description || "No description"}
   707→              </p>
   708→            </div>
   709→
   710→            <div>
   711→              <span className="font-medium text-sm">Tree Depth</span>
   712→              <p className="mt-1 text-muted-foreground text-sm">
   713→                {selectedNodeData?.depth === 0
   714→                  ? "Leaf node (smallest subproblem)"
   715→                  : `${selectedNodeData?.depth} level${(selectedNodeData?.depth ?? 0) > 1 ? "s" : ""} from leaves`}
   716→              </p>
   717→            </div>
   718→
   719→            <div>
   720→              <span className="font-medium text-sm">Connections</span>
   721→              <p className="mt-1 text-muted-foreground text-sm">
   722→                {
   723→                  edges.filter(
   724→                    (e) =>
   725→                      e.source === selectedNode?.id ||
   726→                      e.target === selectedNode?.id
   727→                  ).length
   728→                }{" "}
   729→                connected edges
   730→              </p>
   731→            </div>
   732→
   733→            <div className="flex gap-2 pt-4">
   734→              {selectedNodeData?.status === "not-started" && (
   735→                <Button
   736→                  className="flex-1"
   737→                  disabled={isUpdating}
   738→                  onClick={handleOpenCreateModal}
   739→                >
   740→                  {isUpdating ? (
   741→                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   742→                  ) : (
   743→                    <Play className="mr-2 h-4 w-4" />
   744→                  )}
   745→                  Start Problem
   746→                </Button>
   747→              )}
   748→              {selectedNodeData?.status === "in-progress" && (
   749→                <Button
   750→                  className="flex-1"
   751→                  disabled={isUpdating}
   752→                  onClick={handleCompleteProblem}
   753→                  variant="default"
   754→                >
   755→                  {isUpdating ? (
   756→                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   757→                  ) : (
   758→                    <CheckCircle2 className="mr-2 h-4 w-4" />
   759→                  )}
   760→                  Complete Problem
   761→                </Button>
   762→              )}
   763→              {selectedNodeData?.status === "completed" && (
   764→                <div className="flex-1 rounded-lg bg-green-50 p-3 text-center text-green-700 text-sm">
   765→                  This problem has been completed
   766→                </div>
   767→              )}
   768→            </div>
   769→          </div>
   770→        </SheetContent>
   771→      </Sheet>
   772→
   773→      <Dialog onOpenChange={setCreateModalOpen} open={createModalOpen}>
   774→        <DialogContent>
   775→          <DialogHeader>
   776→            <DialogTitle>Create Component Folder</DialogTitle>
   777→            <DialogDescription>
   778→              Create a new {componentType} folder for this problem. This will
   779→              also mark the problem as started.
   780→            </DialogDescription>
   781→          </DialogHeader>
   782→          <div className="space-y-4 py-4">
   783→            <div className="space-y-2">
   784→              <Label htmlFor="componentType">Type</Label>
   785→              <div className="flex gap-2">
   786→                <Button
   787→                  onClick={() => setComponentType("atom")}
   788→                  size="sm"
   789→                  variant={componentType === "atom" ? "default" : "outline"}
   790→                >
   791→                  Atom
   792→                </Button>
   793→                <Button
   794→                  onClick={() => setComponentType("molecule")}
   795→                  size="sm"
   796→                  variant={componentType === "molecule" ? "default" : "outline"}
   797→                >
   798→                  Molecule
   799→                </Button>
   800→              </div>
   801→            </div>
   802→            <div className="space-y-2">
   803→              <Label htmlFor="slug">Slug</Label>
   804→              <Input
   805→                id="slug"
   806→                onChange={(e) => setComponentSlug(e.target.value)}
   807→                placeholder="my-component"
   808→                value={componentSlug}
   809→              />
   810→              <p className="text-muted-foreground text-xs">
   811→                Folder will be created at: {componentType}s/(components)/(
   812→                {project?.codebaseName})/{componentSlug || "..."}
   813→              </p>
   814→            </div>
   815→          </div>
   816→          <DialogFooter>
   817→            <Button
   818→              disabled={!componentSlug || isUpdating}
   819→              onClick={handleCreateAndStart}
   820→            >
   821→              {isUpdating ? (
   822→                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   823→              ) : null}
   824→              Create & Start
   825→            </Button>
   826→          </DialogFooter>
   827→        </DialogContent>
   828→      </Dialog>
   829→    </div>
   830→  );
   831→}
   832→
   833→export default function FloatingBarPage() {
   834→  return (
   835→    <ReactFlowProvider>
   836→      <FloatingBarFlow />
   837→    </ReactFlowProvider>
   838→  );
   839→}
