"use client";

import {
  Background,
  type Edge,
  type Node,
  type NodeMouseHandler,
  ReactFlow,
  ReactFlowProvider,
  useEdgesState,
  useNodesState,
} from "@xyflow/react";
import "@xyflow/react/dist/style.css";
import dagre from "dagre";
import { CheckCircle2, Loader2, Menu, Play, Settings, X } from "lucide-react";
import { useCallback, useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet";
import {
  completeProblem,
  createComponentFolder,
  getProblemsByProject,
  getRandomStartedProject,
  startProblem,
} from "../tree-viz/actions";
import {
  deriveProblemStatus,
  type Problem,
  type ProblemStatus,
  type ProblemWithChildren,
  type StartedProject,
} from "../tree-viz/types";

const nodeWidth = 200;
const nodeHeight = 50;

const branchColors = [
  "#ef4444",
  "#f97316",
  "#eab308",
  "#22c55e",
  "#06b6d4",
  "#3b82f6",
  "#8b5cf6",
  "#ec4899",
  "#f43f5e",
  "#14b8a6",
];

const statusStyles: Record<
  ProblemStatus,
  {
    background: string;
    color: string;
    borderStyle: string;
    opacity: number;
  }
> = {
  "not-started": {
    background: "#f3f4f6",
    color: "#374151",
    borderStyle: "solid",
    opacity: 1,
  },
  "in-progress": {
    background: "#fef3c7",
    color: "#92400e",
    borderStyle: "solid",
    opacity: 1,
  },
  completed: {
    background: "#d1fae5",
    color: "#065f46",
    borderStyle: "solid",
    opacity: 1,
  },
  approved: {
    background: "#dbeafe",
    color: "#1e40af",
    borderStyle: "solid",
    opacity: 1,
  },
  blocked: {
    background: "#fee2e2",
    color: "#991b1b",
    borderStyle: "dashed",
    opacity: 0.8,
  },
};

function buildProblemTree(flatProblems: Problem[]): ProblemWithChildren[] {
  const problemMap = new Map<string, ProblemWithChildren>();
  const roots: ProblemWithChildren[] = [];

  for (const problem of flatProblems) {
    problemMap.set(problem.id, { ...problem, children: [] });
  }

  for (const problem of flatProblems) {
    const node = problemMap.get(problem.id);
    if (!node) continue;

    if (problem.parentProblemId === null) {
      roots.push(node);
    } else {
      const parent = problemMap.get(problem.parentProblemId);
      if (parent) {
        parent.children.push(node);
      } else {
        roots.push(node);
      }
    }
  }

  return roots;
}

function computeDepthFromLeaves(
  roots: ProblemWithChildren[]
): Map<string, number> {
  const depthMap = new Map<string, number>();

  function computeDepth(node: ProblemWithChildren): number {
    if (node.children.length === 0) {
      depthMap.set(node.id, 0);
      return 0;
    }
    const maxChildDepth = Math.max(...node.children.map(computeDepth));
    const depth = maxChildDepth + 1;
    depthMap.set(node.id, depth);
    return depth;
  }

  for (const root of roots) {
    computeDepth(root);
  }

  return depthMap;
}

function convertProblemsToGraph(
  roots: ProblemWithChildren[],
  _flatProblems: Problem[]
): { nodes: Node[]; edges: Edge[] } {
  const nodes: Node[] = [];
  const edges: Edge[] = [];
  const depthMap = computeDepthFromLeaves(roots);

  const rootBranchMap = new Map<string, number>();

  function assignBranchColors(
    node: ProblemWithChildren,
    branchIndex: number
  ): void {
    rootBranchMap.set(node.id, branchIndex);
    for (const child of node.children) {
      assignBranchColors(child, branchIndex);
    }
  }

  roots.forEach((root, index) => {
    assignBranchColors(root, index % branchColors.length);
  });

  function processNode(node: ProblemWithChildren): void {
    const status = deriveProblemStatus(node);
    const statusStyle = statusStyles[status];
    const branchIndex = rootBranchMap.get(node.id) ?? 0;
    const borderColor = branchColors[branchIndex];
    const isRoot = node.parentProblemId === null;
    const depth = depthMap.get(node.id) ?? 0;

    nodes.push({
      id: node.id,
      data: {
        label: node.title,
        status,
        description: node.description,
        isRoot,
        depth,
        problem: node,
      },
      position: { x: 0, y: 0 },
      style: {
        background: statusStyle.background,
        color: statusStyle.color,
        border: `2px ${statusStyle.borderStyle} ${isRoot ? "#1f2937" : borderColor}`,
        borderWidth: isRoot ? "3px" : "2px",
        opacity: statusStyle.opacity,
        borderRadius: "8px",
        padding: "8px 12px",
        fontSize: "12px",
        fontWeight: isRoot ? "600" : "500",
        minWidth: `${nodeWidth}px`,
        textAlign: "center" as const,
      },
    });

    for (const child of node.children) {
      edges.push({
        id: `e${node.id}-${child.id}`,
        source: node.id,
        target: child.id,
        style: { stroke: borderColor, strokeWidth: 2 },
        animated: deriveProblemStatus(child) === "in-progress",
      });
      processNode(child);
    }
  }

  for (const root of roots) {
    processNode(root);
  }

  return { nodes, edges };
}

function getLayoutedElements(
  nodes: Node[],
  edges: Edge[]
): { nodes: Node[]; edges: Edge[] } {
  const dagreGraph = new dagre.graphlib.Graph();
  dagreGraph.setDefaultEdgeLabel(() => ({}));

  dagreGraph.setGraph({
    rankdir: "RL",
    nodesep: 30,
    ranksep: 80,
    marginx: 50,
    marginy: 50,
  });

  for (const node of nodes) {
    dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
  }

  for (const edge of edges) {
    dagreGraph.setEdge(edge.source, edge.target);
  }

  dagre.layout(dagreGraph);

  const layoutedNodes = nodes.map((node) => {
    const nodeWithPosition = dagreGraph.node(node.id);
    return {
      ...node,
      sourcePosition: "left" as const,
      targetPosition: "right" as const,
      position: {
        x: nodeWithPosition.x - nodeWidth / 2,
        y: nodeWithPosition.y - nodeHeight / 2,
      },
    };
  });

  return { nodes: layoutedNodes, edges };
}

interface ProblemNodeData {
  label: string;
  status: ProblemStatus;
  description: string;
  isRoot: boolean;
  depth: number;
  problem: Problem;
}

type SidebarPanel = "menu" | "settings" | null;

function FloatingBarFlow() {
  const [nodes, setNodes] = useNodesState<Node>([]);
  const [edges, setEdges] = useEdgesState<Edge>([]);
  const [project, setProject] = useState<StartedProject | null>(null);
  const [problems, setProblems] = useState<Problem[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedNode, setSelectedNode] = useState<Node | null>(null);
  const [sheetOpen, setSheetOpen] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [componentType, setComponentType] = useState<"atom" | "molecule">(
    "atom"
  );
  const [componentSlug, setComponentSlug] = useState("");

  // Floating sidebar state
  const [sidebarPanel, setSidebarPanel] = useState<SidebarPanel>(null);

  const loadData = useCallback(async () => {
    setIsLoading(true);
    try {
      const proj = await getRandomStartedProject();
      setProject(proj);
      if (proj) {
        const data = await getProblemsByProject(proj.id);
        setProblems(data);
      }
    } catch {
      setError("Failed to load data");
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadData();
  }, [loadData]);

  useEffect(() => {
    if (problems.length === 0) {
      setNodes([]);
      setEdges([]);
      return;
    }

    const tree = buildProblemTree(problems);
    const { nodes: graphNodes, edges: graphEdges } = convertProblemsToGraph(
      tree,
      problems
    );
    const { nodes: layoutedNodes, edges: layoutedEdges } = getLayoutedElements(
      graphNodes,
      graphEdges
    );
    setNodes(layoutedNodes);
    setEdges(layoutedEdges);
  }, [problems, setNodes, setEdges]);

  const onNodeClick: NodeMouseHandler = useCallback((_event, node) => {
    setSelectedNode(node);
    setSheetOpen(true);
  }, []);

  function titleToSlug(title: string): string {
    return title
      .toLowerCase()
      .replace(/\s+/g, "-")
      .replace(/[^a-z0-9-]/g, "");
  }

  function handleOpenCreateModal() {
    if (!selectedNode) return;
    const problemData = selectedNode.data as ProblemNodeData;
    setComponentSlug(titleToSlug(problemData.label));
    setComponentType("atom");
    setCreateModalOpen(true);
  }

  async function handleCreateAndStart() {
    if (!(selectedNode && project)) return;
    const problemData = selectedNode.data as ProblemNodeData;

    setIsUpdating(true);
    try {
      const createResult = await createComponentFolder(
        componentType,
        componentSlug,
        project.id
      );
      if (!createResult.success) {
        setError(createResult.error ?? "Failed to create component folder");
        setIsUpdating(false);
        return;
      }

      const result = await startProblem(problemData.problem.id);
      if (result.success && result.problem) {
        setProblems((prev) =>
          prev.map((p) => (p.id === result.problem!.id ? result.problem! : p))
        );
        setSelectedNode((prev) =>
          prev
            ? {
                ...prev,
                data: {
                  ...prev.data,
                  status: "in-progress",
                  problem: result.problem,
                },
              }
            : null
        );
        setCreateModalOpen(false);
      } else {
        setError(result.error ?? "Failed to start problem");
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsUpdating(false);
    }
  }

  async function handleCompleteProblem() {
    if (!selectedNode) return;
    const problemData = selectedNode.data as ProblemNodeData;

    setIsUpdating(true);
    try {
      const result = await completeProblem(problemData.problem.id);
      if (result.success && result.problem) {
        setProblems((prev) =>
          prev.map((p) => (p.id === result.problem!.id ? result.problem! : p))
        );
        setSelectedNode((prev) =>
          prev
            ? {
                ...prev,
                data: {
                  ...prev.data,
                  status: "completed",
                  problem: result.problem,
                },
              }
            : null
        );
      } else {
        setError(result.error ?? "Failed to complete problem");
      }
    } catch {
      setError("An unexpected error occurred");
    } finally {
      setIsUpdating(false);
    }
  }

  const stats = {
    total: problems.length,
    notStarted: problems.filter((p) => !(p.startedAt || p.completedAt)).length,
    inProgress: problems.filter((p) => p.startedAt && !p.completedAt).length,
    completed: problems.filter((p) => p.completedAt).length,
    roots: problems.filter((p) => p.parentProblemId === null).length,
  };

  function toggleSidebar(panel: SidebarPanel) {
    if (sidebarPanel === panel) {
      setSidebarPanel(null);
    } else {
      setSidebarPanel(panel);
    }
  }

  if (isLoading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
      </div>
    );
  }

  if (!project) {
    return (
      <div className="flex h-screen flex-col items-center justify-center gap-4">
        <p className="text-muted-foreground">
          No started projects found. Start a project first to visualize
          problems.
        </p>
      </div>
    );
  }

  const selectedNodeData = selectedNode?.data as ProblemNodeData | undefined;

  return (
    <div className="relative flex h-screen flex-col">
      {error && (
        <div className="absolute top-4 right-4 z-50 rounded-lg border border-red-500/50 bg-red-500/10 p-4 text-red-600">
          {error}
          <button
            className="ml-2 text-red-600 hover:text-red-800"
            onClick={() => setError(null)}
            type="button"
          >
            x
          </button>
        </div>
      )}

      {/* Dock at bottom */}
      <div className="fixed bottom-4 left-1/2 z-40 flex -translate-x-1/2 gap-1 rounded-full border bg-background/95 px-1.5 py-1 shadow-lg backdrop-blur-sm">
        <Button
          className="h-8 w-8 rounded-full"
          onClick={() => toggleSidebar("menu")}
          size="icon"
          variant={sidebarPanel === "menu" ? "default" : "ghost"}
        >
          <Menu className="h-4 w-4" />
        </Button>
        <Button
          className="h-8 w-8 rounded-full"
          onClick={() => toggleSidebar("settings")}
          size="icon"
          variant={sidebarPanel === "settings" ? "default" : "ghost"}
        >
          <Settings className="h-4 w-4" />
        </Button>
      </div>

      {/* Full-height sidebar panel */}
      <div
        className={`fixed top-3 right-3 bottom-3 z-30 transition-all duration-300 ${
          sidebarPanel
            ? "translate-x-0 opacity-100"
            : "pointer-events-none translate-x-full opacity-0"
        }`}
      >
        <div className="flex h-full w-80 flex-col rounded-xl border bg-background/95 shadow-xl backdrop-blur-sm">
          {/* Sidebar header with close button */}
          <div className="flex items-center justify-between border-b p-4">
            <h3 className="font-semibold">
              {sidebarPanel === "menu" ? "Menu" : "Settings"}
            </h3>
            <Button
              className="h-8 w-8"
              onClick={() => setSidebarPanel(null)}
              size="icon"
              variant="ghost"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>

          {/* Sidebar content - scrollable */}
          <div className="flex-1 overflow-y-auto p-4">
            {sidebarPanel === "menu" && (
              <div className="space-y-4">
                <div>
                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
                    Project
                  </h4>
                  <p className="font-medium">{project.title}</p>
                  <p className="text-muted-foreground text-sm">
                    {project.codebaseName}
                  </p>
                </div>
                <div className="border-t pt-4">
                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
                    Statistics
                  </h4>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    <div className="rounded-lg bg-muted p-2">
                      <div className="font-bold text-lg">{stats.total}</div>
                      <div className="text-muted-foreground text-xs">
                        Total Problems
                      </div>
                    </div>
                    <div className="rounded-lg bg-muted p-2">
                      <div className="font-bold text-lg">{stats.roots}</div>
                      <div className="text-muted-foreground text-xs">
                        Root Problems
                      </div>
                    </div>
                    <div className="rounded-lg bg-yellow-100 p-2">
                      <div className="font-bold text-lg text-yellow-800">
                        {stats.inProgress}
                      </div>
                      <div className="text-xs text-yellow-700">In Progress</div>
                    </div>
                    <div className="rounded-lg bg-green-100 p-2">
                      <div className="font-bold text-green-800 text-lg">
                        {stats.completed}
                      </div>
                      <div className="text-green-700 text-xs">Completed</div>
                    </div>
                  </div>
                </div>
                <div className="border-t pt-4">
                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
                    Actions
                  </h4>
                  <Button
                    className="w-full"
                    onClick={() => loadData()}
                    variant="outline"
                  >
                    Refresh Data
                  </Button>
                </div>
              </div>
            )}

            {sidebarPanel === "settings" && (
              <div className="space-y-4">
                <div>
                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
                    Display Options
                  </h4>
                  <div className="space-y-2">
                    <div className="flex items-center justify-between rounded-lg bg-muted p-3">
                      <span className="text-sm">Show Grid</span>
                      <div className="h-4 w-4 rounded bg-primary" />
                    </div>
                    <div className="flex items-center justify-between rounded-lg bg-muted p-3">
                      <span className="text-sm">Animated Edges</span>
                      <div className="h-4 w-4 rounded bg-primary" />
                    </div>
                  </div>
                </div>
                <div className="border-t pt-4">
                  <h4 className="mb-2 font-medium text-muted-foreground text-sm">
                    Legend
                  </h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex items-center gap-2">
                      <div
                        className="h-4 w-4 rounded"
                        style={{
                          background: statusStyles["not-started"].background,
                        }}
                      />
                      <span>Not Started</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <div
                        className="h-4 w-4 rounded"
                        style={{
                          background: statusStyles["in-progress"].background,
                        }}
                      />
                      <span>In Progress</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <div
                        className="h-4 w-4 rounded"
                        style={{
                          background: statusStyles.completed.background,
                        }}
                      />
                      <span>Completed</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <div
                        className="h-4 w-4 rounded"
                        style={{ background: statusStyles.blocked.background }}
                      />
                      <span>Blocked</span>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <ReactFlow
        edges={edges}
        fitView
        fitViewOptions={{ padding: 0.2 }}
        maxZoom={2}
        minZoom={0.1}
        nodes={nodes}
        nodesDraggable={false}
        onNodeClick={onNodeClick}
      >
        <Background />
      </ReactFlow>

      <Sheet onOpenChange={setSheetOpen} open={sheetOpen}>
        <SheetContent>
          <SheetHeader>
            <SheetTitle>{selectedNodeData?.label}</SheetTitle>
            <SheetDescription>
              {selectedNodeData?.isRoot ? "Root Problem" : "Subproblem"}
            </SheetDescription>
          </SheetHeader>
          <div className="space-y-6 p-6">
            <div>
              <span className="font-medium text-sm">Status</span>
              <div className="mt-1">
                <span
                  className="inline-block rounded px-3 py-1 text-sm"
                  style={{
                    background:
                      statusStyles[selectedNodeData?.status ?? "not-started"]
                        .background,
                    color:
                      statusStyles[selectedNodeData?.status ?? "not-started"]
                        .color,
                  }}
                >
                  {selectedNodeData?.status ?? "not-started"}
                </span>
              </div>
            </div>

            <div>
              <span className="font-medium text-sm">Description</span>
              <p className="mt-1 text-muted-foreground text-sm">
                {selectedNodeData?.description || "No description"}
              </p>
            </div>

            <div>
              <span className="font-medium text-sm">Tree Depth</span>
              <p className="mt-1 text-muted-foreground text-sm">
                {selectedNodeData?.depth === 0
                  ? "Leaf node (smallest subproblem)"
                  : `${selectedNodeData?.depth} level${(selectedNodeData?.depth ?? 0) > 1 ? "s" : ""} from leaves`}
              </p>
            </div>

            <div>
              <span className="font-medium text-sm">Connections</span>
              <p className="mt-1 text-muted-foreground text-sm">
                {
                  edges.filter(
                    (e) =>
                      e.source === selectedNode?.id ||
                      e.target === selectedNode?.id
                  ).length
                }{" "}
                connected edges
              </p>
            </div>

            <div className="flex gap-2 pt-4">
              {selectedNodeData?.status === "not-started" && (
                <Button
                  className="flex-1"
                  disabled={isUpdating}
                  onClick={handleOpenCreateModal}
                >
                  {isUpdating ? (
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  ) : (
                    <Play className="mr-2 h-4 w-4" />
                  )}
                  Start Problem
                </Button>
              )}
              {selectedNodeData?.status === "in-progress" && (
                <Button
                  className="flex-1"
                  disabled={isUpdating}
                  onClick={handleCompleteProblem}
                  variant="default"
                >
                  {isUpdating ? (
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  ) : (
                    <CheckCircle2 className="mr-2 h-4 w-4" />
                  )}
                  Complete Problem
                </Button>
              )}
              {selectedNodeData?.status === "completed" && (
                <div className="flex-1 rounded-lg bg-green-50 p-3 text-center text-green-700 text-sm">
                  This problem has been completed
                </div>
              )}
            </div>
          </div>
        </SheetContent>
      </Sheet>

      <Dialog onOpenChange={setCreateModalOpen} open={createModalOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Create Component Folder</DialogTitle>
            <DialogDescription>
              Create a new {componentType} folder for this problem. This will
              also mark the problem as started.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="componentType">Type</Label>
              <div className="flex gap-2">
                <Button
                  onClick={() => setComponentType("atom")}
                  size="sm"
                  variant={componentType === "atom" ? "default" : "outline"}
                >
                  Atom
                </Button>
                <Button
                  onClick={() => setComponentType("molecule")}
                  size="sm"
                  variant={componentType === "molecule" ? "default" : "outline"}
                >
                  Molecule
                </Button>
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="slug">Slug</Label>
              <Input
                id="slug"
                onChange={(e) => setComponentSlug(e.target.value)}
                placeholder="my-component"
                value={componentSlug}
              />
              <p className="text-muted-foreground text-xs">
                Folder will be created at: {componentType}s/(components)/(
                {project?.codebaseName})/{componentSlug || "..."}
              </p>
            </div>
          </div>
          <DialogFooter>
            <Button
              disabled={!componentSlug || isUpdating}
              onClick={handleCreateAndStart}
            >
              {isUpdating ? (
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              ) : null}
              Create & Start
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default function FloatingBarPage() {
  return (
    <ReactFlowProvider>
      <FloatingBarFlow />
    </ReactFlowProvider>
  );
}

