// Enhanced status model for problem nodes
export type ProblemStatus =
  | "not-started"
  | "in-progress"
  | "completed"
  | "approved"
  | "blocked";

export interface Problem {
  id: string;
  projectIdeaId: string;
  parentProblemId: string | null;
  title: string;
  description: string;
  startedAt: Date | null;
  completedAt: Date | null;
  createdAt: Date;
}

export interface ProblemWithChildren extends Problem {
  children: ProblemWithChildren[];
}

export interface StartedProject {
  id: string;
  title: string;
  codebaseName: string;
}

// Derive status from problem state
export function deriveProblemStatus(problem: Problem): ProblemStatus {
  if (problem.completedAt) return "completed";
  if (problem.startedAt) return "in-progress";
  return "not-started";
}

