"use server";

import * as fs from "node:fs/promises";
import { db } from "@product/db";
import { problems, projectIdeas } from "@product/db/schema/index";
import { and, eq, isNotNull, isNull } from "drizzle-orm";
import type { Problem, StartedProject } from "./types";

// Re-export types from shared file
export type {
  Problem,
  ProblemStatus,
  ProblemWithChildren,
  StartedProject,
} from "./types";

export async function getRandomStartedProject(): Promise<StartedProject | null> {
  const results = await db
    .select({
      id: projectIdeas.id,
      title: projectIdeas.title,
      codebaseName: projectIdeas.codebaseName,
    })
    .from(projectIdeas)
    .where(isNotNull(projectIdeas.startedAt))
    .limit(1);
  return results[0] ?? null;
}

export async function getProblemsByProject(
  projectIdeaId: string
): Promise<Problem[]> {
  return db
    .select()
    .from(problems)
    .where(eq(problems.projectIdeaId, projectIdeaId));
}

export async function getRootProblemsByProject(
  projectIdeaId: string
): Promise<Problem[]> {
  return db
    .select()
    .from(problems)
    .where(
      and(
        eq(problems.projectIdeaId, projectIdeaId),
        isNull(problems.parentProblemId)
      )
    );
}

export async function getChildProblems(parentId: string): Promise<Problem[]> {
  return db
    .select()
    .from(problems)
    .where(eq(problems.parentProblemId, parentId));
}

export async function startProblem(
  id: string
): Promise<{ success: boolean; problem?: Problem; error?: string }> {
  try {
    const results = await db
      .update(problems)
      .set({ startedAt: new Date() })
      .where(eq(problems.id, id))
      .returning();
    if (results.length === 0) {
      return { success: false, error: "Problem not found" };
    }
    return { success: true, problem: results[0] };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : "Failed to start problem",
    };
  }
}

export async function completeProblem(
  id: string
): Promise<{ success: boolean; problem?: Problem; error?: string }> {
  try {
    const existing = await db
      .select()
      .from(problems)
      .where(and(eq(problems.id, id), isNotNull(problems.startedAt)))
      .limit(1);

    if (existing.length === 0) {
      return { success: false, error: "Problem not found or not started" };
    }

    const results = await db
      .update(problems)
      .set({ completedAt: new Date() })
      .where(eq(problems.id, id))
      .returning();
    return { success: true, problem: results[0] };
  } catch (error) {
    return {
      success: false,
      error:
        error instanceof Error ? error.message : "Failed to complete problem",
    };
  }
}

// Base path for atoms and molecules in pavilion
const COMPONENTS_BASE_PATH =
  "/Users/lellyo/Desktop/cool-projects/atoms-trees/treasure-trove/product/apps/web/src/app";

export async function createComponentFolder(
  type: "atom" | "molecule",
  slug: string,
  projectId: string
): Promise<{ success: boolean; path?: string; error?: string }> {
  try {
    // Validate slug - only allow lowercase letters, numbers, and dashes
    const validSlug = slug
      .toLowerCase()
      .replace(/\s+/g, "-")
      .replace(/[^a-z0-9-]/g, "");

    if (!validSlug) {
      return { success: false, error: "Invalid slug" };
    }

    // Fetch the project to get its codebaseName
    const project = await db
      .select({ codebaseName: projectIdeas.codebaseName })
      .from(projectIdeas)
      .where(eq(projectIdeas.id, projectId))
      .limit(1);

    if (project.length === 0) {
      return { success: false, error: "Project not found" };
    }

    const projectFolder = project[0].codebaseName;

    // Build the folder path based on type
    const basePath =
      type === "atom"
        ? `${COMPONENTS_BASE_PATH}/atoms/(components)/(${projectFolder})`
        : `${COMPONENTS_BASE_PATH}/molecules/(components)/(${projectFolder})`;

    // Check if the project folder exists
    try {
      await fs.access(basePath);
    } catch {
      return {
        success: false,
        error: `Project folder not found: (${projectFolder}). Make sure the project exists.`,
      };
    }

    const folderPath = `${basePath}/${validSlug}`;

    // Check if folder already exists
    try {
      await fs.access(folderPath);
      return { success: false, error: `Folder already exists: ${validSlug}` };
    } catch {
      // Folder doesn't exist, which is what we want
    }

    // Create the folder
    await fs.mkdir(folderPath, { recursive: true });

    // Create an empty page.tsx file
    const pageContent = `"use client";

export default function ${toPascalCase(validSlug)}Page() {
  return (
    <div className="flex min-h-screen items-center justify-center p-8">
      <div className="text-center">
        <h1 className="text-2xl font-bold">${validSlug}</h1>
        <p className="text-muted-foreground mt-2">Start building your ${type} here</p>
      </div>
    </div>
  );
}
`;

    await fs.writeFile(`${folderPath}/page.tsx`, pageContent, "utf-8");

    return {
      success: true,
      path: folderPath,
    };
  } catch (error) {
    return {
      success: false,
      error:
        error instanceof Error
          ? error.message
          : "Failed to create component folder",
    };
  }
}

// Helper to convert slug to PascalCase for component name
function toPascalCase(str: string): string {
  return str
    .split("-")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join("");
}

