import React, { useEffect, useRef, useState } from 'react';
import './BackgroundCircles.css';

interface Circle {
  id: number;
  x: number;
  y: number;
  size: number;
  baseSize: number;
  velocityX: number;
  velocityY: number;
  color: string;
  opacity: number;
  pulsePhase: number;
  pulseSpeed: number;
}

// Hardcoded color palette
const COLOR_PALETTE = [
  '#FF6B6B', // Coral Red
  '#4ECDC4', // Turquoise
  '#45B7D1', // Sky Blue
  '#FFA07A', // Light Salmon
  '#98D8C8', // Mint
  '#F7DC6F', // Yellow
  '#BB8FCE', // Lavender
  '#85C1E2', // Powder Blue
];

const BackgroundCircles: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [circles, setCircles] = useState<Circle[]>([]);
  const animationFrameId = useRef<number>();
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  // Initialize circles based on screen size
  useEffect(() => {
    const updateDimensions = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      setDimensions({ width, height });

      // Determine number of circles based on screen size
      const area = width * height;
      const baseCount = 20;
      const circleCount = Math.floor(baseCount * (area / (1920 * 1080)));
      const actualCount = Math.max(10, Math.min(circleCount, 40)); // Between 10 and 40 circles

      // Generate initial circles
      const newCircles: Circle[] = [];
      for (let i = 0; i < actualCount; i++) {
        const baseSize = getRandomSize(width);
        newCircles.push({
          id: i,
          x: Math.random() * width,
          y: Math.random() * height,
          size: baseSize,
          baseSize,
          velocityX: (Math.random() - 0.5) * getVelocityMultiplier(width),
          velocityY: (Math.random() - 0.5) * getVelocityMultiplier(width),
          color: COLOR_PALETTE[Math.floor(Math.random() * COLOR_PALETTE.length)],
          opacity: Math.random() * 0.4 + 0.2, // 0.2 to 0.6
          pulsePhase: Math.random() * Math.PI * 2,
          pulseSpeed: Math.random() * 0.02 + 0.01, // 0.01 to 0.03
        });
      }

      setCircles(newCircles);
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);

    return () => {
      window.removeEventListener('resize', updateDimensions);
    };
  }, []);

  // Get circle size based on screen width (responsive)
  const getRandomSize = (width: number): number => {
    if (width < 640) {
      // Mobile: smaller circles
      return Math.random() * 40 + 20; // 20-60px
    } else if (width < 1024) {
      // Tablet: medium circles
      return Math.random() * 60 + 40; // 40-100px
    } else {
      // Desktop: larger circles
      return Math.random() * 100 + 60; // 60-160px
    }
  };

  // Get velocity multiplier based on screen width
  const getVelocityMultiplier = (width: number): number => {
    if (width < 640) {
      return 0.3; // Slower on mobile
    } else if (width < 1024) {
      return 0.5; // Medium speed on tablet
    } else {
      return 0.7; // Faster on desktop
    }
  };

  // Animation loop
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    canvas.width = dimensions.width;
    canvas.height = dimensions.height;

    const animate = () => {
      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw circles
      setCircles(prevCircles => {
        const updatedCircles = prevCircles.map(circle => {
          // Update position
          let newX = circle.x + circle.velocityX;
          let newY = circle.y + circle.velocityY;
          let newVelocityX = circle.velocityX;
          let newVelocityY = circle.velocityY;

          // Bounce off edges
          if (newX - circle.size < 0 || newX + circle.size > canvas.width) {
            newVelocityX = -newVelocityX;
            newX = Math.max(circle.size, Math.min(canvas.width - circle.size, newX));
          }
          if (newY - circle.size < 0 || newY + circle.size > canvas.height) {
            newVelocityY = -newVelocityY;
            newY = Math.max(circle.size, Math.min(canvas.height - circle.size, newY));
          }

          // Update pulse phase for size animation
          const newPulsePhase = circle.pulsePhase + circle.pulseSpeed;
          const pulseFactor = Math.sin(newPulsePhase) * 0.2 + 1; // Oscillate between 0.8 and 1.2
          const newSize = circle.baseSize * pulseFactor;

          return {
            ...circle,
            x: newX,
            y: newY,
            velocityX: newVelocityX,
            velocityY: newVelocityY,
            pulsePhase: newPulsePhase,
            size: newSize,
          };
        });

        // Draw circles
        updatedCircles.forEach(circle => {
          ctx.beginPath();
          ctx.arc(circle.x, circle.y, circle.size, 0, Math.PI * 2);
          ctx.fillStyle = hexToRgba(circle.color, circle.opacity);
          ctx.fill();

          // Optional: Add a subtle glow effect
          const gradient = ctx.createRadialGradient(
            circle.x, circle.y, 0,
            circle.x, circle.y, circle.size
          );
          gradient.addColorStop(0, hexToRgba(circle.color, circle.opacity * 0.8));
          gradient.addColorStop(0.5, hexToRgba(circle.color, circle.opacity * 0.4));
          gradient.addColorStop(1, hexToRgba(circle.color, 0));
          ctx.fillStyle = gradient;
          ctx.fill();
        });

        return updatedCircles;
      });

      animationFrameId.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationFrameId.current) {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
  }, [dimensions, circles.length]); // Re-run when dimensions or circle count changes

  // Helper function to convert hex to rgba
  const hexToRgba = (hex: string, alpha: number): string => {
    const r = parseInt(hex.slice(1, 3), 16);
    const g = parseInt(hex.slice(3, 5), 16);
    const b = parseInt(hex.slice(5, 7), 16);
    return `rgba(${r}, ${g}, ${b}, ${alpha})`;
  };

  return (
    <div className="background-circles-container">
      <canvas
        ref={canvasRef}
        className="background-circles-canvas"
        aria-hidden="true"
        role="presentation"
      />
    </div>
  );
};

export default BackgroundCircles;

