input.content:
/* BackgroundCircles Component Styles */

.background-circles-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1;
  pointer-events: none;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
}

.background-circles-canvas {
  display: block;
  width: 100%;
  height: 100%;
}

/* Responsive adjustments using CSS media queries */

/* Mobile devices (portrait) */
@media (max-width: 639px) and (orientation: portrait) {
  .background-circles-container {
    background: linear-gradient(135deg, #f5f7fa 0%, #e0e7f0 100%);
  }
}

/* Mobile devices (landscape) */
@media (max-width: 639px) and (orientation: landscape) {
  .background-circles-container {
    background: linear-gradient(135deg, #f5f7fa 0%, #d0dae8 100%);
  }
}

/* Tablet devices */
@media (min-width: 640px) and (max-width: 1023px) {
  .background-circles-container {
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  }
}

/* Desktop devices */
@media (min-width: 1024px) {
  .background-circles-container {
    background: linear-gradient(135deg, #e8f0f7 0%, #b8c9e0 100%);
  }
}

/* Large desktop devices */
@media (min-width: 1440px) {
  .background-circles-container {
    background: linear-gradient(135deg, #dce8f5 0%, #a8bbd8 100%);
  }
}

/* Extra large screens */
@media (min-width: 1920px) {
  .background-circles-container {
    background: linear-gradient(135deg, #d0e0f0 0%, #98add0 100%);
  }
}

/* Dark mode support (if needed in the future) */
@media (prefers-color-scheme: dark) {
  .background-circles-container {
    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  }
}

/* High contrast mode for accessibility */
@media (prefers-contrast: high) {
  .background-circles-container {
    background: #ffffff;
  }
}

/* Reduced motion for accessibility */
@media (prefers-reduced-motion: reduce) {
  .background-circles-canvas {
    /* Animations will be handled in the component itself */
    animation: none;
  }
}

/* Print styles - hide background when printing */
@media print {
  .background-circles-container {
    display: none;
  }
}

/* Fallback for browsers that don't support canvas */
.no-canvas .background-circles-container {
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
}

.no-canvas .background-circles-canvas {
  display: none;
}

