/**
 * Example usage of the BackgroundCircles component
 *
 * This file demonstrates how to integrate the BackgroundCircles component
 * into a React application. The component works as a fixed background layer
 * that sits behind all other content.
 */

import React from 'react';
import BackgroundCircles from './BackgroundCircles';

/**
 * Example 1: Basic usage
 * Simply render the component and it will fill the viewport
 */
export function BasicExample() {
  return (
    <div>
      <BackgroundCircles />
      <div style={{ position: 'relative', zIndex: 1, padding: '2rem' }}>
        <h1>Welcome to My App</h1>
        <p>This content appears above the animated background.</p>
      </div>
    </div>
  );
}

/**
 * Example 2: Full page layout
 * Using the component as a background for an entire application
 */
export function FullPageExample() {
  return (
    <div style={{ minHeight: '100vh', position: 'relative' }}>
      <BackgroundCircles />

      {/* Header */}
      <header style={{
        position: 'relative',
        zIndex: 10,
        padding: '1rem 2rem',
        background: 'rgba(255, 255, 255, 0.8)',
        backdropFilter: 'blur(10px)'
      }}>
        <h1>My Application</h1>
        <nav>
          <a href="#home">Home</a>
          <a href="#about">About</a>
          <a href="#contact">Contact</a>
        </nav>
      </header>

      {/* Main content */}
      <main style={{
        position: 'relative',
        zIndex: 1,
        padding: '4rem 2rem',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <section>
          <h2>About Us</h2>
          <p>
            This is an example of how to use the BackgroundCircles component
            in a full-page layout. The animated circles provide a dynamic
            and engaging background without interfering with content.
          </p>
        </section>

        <section style={{ marginTop: '3rem' }}>
          <h2>Features</h2>
          <ul>
            <li>Smooth animations</li>
            <li>Responsive design</li>
            <li>Zero dependencies</li>
            <li>Performance optimized</li>
          </ul>
        </section>
      </main>

      {/* Footer */}
      <footer style={{
        position: 'relative',
        zIndex: 10,
        padding: '2rem',
        textAlign: 'center',
        background: 'rgba(255, 255, 255, 0.8)',
        backdropFilter: 'blur(10px)'
      }}>
        <p>&copy; 2024 My Application. All rights reserved.</p>
      </footer>
    </div>
  );
}

/**
 * Example 3: Hero section
 * Using the component for a landing page hero section
 */
export function HeroExample() {
  return (
    <div style={{
      position: 'relative',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <BackgroundCircles />

      <div style={{
        position: 'relative',
        zIndex: 1,
        textAlign: 'center',
        padding: '2rem',
        background: 'rgba(255, 255, 255, 0.9)',
        borderRadius: '1rem',
        boxShadow: '0 10px 40px rgba(0, 0, 0, 0.1)',
        maxWidth: '600px'
      }}>
        <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>
          Welcome to Our Platform
        </h1>
        <p style={{ fontSize: '1.25rem', marginBottom: '2rem', color: '#666' }}>
          Experience the future of web applications with our innovative solution.
        </p>
        <button style={{
          padding: '1rem 2rem',
          fontSize: '1rem',
          background: '#4ECDC4',
          color: 'white',
          border: 'none',
          borderRadius: '0.5rem',
          cursor: 'pointer'
        }}>
          Get Started
        </button>
      </div>
    </div>
  );
}

/**
 * Example 4: Card grid layout
 * Using the component behind a grid of cards
 */
export function CardGridExample() {
  const cards = [
    { title: 'Feature One', description: 'Amazing feature description' },
    { title: 'Feature Two', description: 'Another great feature' },
    { title: 'Feature Three', description: 'Even more awesomeness' },
    { title: 'Feature Four', description: 'The best feature yet' },
  ];

  return (
    <div style={{ minHeight: '100vh', padding: '4rem 2rem' }}>
      <BackgroundCircles />

      <div style={{ position: 'relative', zIndex: 1, maxWidth: '1200px', margin: '0 auto' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '3rem', fontSize: '2.5rem' }}>
          Our Features
        </h1>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
          gap: '2rem'
        }}>
          {cards.map((card, index) => (
            <div
              key={index}
              style={{
                padding: '2rem',
                background: 'rgba(255, 255, 255, 0.9)',
                borderRadius: '1rem',
                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-5px)';
                e.currentTarget.style.boxShadow = '0 8px 30px rgba(0, 0, 0, 0.15)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 4px 20px rgba(0, 0, 0, 0.1)';
              }}
            >
              <h3 style={{ marginBottom: '1rem', fontSize: '1.5rem' }}>
                {card.title}
              </h3>
              <p style={{ color: '#666' }}>
                {card.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * Example 5: Modal/Overlay
 * Using the component behind a centered modal
 */
export function ModalExample() {
  const [isOpen, setIsOpen] = React.useState(true);

  return (
    <div style={{ minHeight: '100vh', position: 'relative' }}>
      <BackgroundCircles />

      <div style={{ position: 'relative', zIndex: 1, padding: '2rem' }}>
        <button
          onClick={() => setIsOpen(true)}
          style={{
            padding: '1rem 2rem',
            background: '#4ECDC4',
            color: 'white',
            border: 'none',
            borderRadius: '0.5rem',
            cursor: 'pointer'
          }}
        >
          Open Modal
        </button>
      </div>

      {isOpen && (
        <>
          {/* Overlay */}
          <div
            onClick={() => setIsOpen(false)}
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(0, 0, 0, 0.5)',
              zIndex: 100,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            {/* Modal */}
            <div
              onClick={(e) => e.stopPropagation()}
              style={{
                background: 'white',
                padding: '2rem',
                borderRadius: '1rem',
                maxWidth: '500px',
                width: '90%',
                position: 'relative'
              }}
            >
              <button
                onClick={() => setIsOpen(false)}
                style={{
                  position: 'absolute',
                  top: '1rem',
                  right: '1rem',
                  background: 'none',
                  border: 'none',
                  fontSize: '1.5rem',
                  cursor: 'pointer'
                }}
              >
                ×
              </button>

              <h2 style={{ marginBottom: '1rem' }}>Modal Title</h2>
              <p>
                This modal appears above the animated background. The background
                continues to animate smoothly behind the overlay.
              </p>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

/**
 * Example 6: Split screen layout
 * Using the component behind a two-column layout
 */
export function SplitScreenExample() {
  return (
    <div style={{ height: '100vh', display: 'flex' }}>
      <BackgroundCircles />

      {/* Left side */}
      <div style={{
        flex: 1,
        position: 'relative',
        zIndex: 1,
        padding: '4rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'rgba(255, 255, 255, 0.95)'
      }}>
        <div>
          <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>
            Sign In
          </h1>
          <form>
            <input
              type="email"
              placeholder="Email"
              style={{
                width: '100%',
                padding: '0.75rem',
                marginBottom: '1rem',
                borderRadius: '0.5rem',
                border: '1px solid #ddd'
              }}
            />
            <input
              type="password"
              placeholder="Password"
              style={{
                width: '100%',
                padding: '0.75rem',
                marginBottom: '1rem',
                borderRadius: '0.5rem',
                border: '1px solid #ddd'
              }}
            />
            <button style={{
              width: '100%',
              padding: '0.75rem',
              background: '#4ECDC4',
              color: 'white',
              border: 'none',
              borderRadius: '0.5rem',
              cursor: 'pointer',
              fontSize: '1rem'
            }}>
              Sign In
            </button>
          </form>
        </div>
      </div>

      {/* Right side */}
      <div style={{
        flex: 1,
        position: 'relative',
        zIndex: 1,
        padding: '4rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'rgba(255, 255, 255, 0.8)'
      }}>
        <div>
          <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>
            Welcome Back!
          </h2>
          <p style={{ fontSize: '1.1rem', color: '#666' }}>
            Sign in to access your account and continue your journey.
          </p>
        </div>
      </div>
    </div>
  );
}

/**
 * Main App component that demonstrates all examples
 */
export default function App() {
  return (
    <div>
      {/* Choose one of the examples below */}

      {/* <BasicExample /> */}
      {/* <FullPageExample /> */}
      <HeroExample />
      {/* <CardGridExample /> */}
      {/* <ModalExample /> */}
      {/* <SplitScreenExample /> */}
    </div>
  );
}

