/**
 * Unit tests for BackgroundCircles component
 *
 * Note: These tests can be run with Jest and React Testing Library
 * Install required packages: npm install --save-dev @testing-library/react @testing-library/jest-dom jest-canvas-mock
 */

import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';
import BackgroundCircles from './BackgroundCircles';

// Mock canvas context
beforeEach(() => {
  HTMLCanvasElement.prototype.getContext = jest.fn(() => ({
    clearRect: jest.fn(),
    beginPath: jest.fn(),
    arc: jest.fn(),
    fill: jest.fn(),
    createRadialGradient: jest.fn(() => ({
      addColorStop: jest.fn(),
    })),
    fillStyle: '',
  })) as any;
});

describe('BackgroundCircles Component', () => {
  describe('Rendering', () => {
    test('renders without crashing', () => {
      render(<BackgroundCircles />);
      const container = screen.getByRole('presentation');
      expect(container).toBeInTheDocument();
    });

    test('renders canvas element', () => {
      render(<BackgroundCircles />);
      const canvas = screen.getByRole('presentation');
      expect(canvas.tagName).toBe('CANVAS');
    });

    test('has correct CSS class', () => {
      const { container } = render(<BackgroundCircles />);
      const containerDiv = container.querySelector('.background-circles-container');
      expect(containerDiv).toBeInTheDocument();
    });

    test('canvas has correct aria attributes', () => {
      render(<BackgroundCircles />);
      const canvas = screen.getByRole('presentation');
      expect(canvas).toHaveAttribute('aria-hidden', 'true');
    });
  });

  describe('Responsiveness', () => {
    test('updates on window resize', async () => {
      const { container } = render(<BackgroundCircles />);

      // Initial render
      expect(container.querySelector('canvas')).toBeInTheDocument();

      // Simulate resize
      global.innerWidth = 768;
      global.innerHeight = 1024;
      window.dispatchEvent(new Event('resize'));

      // Wait for state update
      await waitFor(() => {
        const canvas = container.querySelector('canvas') as HTMLCanvasElement;
        expect(canvas).toBeInTheDocument();
      });
    });

    test('handles small screen sizes', () => {
      global.innerWidth = 320;
      global.innerHeight = 568;

      render(<BackgroundCircles />);
      const canvas = screen.getByRole('presentation') as HTMLCanvasElement;

      expect(canvas).toBeInTheDocument();
    });

    test('handles large screen sizes', () => {
      global.innerWidth = 1920;
      global.innerHeight = 1080;

      render(<BackgroundCircles />);
      const canvas = screen.getByRole('presentation') as HTMLCanvasElement;

      expect(canvas).toBeInTheDocument();
    });
  });

  describe('Canvas Setup', () => {
    test('sets canvas dimensions based on window size', () => {
      global.innerWidth = 1024;
      global.innerHeight = 768;

      render(<BackgroundCircles />);
      const canvas = screen.getByRole('presentation') as HTMLCanvasElement;

      // Canvas dimensions should be set (may take a tick)
      waitFor(() => {
        expect(canvas.width).toBeGreaterThan(0);
        expect(canvas.height).toBeGreaterThan(0);
      });
    });
  });

  describe('Animation', () => {
    test('uses requestAnimationFrame for animation', () => {
      const requestAnimationFrameSpy = jest.spyOn(window, 'requestAnimationFrame');

      render(<BackgroundCircles />);

      waitFor(() => {
        expect(requestAnimationFrameSpy).toHaveBeenCalled();
      });

      requestAnimationFrameSpy.mockRestore();
    });

    test('cancels animation on unmount', () => {
