     1→import { createRoot } from "react-dom/client";
     2→import { useState, useEffect } from "react";
     3→import { motion, AnimatePresence } from "framer-motion";
     4→import "./styles.css";
     5→import { ManifestoPage } from "./pages/ManifestoPage";
     6→import { WaitlistPage } from "./pages/WaitlistPage";
     7→import {
     8→  Drawer,
     9→  DrawerContent,
    10→  DrawerTrigger,
    11→} from "../components/ui/drawer";
    12→
    13→type Route = "manifesto" | "waitlist";
    14→
    15→const CELL_WIDTH = 96; // 6rem = 96px
    16→const CELL_HEIGHT = 32; // 2rem = 32px
    17→const MOBILE_BREAKPOINT = 640; // px
    18→
    19→function App() {
    20→  const [currentRoute, setCurrentRoute] = useState<Route>("manifesto");
    21→  const [darkMode, setDarkMode] = useState(false);
    22→  const [gridSize, setGridSize] = useState({ cols: 0, rows: 0, isMobile: false, isCompact: false });
    23→  const [drawerOpen, setDrawerOpen] = useState(false);
    24→
    25→  useEffect(() => {
    26→    document.documentElement.classList.toggle("dark", darkMode);
    27→  }, [darkMode]);
    28→
    29→  useEffect(() => {
    30→    const updateGridSize = () => {
    31→      const cols = Math.floor(window.innerWidth / CELL_WIDTH);
    32→      const rows = Math.floor(window.innerHeight / CELL_HEIGHT);
    33→      const isMobile = window.innerWidth < MOBILE_BREAKPOINT;
    34→      const isCompact = cols <= 4;
    35→      setGridSize({ cols, rows, isMobile, isCompact });
    36→    };
    37→
    38→    updateGridSize();
    39→    window.addEventListener("resize", updateGridSize);
    40→    return () => window.removeEventListener("resize", updateGridSize);
    41→  }, []);
    42→
    43→  const routes: { name: Route; label: string }[] = [
    44→    { name: "manifesto", label: "Manifesto" },
    45→    { name: "waitlist", label: "Waitlist" },
    46→  ];
    47→
    48→  const renderContent = () => {
    49→    switch (currentRoute) {
    50→      case "manifesto":
    51→        return <ManifestoPage onNavigateToWaitlist={() => setCurrentRoute("waitlist")} />;
    52→      case "waitlist":
    53→        return <WaitlistPage />;
    54→    }
    55→  };
    56→
    57→  const { cols, rows, isMobile, isCompact } = gridSize;
    58→  if (cols === 0 || rows === 0) return null;
    59→
    60→  // Define regions based on layout - content has fixed height fitting viewport
    61→  const contentStartRow = isMobile ? 1 : 2;
    62→  const contentEndRow = rows - 2; // Leave 2 rows at bottom
    63→  const sidebarStartRow = 3;
    64→
    65→  let sidebarRegion: { startRow: number; endRow: number; startCol: number; endCol: number };
    66→  let contentRegion: { startRow: number; endRow: number; startCol: number; endCol: number };
    67→  let darkModeCell: { row: number; col: number };
    68→
    69→  if (isCompact) {
    70→    // Super small screens: full width content, only 1000x + darkmode in bottom row
    71→    sidebarRegion = { startRow: rows - 1, endRow: rows, startCol: 0, endCol: 1 };
    72→    contentRegion = { startRow: contentStartRow, endRow: contentEndRow, startCol: 0, endCol: cols };
    73→    darkModeCell = { row: rows - 1, col: cols - 1 };
    74→  } else if (isMobile) {
    75→    sidebarRegion = { startRow: rows - 1, endRow: rows, startCol: 0, endCol: cols - 1 };
    76→    contentRegion = { startRow: contentStartRow, endRow: contentEndRow, startCol: 1, endCol: cols - 1 };
    77→    darkModeCell = { row: rows - 1, col: cols - 1 };
    78→  } else {
    79→    // Desktop: 1000x at row 3, then gap at row 4, nav at rows 5-6
    80→    sidebarRegion = { startRow: sidebarStartRow, endRow: sidebarStartRow + 4, startCol: 0, endCol: 1 };
    81→    contentRegion = { startRow: contentStartRow, endRow: contentEndRow, startCol: 2, endCol: Math.min(cols - 2, 12) };
    82→    darkModeCell = { row: rows - 1, col: cols - 1 };
    83→  }
    84→
    85→  const isInRegion = (row: number, col: number, region: typeof sidebarRegion) => {
    86→    return row >= region.startRow && row < region.endRow && col >= region.startCol && col < region.endCol;
    87→  };
    88→
    89→  const cells = [];
    90→
    91→  // Render all background grid cells (skip active regions)
    92→  for (let row = 0; row < rows; row++) {
    93→    for (let col = 0; col < cols; col++) {
    94→      const isDarkModeCell = row === darkModeCell.row && col === darkModeCell.col;
    95→      const isInSidebar = isCompact
    96→        ? (row === sidebarRegion.startRow && col === 0)
    97→        : isMobile
    98→          ? (row === sidebarRegion.startRow && col < cols - 1)
    99→          : isInRegion(row, col, sidebarRegion);
   100→
   101→      if (isInSidebar || isInRegion(row, col, contentRegion) || isDarkModeCell) {
   102→        continue;
   103→      }
   104→
   105→      cells.push(
   106→        <div
   107→          key={`${row}-${col}`}
   108→          className="grid-cell"
   109→          style={{
   110→            gridRow: row + 1,
   111→            gridColumn: col + 1,
   112→          }}
   113→        />
   114→      );
   115→    }
   116→  }
   117→
   118→  // Render sidebar nav items with 1000x branding cell
   119→  if (isCompact) {
   120→    // Compact mode: 1000x with hamburger, opens drawer
   121→    cells.push(
   122→      <div
   123→        key="branding"
   124→        className="active-cell bg-foreground"
   125→        style={{
   126→          gridRow: rows,
   127→          gridColumn: 1,
   128→        }}
   129→      >
   130→        <Drawer open={drawerOpen} onOpenChange={setDrawerOpen}>
   131→          <DrawerTrigger asChild>
   132→            <button className="w-full h-full flex items-center justify-center gap-1.5 px-2 text-xs font-medium tracking-wide text-background">
   133→              <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
   134→                <line x1="3" y1="6" x2="21" y2="6" />
   135→                <line x1="3" y1="12" x2="21" y2="12" />
   136→                <line x1="3" y1="18" x2="21" y2="18" />
   137→              </svg>
   138→              1000x
   139→            </button>
   140→          </DrawerTrigger>
   141→          <DrawerContent>
   142→            <div className="p-4">
   143→              <div className="grid grid-cols-2 gap-2">
   144→                {routes.map((route) => {
   145→                  const isActive = currentRoute === route.name;
   146→                  return (
   147→                    <button
   148→                      key={route.name}
   149→                      onClick={() => {
   150→                        setCurrentRoute(route.name);
   151→                        setDrawerOpen(false);
   152→                      }}
   153→                      className={`p-4 text-sm tracking-wide border transition-colors ${isActive
   154→                        ? "bg-foreground text-background border-foreground"
   155→                        : "bg-background text-foreground border-border hover:bg-foreground/5"
   156→                        }`}
   157→                    >
   158→                      {route.label}
   159→                    </button>
   160→                  );
   161→                })}
   162→              </div>
   163→            </div>
   164→          </DrawerContent>
   165→        </Drawer>
   166→      </div>
   167→    );
   168→  } else if (isMobile) {
   169→    // Mobile: horizontal nav at bottom
   170→    // 1000x branding cell
   171→    cells.push(
   172→      <div
   173→        key="branding"
   174→        className="active-cell bg-foreground"
   175→        style={{
   176→          gridRow: rows,
   177→          gridColumn: 1,
   178→        }}
   179→      >
   180→        <div className="w-full h-full flex items-center justify-center px-2 text-xs font-medium tracking-wide text-background">
   181→          1000x
   182→        </div>
   183→      </div>
   184→    );
   185→    // Nav items
   186→    routes.forEach((route, index) => {
   187→      const isActive = currentRoute === route.name;
   188→      cells.push(
   189→        <div
   190→          key={`nav-${route.name}`}
   191→          className={`active-cell ${isActive ? "selected-cell" : ""}`}
   192→          style={{
   193→            gridRow: rows,
   194→            gridColumn: index + 2,
   195→          }}
   196→        >
   197→          <button
   198→            onClick={() => setCurrentRoute(route.name)}
   199→            className="w-full h-full flex items-center justify-center px-2 text-xs tracking-wide hover:bg-foreground/5 transition-colors"
   200→          >
   201→            {route.label}
   202→          </button>
   203→        </div>
   204→      );
   205→    });
   206→  } else {
   207→    // Desktop: vertical nav on left with gap after 1000x
   208→    // 1000x branding cell
   209→    cells.push(
   210→      <div
   211→        key="branding"
   212→        className="active-cell bg-foreground"
   213→        style={{
   214→          gridRow: sidebarRegion.startRow + 1,
   215→          gridColumn: 1,
   216→        }}
   217→      >
   218→        <div className="w-full h-full flex items-center px-4 text-sm font-medium tracking-wide text-background">
   219→          1000x
   220→        </div>
   221→      </div>
   222→    );
   223→    // Gap cell between 1000x and nav
   224→    cells.push(
   225→      <div
   226→        key="sidebar-gap"
   227→        className="grid-cell"
   228→        style={{
   229→          gridRow: sidebarRegion.startRow + 2,
   230→          gridColumn: 1,
   231→        }}
   232→      />
   233→    );
   234→    // Nav items (start 2 rows below 1000x for the gap)
   235→    routes.forEach((route, index) => {
   236→      const row = sidebarRegion.startRow + index + 2; // +2 for gap
   237→      const isActive = currentRoute === route.name;
   238→
   239→      cells.push(
   240→        <div
   241→          key={`nav-${route.name}`}
   242→          className={`active-cell ${isActive ? "selected-cell" : ""}`}
   243→          style={{
   244→            gridRow: row + 1,
   245→            gridColumn: 1,
   246→          }}
   247→        >
   248→          <button
   249→            onClick={() => setCurrentRoute(route.name)}
   250→            className="w-full h-full flex items-center px-4 text-sm tracking-wide hover:bg-foreground/5 transition-colors"
   251→          >
   252→            {route.label}
   253→          </button>
   254→        </div>
   255→      );
   256→    });
   257→  }
   258→
   259→  // Render content area with scrollable overflow
   260→  cells.push(
   261→    <div
   262→      key="content"
   263→      className="active-cell overflow-hidden"
   264→      style={{
   265→        gridRow: `${contentRegion.startRow + 1} / ${contentRegion.endRow + 1}`,
   266→        gridColumn: `${contentRegion.startCol + 1} / ${contentRegion.endCol + 1}`,
   267→      }}
   268→    >
   269→      <div className="w-full h-full p-8 overflow-y-auto">
   270→        {renderContent()}
   271→      </div>
   272→    </div>
   273→  );
   274→
   275→  // Dark mode toggle
   276→  cells.push(
   277→    <div
   278→      key="darkmode"
   279→      className="active-cell"
   280→      style={{
   281→        gridRow: rows,
   282→        gridColumn: cols,
   283→      }}
   284→    >
   285→      <button
   286→        onClick={() => setDarkMode(!darkMode)}
   287→        className="w-full h-full flex items-center justify-center text-xs hover:bg-foreground/5 transition-colors overflow-hidden"
   288→      >
   289→        <AnimatePresence mode="wait">
   290→          <motion.span
   291→            key={darkMode ? "sun" : "moon"}
   292→            initial={{ y: -20, opacity: 0, rotate: -90 }}
   293→            animate={{ y: 0, opacity: 1, rotate: 0 }}
   294→            exit={{ y: 20, opacity: 0, rotate: 90 }}
   295→            transition={{ duration: 0.2 }}
   296→          >
   297→            {darkMode ? "☀" : "●"}
   298→          </motion.span>
   299→        </AnimatePresence>
   300→      </button>
   301→    </div>
   302→  );
