/**
 * Hero Section Styles
 * Plain CSS implementation with no preprocessors or CSS-in-JS
 */

/* Main Hero Section */
.hero-section {
  width: 100%;
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 2rem;
  position: relative;
}

/* Navigation */
.hero-nav {
  max-width: 1200px;
  margin: 0 auto 3rem;
}

.nav-list {
  display: flex;
  gap: 2rem;
  list-style: none;
  padding: 0;
  margin: 0;
  flex-wrap: wrap;
}

.nav-link {
  color: #ffffff;
  text-decoration: none;
  font-size: 1rem;
  font-weight: 500;
  padding: 0.5rem 1rem;
  border-radius: 0.375rem;
  transition: background-color 200ms ease-in-out, opacity 200ms ease-in-out;
  display: inline-block;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.1);
  opacity: 0.9;
}

.nav-link:focus {
  outline: 2px solid #ffffff;
  outline-offset: 2px;
}

/* Main Content Container */
.hero-content {
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
}

/* Header Section */
.hero-header {
  text-align: center;
  margin-bottom: 3rem;
  animation: fadeIn 600ms ease-in-out;
}

.hero-title {
  font-size: 3rem;
  font-weight: 700;
  color: #ffffff;
  margin: 0 0 1rem 0;
  line-height: 1.2;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2);
}

.hero-description {
  font-size: 1.25rem;
  color: rgba(255, 255, 255, 0.95);
  margin: 0;
  max-width: 700px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.6;
}

/* Starter Packs Container */
.starter-packs-container {
  margin-bottom: 3rem;
}

.starter-packs-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
  animation: fadeIn 800ms ease-in-out 200ms both;
}

/* Individual Starter Pack Card */
.starter-pack-card {
  background: #ffffff;
  border-radius: 0.75rem;
  overflow: hidden;
  text-decoration: none;
  color: inherit;
  display: block;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 300ms ease-in-out, box-shadow 300ms ease-in-out;
  animation: fadeInUp 500ms ease-in-out both;
}

.starter-pack-card:nth-child(1) { animation-delay: 100ms; }
.starter-pack-card:nth-child(2) { animation-delay: 200ms; }
.starter-pack-card:nth-child(3) { animation-delay: 300ms; }
.starter-pack-card:nth-child(4) { animation-delay: 400ms; }
.starter-pack-card:nth-child(5) { animation-delay: 500ms; }
.starter-pack-card:nth-child(6) { animation-delay: 600ms; }

.starter-pack-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
}

