@tailwind base;
@tailwind components;
@tailwind utilities;

/* EchoPilot Design System - High Contrast Accessibility Theme */

@layer base {
  :root {
    /* Light mode - rarely used, dark mode is default for accessibility */
    --background: 220 20% 97%;
    --foreground: 220 20% 10%;

    --card: 0 0% 100%;
    --card-foreground: 220 20% 10%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 20% 10%;

    --primary: 200 100% 50%;
    --primary-foreground: 220 20% 5%;

    --secondary: 220 15% 90%;
    --secondary-foreground: 220 20% 10%;

    --muted: 220 15% 85%;
    --muted-foreground: 220 15% 40%;

    --accent: 160 100% 40%;
    --accent-foreground: 220 20% 5%;

    --destructive: 0 80% 60%;
    --destructive-foreground: 0 0% 100%;

    --success: 140 70% 45%;
    --success-foreground: 0 0% 100%;

    --warning: 45 100% 50%;
    --warning-foreground: 220 20% 10%;

    --border: 220 15% 80%;
    --input: 220 15% 85%;
    --ring: 200 100% 50%;

    --radius: 0.75rem;

    --sidebar-background: 220 20% 95%;
    --sidebar-foreground: 220 20% 10%;
    --sidebar-primary: 200 100% 50%;
    --sidebar-primary-foreground: 220 20% 5%;
    --sidebar-accent: 220 15% 90%;
    --sidebar-accent-foreground: 220 20% 10%;
    --sidebar-border: 220 15% 80%;
    --sidebar-ring: 200 100% 50%;
  }

  .dark {
    /* High contrast dark mode - Primary theme for EchoPilot */
    --background: 220 25% 6%;
    --foreground: 60 30% 96%;

    --card: 220 25% 10%;
    --card-foreground: 60 30% 96%;

    --popover: 220 25% 10%;
    --popover-foreground: 60 30% 96%;

    --primary: 180 100% 50%;
    --primary-foreground: 220 25% 6%;

    --secondary: 220 20% 18%;
    --secondary-foreground: 60 30% 96%;

    --muted: 220 20% 15%;
    --muted-foreground: 220 10% 60%;

    --accent: 45 100% 55%;
    --accent-foreground: 220 25% 6%;

    --destructive: 0 75% 55%;
    --destructive-foreground: 60 30% 96%;

    --success: 140 70% 50%;
    --success-foreground: 220 25% 6%;

    --warning: 45 100% 55%;
    --warning-foreground: 220 25% 6%;

    --border: 220 20% 20%;
    --input: 220 20% 18%;
    --ring: 180 100% 50%;

    --sidebar-background: 220 25% 8%;
    --sidebar-foreground: 60 30% 96%;
    --sidebar-primary: 180 100% 50%;
    --sidebar-primary-foreground: 220 25% 6%;
    --sidebar-accent: 220 20% 15%;
    --sidebar-accent-foreground: 60 30% 96%;
    --sidebar-border: 220 20% 18%;
    --sidebar-ring: 180 100% 50%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }

  /* Focus styles for accessibility */
  :focus-visible {
    @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
  }

  /* Large text for accessibility */
  html {
    font-size: 18px;
  }
}

@layer components {
  /* Pulse animation for recording state */
  @keyframes pulse-ring {
    0% {
      transform: scale(1);
      opacity: 0.8;
    }
    50% {
      transform: scale(1.15);
      opacity: 0.4;
    }
    100% {
      transform: scale(1);
      opacity: 0.8;
    }
  }

  .animate-pulse-ring {
    animation: pulse-ring 1.5s ease-in-out infinite;
  }

  /* Voice button styles */
  .voice-button {
    @apply relative flex items-center justify-center rounded-full transition-all duration-300;
    @apply focus-visible:ring-4 focus-visible:ring-primary/50;
  }

  .voice-button-idle {
    @apply bg-primary text-primary-foreground shadow-lg;
    @apply hover:bg-primary/90 hover:shadow-xl;
  }

  .voice-button-listening {
    @apply bg-destructive text-destructive-foreground shadow-2xl;
  }

  .voice-button-processing {
    @apply bg-accent text-accent-foreground;
  }

  .voice-button-speaking {
    @apply bg-success text-success-foreground;
  }

  /* Email card styles */
  .email-card {
    @apply p-4 rounded-lg border-2 border-transparent transition-all duration-200;
    @apply hover:border-primary/50 hover:bg-card/80;
    @apply focus-visible:border-primary focus-visible:bg-card/80;
  }

  .email-card-unread {
    @apply border-l-4 border-l-primary bg-primary/5;
  }

  .email-card-selected {
    @apply border-primary bg-primary/10;
  }

  /* Status indicator */
  .status-indicator {
    @apply inline-flex items-center gap-2 px-4 py-2 rounded-full text-sm font-semibold;
  }

  .status-idle {
    @apply bg-muted text-muted-foreground;
  }

  .status-listening {
    @apply bg-destructive/20 text-destructive;
  }

  .status-processing {
    @apply bg-accent/20 text-accent;
  }

  .status-speaking {
    @apply bg-success/20 text-success;
  }
}

/* Screen reader only utility */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

