            </p>
            <p className="text-sm text-muted-foreground">
              {privacyMode ? '••••••@••••••' : email.from}
            </p>
          </div>
          <p className="text-sm text-muted-foreground">
            {formatDate(email.date)}
          </p>
        </div>
      </div>

      {/* Email body */}
      <div className="flex-1 overflow-y-auto p-6">
        <div className="prose prose-lg dark:prose-invert max-w-none">
