          <Forward className="w-5 h-5 mr-2" aria-hidden="true" />
          Forward
        </Button>
        <Button variant="destructive" size="lg">
          <Trash2 className="w-5 h-5" aria-hidden="true" />
          <span className="sr-only">Delete</span>
